/** vars **/
:root {
    --box-background-color: rgb(240, 243, 245);
    --box-border-color: rgb(208, 215, 219);
    --link-color: #08517e;
    --link-color-hover: rgb(0, 149, 213);
    --page-background-color: #fff;
    
    --font-size-base: 14px;
    --font-size-sm: 13px;
    --font-size-xs: 11px;
}

@media (min-width: 1440px) {
    :root {
    --font-size-base: 15px;
    --font-size-sm: 14px;
    --font-size-xs: 12px;
    }
}

@media (min-width: 1920px) {
    :root {
    --font-size-base: 16px;
    --font-size-sm: 15px;
    --font-size-xs: 13px;
    }
}

/** main **/
html,
.message-body .bbWrapper {
    font-family: 'Open Sans', sans-serif;
}

a,
.bbWrapper a {
    color: var(--link-color);
}

a:hover, a:active,
.bbWrapper a:hover, .bbWrapper a:active,
.pageNav-jump:hover, .pageNav-jump:active {
    color: var(--link-color-hover);
}

.button.button--cta,
a.button.button--cta,
.pageNav-page.pageNav-page--current a,
.button.button--primary,
a.button.button--primary {
    background-color: var(--link-color);
    border-color: var(--link-color);
    color: #fff;
}

.button.button--cta:hover, .button.button--cta:active,
a.button.button--cta:hover, a.button.button--cta:active,
.pageNav-page.pageNav-page--current a:hover, .pageNav-page.pageNav-page--current a:active,
.button.button--primary:hover, .button.button--primary:active,
a.button.button--primary:hover, a.button.button--primary:active {
    background-color: var(--link-color-hover);
    border-color: var(--link-color-hover);
    color: #fff;
}

.button.button--alt:hover, .button.button--alt:active,
a.button.button--alt:hover, a.button.button--alt:active,
button.button.button--alt:hover, button.button.button--alt:active {
    border-color: var(--link-color-hover);
}


/** page **/
#header-banner {
    display: none;
}

@media (min-width: 640px) {
    #header-banner {
        display: block  ;
    }
}

.p-header.is-sticky {
    height: auto;
}

.p-header.is-sticky .p-header-content {
    padding: 0;
}

.p-header.is-sticky .p-header-logo.p-header-logo--image img {
    height: 1.8em;
}

.p-header.is-sticky .p-nav-search {
    display: none;
}

@media (min-width: 640px) {
    .p-header.is-sticky .p-nav-search {
        display: block;
    }
}

.p-header.is-sticky .p-nav-search input {
    height: 2.1em;
}

.p-header.is-sticky .p-nav-search .input-search > i {
    height: 2.1em;
    width: 2.1em;
}

.p-header.is-sticky .new-navigation .new-navigation-icon,
.p-header.is-sticky .new-navigation .forum-navigation-icon {
    background-size: contain;
    height: 1.65em;
    width: 1.65em;
}

.p-header.is-sticky .headerAvatar {
    font-size: 12px;
    height: 2em;
    vertical-align: middle;
    width: 2em;
}

.p-header.is-sticky .headerAvatar ~ .badge {
    top: -3px;
}

.california-banner-ad-container {
    padding: 0;
}

.california-sticky-footer-ad-container {
    height: auto;
}

/*
.sidebar {
    display: none;
}
*/

.california-additional-header h1 {
    font-size: 24px;
}

@media (min-width: 640px) {
    .california-additional-header h1 {
        font-size: 36px;
    }
}



/** board list **/

.subforum-list-container .node-title {
    line-height: 1.5em;
    margin: 0;
}

.subforum-list-container .node-stats-mobile {
    display: none;
}

@media (min-width: 640px) {
    .subforum-list-container .node-stats-mobile {
        display: block;
    }
}



/** thread list **/
.structItem--thread.is-ignored {
    display: table !important;
}

.structItemContainer .structItem-cell {
    padding: 0.5em 0;
}

.structItemContainer .structItem-cell.structItem-cell--main {
    padding-right: 0;
}

@media (min-width: 640px) {
    .structItemContainer .structItem-cell.structItem-cell--main {
        padding-right: 2em;
    }
}

.structItemContainer .structItem-parts > li {
    color: #aaa;
    font-size: var(--font-size-xs);
}

.structItemContainer .structItem-iconContainer .avatar {
    height: 0.8em;
    vertical-align: middle;
    width: 0.8em;
}

.structItemContainer .structItem-title {
    display: flex;
    max-width: none;
    width: 100%;
}

@media (min-width: 640px) {
    .structItemContainer .structItem-title {
        width: auto;
    }
}

.structItemContainer .structItem-title a.hover-td-none {
    margin-right: 0.5em;
}

.structItemContainer .structItem-title a[qid] {
    display: inline-block;
    padding-left: 1.2em;
    position: relative;
}

.structItemContainer .structItem-title a[qid]::before {
    border-color: var(--link-color);
    border-style: solid;
    border-width: 1px;
    border-radius: 100%;
    content: '';
    height: 0.5em;
    left: 0;
    position: absolute;
    top: 0.5em;
    width: 0.5em;
}

.structItemContainer .is-unread .structItem-title a[qid]::before {
    background-color: var(--link-color);
}

.structItemContainer .structItem-minor {
    display: none;
}

@media (min-width: 640px) {
    .structItemContainer .structItem-minor {
        display: flex;
    }
}



/** thread view **/
.p-body-inner {
    max-width: none;
}

.message, .message + .message, .message.message--bordered {
    border-color: var(--box-border-color);
    border-top-width: 4px;
}

.message.message--post {
    margin-top: 0;
    margin-bottom: 2.5em;
}

.comment-options .actionBar-set {
    top: 2.5em;
}

.message .message-cell--main,
.message .message-cell--user {
    padding-top: 2.5em;
}

.message .message-cell--user {
    padding-bottom: 0;
}

.message .message-cell--main > div {
    display: flex;
    flex-direction: column;
    height: 100%;
}

.message-user {
    background-color: rgba(0,0,0,0.04);
    border-radius: 4px;
    height: 100%;
    padding: 10px;
}

.message-user .avatar {
    height: 1.2em;
    width: 1.2em;
}

.message-user .message-userTitle,
.message-user .message-userStats {
    font-size: var(--font-size-xs);
}


.message-attribution {
    background-color: rgba(0,0,0,0.04);
    border-radius: 4px;
    font-size: var(--font-size-sm);
    padding: 0.5em;
}

.message-attribution .message-attribution-main {
    position: relative;
}

.message.is-unread .message-attribution .message-attribution-main::before {
  content: 'NEW';
  background-color: var(--link-color);
  color: var(--box-background-color);
  padding: 0.25em 0.5em;
  border-radius: 0.2rem;
  margin-left: -0.25em;
  margin-right: 1em;
}

.message-attribution time {
    display: inline-block;
    position: relative;
}

.message-attribution time::after {
    content: " (" attr(title) ")";
}

.message-content {
    margin: 0;
}

.message-content .bbWrapper {
    font-size: var(--font-size-base);
    margin: 2em 0;
}

.message-footer {
    align-items: center;
    background-color: rgba(0,0,0,0.04);
    border-radius: 4px;
    display: flex;
    font-size: var(--font-size-xs);
    justify-content: space-between;
    margin-top: auto;
    padding: 0.5em;
}

.message-footer .message-actionBar {
    align-items: center;
    display: flex;
}

.message-footer .message-actionBar .actionBar-set {
    align-items: center;
    display: flex;
}

.message-footer .message-actionBar .actionBar-set a.button--actionSet {
    font-size: var(--font-size-xs);
}

.message.is-ignored {
  display: block !important;
}

.message.is-ignored .message-avatar,
.message.is-ignored .message-userStats,
.message.is-ignored .message-footer {
  display: none;
}



.bbCodeBlock {
    background-color: var(--box-background-color);
    border-left-color: var(--link-color-hover);
    border-left-width: 4px;
    margin: 1em 0;
    padding: 0;
    width: 100%;
}

.bbCodeBlock-title {
    background-color: rgba(0,0,0,0.05);
    padding: 0.5em 1em;
    text-align: left;
    width: 100%;
}

.bbCodeBlock-content,
.bbCodeBlock .contentRow .contentRow-main {
    background-color: transparent;
    padding-top: 0.5em;
    padding-bottom: 0.5em;
}

.bbCodeBlock .contentRow-header,
.bbCodeBlock .contentRow-snippet,
.bbCodeBlock .contentRow-minor {
    padding-left: 1em;
    padding-right: 1em;
}

.bbCodeBlock .contentRow {
    flex-direction: column;
}

.bbCodeBlock .contentRow .contentRow-figure {
    display: none;
}



.california-likes-container {
    align-items: center;
    display: flex;
    height: auto;
