style.css:
/* ==UserStyle==
@name         MeWe++ Fluid Auto Multi Columns
@namespace    https://kevin-guertin.github.io
@version      2.0.0
@description  A Userstyle for Mewe to make your stream use the full width of your window and include as many columns of posts as it can. It also reduces rounded corners, spacing, and increases comment text size.
@author       kevin-guertin (https://kevin-guertin.github.io)
@homepageURL  https://github.com/kevin-guertin/mewe-fluid-userstyle
@supportURL   https://github.com/kevin-guertin/mewe-fluid-userstyle/issues
@updateURL    https://raw.githubusercontent.com/kevin-guertin/mewe-fluid-userstyle/master/mewe-fluid.user.css
@license      MIT
@preprocessor default
==/UserStyle== */

@-moz-document url-prefix("https://mewe.com/myworld"),
url-prefix("https://mewe.com/groups"),
url-prefix("https://mewe.com/pages"),
url-prefix("https://mewe.com/mycloud") {
    .mewe-app-wrapper {
        max-width: none;
        padding-top: 8rem;
    }


    .h-size-600 {
        max-width: none;
        flex: none;
    }

    .c-feed-simple {
        display: grid !important;
        grid-template-columns: repeat(auto-fill, minmax(440px, 1fr));
        grid-gap: 1.5rem;
        max-width: none;
        justify-items: stretch;
        align-items: stretch;
    }

    .h-width_full {
        display: flex;
        justify-content: center;
        align-content: center;
    }

    .c-app-route {
        min-width: 440px;
    }

    .c-mw-postbox-placeholder {
        width: 33%;
        min-width: 550px;
        max-width: none;
        margin: 0 auto 2.5rem auto;
        border-radius: .5rem;
    }


    .c-mw-post {
        align-self: start
    }
}

@-moz-document domain("mewe.com") {

    /* Full-width header*/
    .c-mw-app-main-header .mewe-app-header_top .mewe-app-header_top_inner {
        max-width: none;
        padding: 0 1.5em 0 0;
    }

    .c-mw-app-main-header .mewe-app-header_bottom {
        height: 2.75rem;
    }

    .mewe-app-header .mewe-app-header_logo {
        margin: 0 0 0 1.5rem;
    }

    .c-mw-app-main-header .mewe-app-header_top .mewe-app-header_top_inner {
        height: 4rem;
    }

    .c-mw-app-main-header .mewe-app-header_top .mewe-app-header_top_inner .mewe-app-header_search .search-input_wrapper .h-input_search {
        height: 2.5rem;
    }

    .icn_search {
        width: 10px !important;
        height: 10px !important;
    }
}

@-moz-document domain("mewe.com") {

    /* Feed filters */
    .h-nav-simple,
    .h-nav-simple+.items-center {
        justify-content: center;
    }

    .h-nav-simple+.items-center {
        width: 100%;
        margin-top: .75rem;
    }
}

@-moz-document domain("mewe.com") {

    /* Posts */
    .c-mw-postbox {
        max-width: none;
        border-radius: .5rem;
    }

    .dialog_inner,
    .dialog_header {
        border-radius: .5rem;
    }

    .mw-box {
        padding: 1rem 2rem;
        border-radius: 0.5rem;
    }

    .c-mw-post {
        border-radius: 0.5rem;
    }

    .c-mw-postbar.postbar {
        display: flex;
        flex-direction: column;
    }

    .c-mw-postbar .c-mw-comment-your.comment-your {
        padding: 1rem;
        order: 3;
    }

    .c-mw-comments-table {
        order: 2
    }

    .postbar_header {
        order: 1;
    }

    .c-mw-comment .comment_text {
        font-size: 0.87rem;
        line-height: 1.35rem;
    }
}

@-moz-document domain("mewe.com") {

    /* Chat */
    .c-mw-chat-window {
        border-radius: 0.5rem;
    }

    .c-mw-chat .chat_titlebar {
        border-radius: 0.5rem 0.5rem 0 0;
    }
}

@-moz-document domain("mewe.com") {

    /* Hide header tab names */
    .header_nav_link_name {
        display: none !important;
        visibility: hidden !important;
    }
}

@-moz-document url-prefix("https://mewe.com/myworld"),
url-prefix("https://mewe.com/groups") {

    /* Hide left sidebar */
    .h-sidebar-left {
        display: none;
        visibility: hidden;
    }
}

@-moz-document url-prefix("https://mewe.com/myworld"),
url-prefix("https://mewe.com/groups") {

    /* Hide right sidebar */
    .h-sidebar-right {
        display: none;
        visibility: hidden;
    }
}
