style.css:
@-moz-document url-prefix("http://jwc.wust.edu.cn/"){
/*导航栏*/
#nav {
    position: fixed;
    z-index: 500;
    width: 100%;
    top: 0;
    left: 0;
    height: 41px;
    color: #bababa;
    -webkit-box-shadow: 0 1px 0 rgba(0,0,0,.2);
    -moz-box-shadow: 0 1px 0 rgba(0,0,0,.2);
    box-shadow: 0 1px 0 rgba(0,0,0,.2);
}
.nav{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.head{
    background:url(http://jwc.wust.edu.cn/_upload/tpl/00/a5/165/template165/images/logo.jpg) no-repeat 0;
}
.inner.clearfix, ul.sub-nav {
    width: 100% !important; 
    height: auto !important;
    left: -10px !important;
}
.nav .wp_nav li.nav-item a span.item-name {
    padding: 0 29px;
}
.wp_nav .sub-nav{
    border:0 !important;
}
.wp_nav .sub-nav .nav-item a{
    border-top: 0px;
}
.nav .wp_nav .sub-nav li.nav-item a span.item-name {
    padding: 3px 15px;
    height: 30px;
    line-height: 30px;
    font-size: 14px;
}
.nav .wp_nav .sub-nav li.nav-item a:hover, .navbar .navbox .wp_nav .sub-nav li.nav-item a.parent{
    transition: all 0.3s ease-in-out;
}
/*flash插件*/
embed {
    display: none;
}

/*滚动条*/
::-webkit-scrollbar {/*滚动条整体样式*/
        width: 10px;     /*高宽分别对应横竖滚动条的尺寸*/
        height: 1px;
  }

::-webkit-scrollbar-thumb {/*滚动条里面小方块*/
        border-radius: 10px;
         -webkit-box-shadow: inset 0 0 5px rgba(0,0,0,0.2);
        background: #535353;
  }

::-webkit-scrollbar-track {/*滚动条里面轨道*/
        -webkit-box-shadow: inset 0 0 5px rgba(0,0,0,0.2);
        border-radius: 10px;
        background: #EDEDED;
}
/*背景*/
body{
	background-color: #fff;
	background:url(https://s2.ax1x.com/2020/03/10/8iduOf.jpg);
}

/*nav*/
.wrapper .inner {
    width: 100%;
    margin: 0 auto;
}
.head {
    background-size: cover;
    margin: 52px 0px 0px 0px;
}
/*搜索框*/
.keyword {
    border-radius: 8px;
    color: #b4b4b4;
    background: #fff;
    border: none;
    width: 230px !important;
    height: 20px;
    line-height: 18px;
    margin-left: 5px;
    padding: 6px;
    margin-right: 30px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
.keyword:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.wp_search .search {
    width: 28px;
    height: 23px;
    border: none;
    cursor: pointer;
    position: relative;
    right: 33px;
    top: 3px;
    margin-left: -22px;
}
.wp_search .search {
    background: url(http://jwc.wust.edu.cn/_upload/tpl/00/a5/165/template165/images/ss_bon.png) no-repeat 0 0;
}

/*foot*/
.foot {
    width: 100%;
    float: left;
    height: 50px;
    line-height: 50px;
    background:#244d9a;
        box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.foot p{
    font-size:16px !important;
}

/*通知公告*/

.top_right_1.right_1{
    display:block !important;
    border-radius:9px;
    width: 670px;
    height:453px;
    margin: 0 0 0 -136px;
    padding:20px;
    background:white;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
.top_right_1.right_1:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.post1 .wp_article_list .list_item .Article_Title a {
    font-size: 16px;
}
.post1 .con-more .more-btn A {
    font-size: 14px;
    width: 60px;
}
.post1 .wp_article_list .list_item .Article_PublishDate {
    font-size: 15px;
}

/*图片交替*/
[frag=窗口101] div {
    margin:0 0 0 -68px;
    width: 710px !important;
    overflow: visible !important;
}
img#w101imgShow{
    height:325px;
    width:710px;
    border-radius:9px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
img#w101imgShow:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.dtjt9_div_img .number {
    right: -300px;
    bottom: 10px;
}
.dtjt9_div_text .dtjt9_div_text_title{
    background: #fff0;
}
.dtjt9_div_text{
    bottom:-30px;
}
.dtjt9_div_text a{
    font-size:14px !important;
}
/*教育教学动态*/
.top_right_2.right_2{
    width:450px;
    border-radius:9px;
    margin: 0px -143px 0px 0px;
    padding:20px;
    background:white;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
.top_right_2.right_2:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
/*校历*/
.top_right_2.top_right_3.right_4{
    width:490px;
    margin: 26px -143px 0px 0px;
    background:white;
    border-radius:9px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
.top_right_2.top_right_3.right_4:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
iframe#iframe_136782602334898156{
    width:100% !important;
    border-radius: 9px;
}
html.yui3-js-enabled body{
    background-color: #fff;
    background:url(http://jwc.wust.edu.cn/_upload/tpl/00/a5/165/template165/images/rl.png) center !important;
}
.calendar-bounding-box .content-box{
    border: 0;
    border-radius:9px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
.calendar-bounding-box .content-box:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}

/*左侧导航*/
.mod_left {
    margin: 0 0 0 -145px;
}
.mod_top_left .post1 .wp_nav .nav-item {
    background:none;
    width: 241px !important;
    float: left;
    background-color: #72a7dd;
    border-radius: 10px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition: 0.3s;
    margin-bottom: 7px;
    padding-bottom: 6px;
}
.mod_top_left .post1 .wp_nav .nav-item:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.mod_top_left .post1 .wp_nav .nav-item.i4, .mod_top_left .post1 .wp_nav .nav-item.i5, .mod_top_left .post1 .wp_nav .nav-item.i6, .mod_top_left .post1 .wp_nav .nav-item.i8, .mod_top_left .post1 .wp_nav .nav-item.i9, .mod_top_left .post1 .wp_nav .nav-item.i10, .mod_top_left .post1 .wp_nav .nav-item.i11, .mod_top_left .post1 .wp_nav .nav-item.i12, .mod_top_left .post1 .wp_nav .nav-item.i13, .mod_top_left .post1 .wp_nav .nav-item.i16, .mod_top_left .post1 .wp_nav .nav-item.i17{
    background:none;
    background-color: white;
}
/*二维码*/
img{
    width:240px;
    height:80px;
    border-radius:9px;
    box-shadow: 0 1px 6px 0 rgba(0, 0, 0, 0.2);
    transition:0.3s;
}
img:hover{
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2);
}
.ewmpc{
    z-index:1;
    position:relative;
    top:-1030px;
    margin:0 50%;
}
div#winfloat_div_w14{
    top:0 !important;
    left:480px !important;
}
div#winfloat_div_w14 div{
    font-size:0 !important;
}
}
