/* ==UserStyle==
@name         Viber Dark
@description  Viber Dark Ссылочки внизу ведут на Real Dark Stylus
@namespace    https://github.com/DopeCatSoft/RealDarkStylus
@homepageURL  https://github.com/DopeCatSoft/RealDarkStylus
@updateURL    https://dl.dropboxusercontent.com/s/7b4irn2dxuma6le/ViberDark.user.css?dl=0
@supportURL   https://github.com/DopeCatSoft/RealDarkStylus
@version      1.0.0
@author       dopecatsoft (http://dopecatsoft.c1.biz)
@license      CC-BY-NC-4.0
@preprocessor stylus
==/UserStyle== */
@-moz-document url-prefix("https://www.viber.com/"), url-prefix("https://account.viber.com/") {
	:root {
		--header-bg: #232323;
		--mobile-bg: #1c1c1c;
		--default-bg: #121212;
		--color: #ddd;
	--black: black;
	--black-color: black;
	}
	header .nav-primary .nav li a:hover, header .nav-primary .nav li.current-menu-item a, .page-template-template-download .phone h3, .page-template-template-download .phone h1, ol, p, ul, h1, h2, h3, h4, h5, h6, .page-template-template-communities .undertext p, .page-template-template-features .undertext p, .page-template-template-download .desktop h3, .page-template-template-download .desktop h1, .page-template-template-download .desktop h2, .page-template-template-download .desktop .desktop-btn span, .page-template-template-download .desktop .more-downloads h4, body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, textarea, p, blockquote, th, td, .home #content.content-container .section-title, .footer-container .view-footer-locale-selector ul.languages-list li {
		color: var(--color);
	}
	
	.country-page .header-container, .footer-widget-wrap, .footer-rakuten {
		background: var(--header-bg);
	}
	
	.country-page .products-section-region .view-composite-products-section {
		background-color: var( --default-bg);
	}
	
	.country-page .products-section-region .view-composite-products-section .composite-products-list-container .view-product-item {
		border: 1px solid rgba(136,136,136,0.38);
		-webkit-box-shadow: none;
		box-shadow: none;
		background: var( --mobile-bg);
	}
	
	.country-page .view-all-rates-section, .page-template-template-security .reasons {
		background: var(--header-bg);
	}
	
	.view-world-banner .banner, .page-template-template-download .phone {
		background: var(--mobile-bg);
	}
	
	.social-links-footer .s-link:hover {
		background: hsla(0,0%,100%,.4);
	}
	
	.social-links-footer .s-link {
		color: var(--color);
		background: hsla(0,0%,100%,.3);
	}
	
	body, html {
		color: var(--color);
		background: var(--header-bg);
	}
	
	.home .view-how-section, .page-template-template-communities .community, .page-template-template-features .community, .page-template-template-communities .extensions, .page-template-template-features .extensions, .page-template-template-download .desktop {
		background: var(--default-bg);
	}
	
	img {
		opacity: 0.65;
		filter: brightness(.8) contrast(1.2);
	}
	
	.point img, .security .container .row .col-md-4 img {
		opacity: .95;
		filter: hue-rotate(100deg) invert(1);
	}
	
	.page-template-template-communities .top-features .feature-video .first-video-column .point.active h4, .page-template-template-features .top-features .feature-video .first-video-column .point.active h4 {
		color: #add8e6;
	}
	
	.global-btn-green, .btn-green {
		background: #90EE90;
	}
	
	.global-btn-green:hover, .btn-green:hover {
		color: var(--color);
		background: #62a362;
	}
	
	.page-template-template-security .reasons .select, .с-what-is-vo mark{
		background: #FFF192;
		color: var(--black-color);
	}
	.с-what-is-vo {
		color: var(--color);
		background: var(--mobile-bg)
	}
	.features {
		background: var(--black);
	}
	
	.footer-container .view-footer-locale-selector ul.languages-list {
		background: #282828;
		-webkit-box-shadow: none;
		box-shadow: none;
	}
	.country-page .view-all-rates-section .description {
		color: var(--color);
	}
	}
