style.css:
@-moz-document url-prefix("https://www.codecademy.com/learn") {
    /* This add custom line as separator */
    .CodeMirror-gutters {
        border-right: /*[[separatorColor]]*/ /*[[custSeparatorThick]]*/ solid !important;
    }

    header:not([style^=background]) {
        background-color: #121212 !important;
        color: #fff !important;
    }

    header #logo {
        -webkit-filter: invert(.8) !important;
        filter: invert(.8) !important;
    }

    header a, button {
        color: #fff !important;
    }

    nav h3, a, button {
        color: #fff !important;
    }

    div[class*=pageWrapper] {
        background: #121212 !important;
        color: #FFF !important;
    }

    h1, h2, h3, h4, h5, h6, p, text, span[class^=text], span[class^=description] {
        color: #FFF !important;
    }

    span:after {
        display: none;
    }

    div[class^=shell] {
        background: #000 !important;
    }

    div[class^=card] {
        background: unset;
    }

    svg text {
        color: #fff;
    }

    div[class^=contentContainer], div[class^=footerContainer], div[class^=pro], body, div[class^=headerBottom], footer {
        background: #121212 !important;
        color: #FFF;
    }

    div[class^=footerContainer], div[class^=legalLocalDivider] {
        border-top: #616161 1px solid;
    }

    div[class^=notificationsContainer], nav {
        background: #212121;
    }

    div[class^=shell] div[class^=footerContainer] {
        background: inherit !important;
        border: none;
    }

    span[class^=percent] {
        background: inherit;
    }

    div[class^=madeIn], div[role=heading], div[class^=iconComponentContainer] {
        color: #fff !important;
    }

    svg[class^=progress], div[class^=trackDetailContainer] {
        background: #000;
    }

    div[class^=list] {
        background: #121212;
    }

    p:after {
        background: none !important;
    }

    button[role=tab] {
        background: #121212;
    }

    button[role=tab]:hover, button[role=tab]:active {
        background: #000;
    }

    button[aria-selected=true] {
        background: #000;
    }

    button[class^=basicBtn], button[class^=basicBtn]:hover, header[class^=header] [data-testid=nav-link] {
        background: #121212 !important;
    }

    button[class*=playBtn], button[class*=playBtn]:hover {
        background: unset !important;
    }
    li[class^=tag] {
        color: #fff !important;
    }

    img[src="//images.ctfassets.net/go6kr6r0ykrq/6uE7N3PXU8dTQ6JJv9YmF9/a08f37a731d7be914d5ac19f4fc2d4ed/pro-codecademy-icon-48x48px.svg"] {
        -webkit-filter: invert(.8) !important;
        filter: invert(.8) !important;
    }

    div[class^=circleText] {
        color: #fff;
    }

    div[class^=title]:not([class^=titleArea]) {
        background: #121212;
        color: #fff !important;
    }

    a[class^=notification], div[class^=queryContainer] {
        border: none;
    }

    div[data-testid=header-search-dropdown] {
        background: #121212;
        border-bottom: #616161 10px solid;
    }

    div[data-testid=header-search-dropdown] input[type=search] {
        color: #FFF;
    }

    aside[class^=infoBox] {
        background: #212121;
        border-color: unset !important;
    }

    aside[class^=infoBox] p {
        border-bottom: none !important;
    }

    dl[class^=timeToComplete] {
        border: none !important;
    }

    dl[class^=timeToComplete] dd, dl[class^=prerequisites] dd {
        color: #fff;
    }

    aside[class^=infoBox] dt {
        color: #fff;
    }

    section[class^=overviewSection] {
        background: #121212 !important;
    }

    ol[class^=courseUnits], ol[class^=courseUnits] div {
        background: #212121 !important;
    }

    blockQuote, cite {
        color: unset !important;
    }

    div[class^=conceptButtons], div[class^=conceptContent] {
        background: #212121;
        box-shadow: none;
    }

    div[class^=container] {
        background: unset;
    }

    div[class^=container] div[class^=header] {
        color: white;
        background: #000 !important;
        border: none;
    }

    div[class^=hintHeader], div[class^=hintHeader]:hover, div[class^=hintHeader]:focus {
        background: #FDD835 !important;
        border: none;
    }

    div[class^=loadingScreen] {
        background: #121212 !important;
    }

    div[class^=bodyParentHeading] {
        color: #fff;
    }

    section[class^=exerciseSection] * {
        background: inherit;
    }

    li[class^=item] div[class^=header] {
        background: inherit !important;
    }

    li[class^=item] div[class^=container][style^=border-color], li[class^=item] button {
        background: #212121 !important;
    }

    div[class^=editorFooter] {
        background: #121212;
    }

    button[class^=notification] img {
        filter: invert(1);
    }

    div[class^=queryContainer] {
        background: #121212;
    }

    div[class^=achievementIcon] svg {
        filter: invert(1);
    }

    .edit {
        background: #121212 !important;
    }

    ul[class^=sideMenu], .account-container, .account-page {
        background: #212121;
        color: #fff;
    }

    .account-page input, .account-page li {
        background-color: #212121 !important;
        border: none !important;
        box-shadow: none !important;
        color: #fff !important;
    }

    .field--text {
        border: #fff 2px solid !important;
    }

    .field--image {
        background: inherit !important;
    }

    div[class*=dropdown] {
        background-color: #212121 !important;
    }

    div[class*=dropdown] input {
        background-color: #616161 !important;
        color: #FFF;
        border: none;
    }

    div[class^=achievement] {
        color: #fff!important;
    }

    div[class^=wall] {
        background-color: #616161;
    }

    nav[aria-label="Catalog Subjects"] {
        background: #121212!important;
    }

    section[data-testid="platform-page"] div[class^=contentContainer], section[data-testid="platform-page"] button,  section[data-testid="platform-page"] button:hover{
        background-color: unset!important;
    }

    .bg-danger{
        background: #B71C1C!important;
    }

    .bg-warning {
        background: #FBC02D!important;
    }

    main.forums{
        background: #121212!important;
    }

    .fcn-checkpoint--disabled {
        background: #424242 !important;
    }
}

@-moz-document url-prefix("https://www.codecademy.com/learn/paths") {
    div[class^=card] {
        background: #121212;
    }

    span[class^=logo] {
        color: #121212 !important;
    }

    div[class^=container] {
        background: #121212;
    }
}
