/*sticky box settings*/
.stickytooltip {
    box-shadow: 5px 5px 8px #818181;
    -webkit-box-shadow: 5px 5px 8px #818181;
    -moz-box-shadow: 5px 5px 8px #818181;
    display: none;
    position: absolute;
    display: none;
    background: #333333bf;
    z-index: 3000;
}
#tooltip {
    border: 1px solid #a1a1a1;
    width: 428px;
    padding: 10px;
    z-index: 10000;
    text-indent: 0;
    display: block;
}
#tooltip h3 {
    color: #31a95b;
    font-weight: 700;
    font-size: 18px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    display: inherit;
}
#tooltip .tooltip_summary {
    width: 100%;
    margin-top: 8px;
    padding-top: 8px;
    float: left;
    color: #f2f0f0;
    border-top: 2px solid #31a95b;
}
#tooltip .tooltip_main .tooltip_detail_left p {
    line-height: 22px;
    overflow: hidden;
    width: 100%;
    color: #f2f0f0;
}
#tooltip .tooltip_main .tooltip_detail_left p label {
    color: #31a95b;
    margin-right: 6px;
    font-weight: 400;
}

#tooltip .tooltip_summary span {
    font-weight: 700;
    color: #31a95b;
}
