style.css:
@-moz-document domain(skyscrapercity.com)
{

/* common */
html.template-thread_view div.p-pageWrapper,
.p-sectionLinks,
hr.sticky-divider {
    background: #ececec !important;
}

html.template-thread_view div.p-pageWrapper,
.p-sectionLinks,
hr.sticky-divider {
    background: #ececec !important;
}
hr.sticky-divider + div.california-thread-item {
    border-top: none !important;
}

/* menu */
.menu.menu--left {
    min-width: 30% !important;
    max-width: 66% !important;
}

/* header */
header.is-sticky {
    display: none !important;
}

.p-header-banner-placeholder {
    width: 100%;
    height: 72px;
    overflow: hidden;
    display: block;
}

.p-header-banner-placeholder img {
    display: none;
}

.header-content-wrapper h1 {
    display: block;
    font-size: 2em;
    font-weight: bold;
}

/* sidebar */
html:not(.template-california_index_view) div.sidebar {
    display: none !important;
}

/* nav */
.headerNav {
    padding: 10px 0px;
}

.california-header-nav {
    color: #383a3b !important;
    margin-bottom: 0px;
}

.pageNav-page > a {
    display: block;
    padding: 7px 8px;
    text-decoration: none;
    min-width: 36px;
    height: 32px;
}


/* section links */
.p-sectionLinks {
    max-width: none;
    font-size: 14px;
    padding: 5px 20px;
}

.p-sectionLinks-inner {
    max-width: none;
    padding: 0px 10px;
    margin: 0 auto;
    padding-left: 2px;
    padding-right: 2px;
}

.california-additional-header h1 {
    font-size: 24px;
    font-weight: 400;
    width: 100%;
}


/* page nav */
.pageNav-page.pageNav-page--current {
    color: #fefefe;
    cursor: pointer;
    border: none;
}

/* body */
.p-body-header {
    margin-bottom: 0px;
    margin-top: 0px;
}

.p-body-inner {
    width: 100%;
    max-width: none;
    padding: 0 20px;
    margin: 0 auto;
}

/* message */
.message--post {
    background: #fefefe !important;
    margin: 7px 0px !important;
    border: 2px solid #cbcbcb !important;
}

.message--post.is-mod-selected {
    background: #fffcd7 !important;
}

.message--post > div.message-inner > div {
    padding: 10px 10px ! important;
}

.message--post > div.message-inner > div.message-cell.message-cell--user {
    background: #f5f5f5 !important;
}

.bbCodeBlock,
div.bbCodeBlock--unfurl,
div.block-outer .button,
div.block-outer a.button,
.fr-toolbar {
    background: #f5f5f5 !important;
}

.message--post > div.comment-options .message-newIndicator {
    background: #B00020 !important;
}

/* message unread */
.is-unread .u-concealed {
    font-weight: bold;
    color: #0f5257 !important;
}

/* message user  */
.message-user {
    height: 100%;
    display: flex;
    flex-direction: column;
}

/* avatar */
.avatar.avatar--ml {
    width: 72px;
    height: 72px;
    font-size: 58px;
}

/* message user info  */
/*
.message-avatar-online {
    display: block;
}
*/
.message-userDetails {
    height: 100%;
    display: flex;
    flex-direction: column;
}

.message-name {
    font-weight: 500;
    font-size: 16px;
    margin: 0;
}

.message-userTitle {
    display: none;
}

/* .userbit-info  */
.userbit-info {
    margin-bottom: 5px;
}

.california-message-user-separator {
    display: none;
}

span.flag-icon {
    width: 100%;
    height: 40px;

    margin-left: 0px;
    background-size: 14px;
    background-position: top left;
    background-repeat: no-repeat;
    position: relative;
    display: block;
}

span.flag-icon::before {
    display: block;
    padding-top: 13px;
    content: attr(title);
    font-weight: 400;
    font-size: 11px;
}

.topical-info .text {
    font-size: 11px;
}

.message-userStats {
    font-size: 11px;
    font-style: italic;
    margin-top: auto;
}

/* message body */
div.message-cell {
    padding: 0px !important;
}


div.actionBar-set.actionBar-set--internal {
    line-height: initial !important;
}

div.block-outer,
form.block.js-quickReply,
div.message.js-newMessagesIndicator {
    padding: 0em 1em !important;
}

/* message attribution */
.message-attribution-main {
    display: flex;
    flex-direction: row-reverse;
    width: 100%;
    justify-content: space-between;
    padding-right: 30px;
}

.message-attribution-main time::before {
    content: attr(title) ", ";
    display: inline;
}

.message-attribution-main .dot-separator {
    display: none;
}

.california-options-button-inner {
    top: 0px;
}

.message-attribution-main .dot-separator {
    display: none;
}

.california-options-button-inner {
    top: 0px;
}


/* message quote */
/* message footer */
.actionBar-set--external {
    display: none !important;
}

.message-actionBar {
    display: flex;
    flex-direction: row-reverse;
}

.california-likes-container {
    height: 0px;
}

/* recommended */
.california-recommended-reading-container {
    display: none !important;
}

/* ad */
.california-ad-mobile {
    display: none;
}

.california-thread-ad {
    display: none;
}


/* alerts */
.contentRow-main.contentRow-main--close {
    padding-left: 6px;
}
}
