style.css:
@-moz-document domain("www.coinbase.com") {
/* Coinbase Pitchblack by SLTW Version 0.4*/



html {filter: invert()}

[class="Article__Image-yr055d-9 czZMXy"] {filter: invert()}


.colorsusedinthistheme {
  color: #0000;
  color: #000000;
  color: #9f9f9f;
  color: #8a8a8a;
  color: #595858;
  color: #00ff00;
  color: #02aa6f;
  color: #aa8602;
  color: #ff00ff;
  color: #ffffff;
  color: #ffffffcf;
  color: #f2f2f2;
  }

.idMTQs {  background-image: linear-gradient(#ffffff, #f2f2f2);}

.bfhPcC {  background-image: linear-gradient(#f2f2f2, #ffffff);}

.dojklx {  background-color: #ffffffcf;}


.emtOes {  background-image: linear-gradient(#ffffff,#f2f2f2);}

.fEuUSx {  background-image: linear-gradient(#f2f2f2, #ffffff);}




/*invert the profile picture so it looks normal*/
[class="Avatar__AvatarImage-hgevj6-0 dyeWdJ Dropdown__StyledAvatar-sc-9c4nka-2 fwUXTW"],[class="Avatar__AvatarImage-hgevj6-0 fzdyiV ChangePicture__UserPicture-sc-1j7rzba-1 fSNWvM"] {filter: invert();}

/*button design*/
.gzOkyf {filter: invert(); background-color: #0000; border-color: #02aa6f;}
.gzOkyf:hover {filter: invert(); background-color: #000; animation: gzOkyf .4s linear;}
@keyframes gzOkyf {
  from {box-shadow: 0px 0px 10px 4px #02aa6f;}
  to {box-shadow: none;}
}


.hyCMpn {filter: invert(); background-color: #0000; border-color: #aa8602;}
.hyCMpn:hover {filter: invert(); background-color: #0000; border-color: #aa8602; animation: hyCMpn .4s linear;}
@keyframes hyCMpn {
  from {box-shadow: 0px 0px 10px 4px #aa8602;}
  to {box-shadow: none;}
}


.lcXJye {filter: invert(); background-color: #0000; color: #ffffff; border-color: #595858;}
.faFfbW:hover, .lcXJye:hover, .lglzYH > button:hover {filter: invert(); background-color: #0000; border-color: #aa8602; color: #aa8602;}


/*Invert colors again to original for Coins that can be exchanged*/

[alt="USDC logo"],[alt="BTC logo"],[alt="XLM logo"],[alt="ZRX logo"],[alt="REP logo"],[alt="BAT logo"],[alt="BAT logo"],[alt="LINK logo"],[alt="ATOM logo"],[alt="DAI logo"],[alt="DASH logo"],[alt="EOS logo"],[alt="ETH logo"],[alt="ETC logo"],[alt="KNC logo"],[alt="LTC logo"],[alt="OXT logo"],[alt="XTZ logo"],[alt="XRP logo"],[alt="ZEC logo"] {filter: invert()}

/*Color of favorites selected (not selected = path {stroke: #8a8a8a;}) */
.fQAJDk,.fQAJDk path {filter: invert(); fill: #00ff00; stroke: #00ff00;}

/*Market is up*/
.eFmaBt  {filter: invert(); color: #00ff00;}


/*Font adjustments (inside graph)*/
.geTkzT {filter: invert(); color: #595858;}
.geTkzT:hover {filter: invert(); color: #ffffff;}
.faYwxd {filter: invert(); color: #02aa6f;}

/*Font adjustment - Current balance*/
.bCFSYI {filter: invert(); color: #02aa6f;}



/*alerts like cookie notifier and such*/
.alert {filter: invert();
    padding: 8px 35px 8px 14px;
    margin-bottom: 20px;
    text-shadow: 0 1px 0 rgba(255,255,255,.5);
    background-color: #fcf8e3;
    border: 1px solid #fbeed5;
    border-radius: 4px;
 
}
.alert > p {color: #000 !important;}





/*graph design*/
.bvriKK, .camJhM 
 {
  box-shadow: 0px 0px 10px 2px #9f9f9f;
  border-radius: 5px;
}


[type="priceValues"]  {;
    stroke-width: 1.7;
    fill: rgba(0, 0, 0, .06);
    pointer-events: none;
    stroke: #ff00ff;
}

}
