style.css:
@-moz-document domain("bacheca.chatta.it"),
domain("www.chatta.it") {
    body:before {
        content: '';
        position: fixed;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        display: block;
        z-index: -999999;
        background: url(https://in.schiatta.it/images/bg.png) no-repeat;
        background-size: cover
    }

    body:after {
        content: '';
        position: fixed;
        right: 0;
        bottom: 0;
        left: 0;
        height: 50px;
        background: linear-gradient(0deg, rgba(251, 198, 124, .75) 0%, rgba(255, 214, 0, 0) 100%);
        display: block;
        z-index: 495;
    }

    .headerPromoSpace,
    #sideexternal,
    .applicationPageTools,
    .panel,
    .posts,
    .view,
    .view:hover,
    .footer,
    #footer,
    #content,
    .content,
    .ftborder,
    #container,
    #wrapper,
    div.profileBadge div.badge,
    div.photos div.photoList,
    div.photos div.photoFoo {
        background: none !important;
        border: 0 !important
    }

    div.wallEditorBox form div.title img {
        border-radius: 10px;
    }

    div.wallEditorBox h1 {
        color: rgba(0, 0, 0, .5);
        text-shadow: none;
    }

    div.toolbar-wrapper {
        background-color: #0b638d;
        border-bottom: 1px solid #0c618a;
        box-shadow: 1px 0 20px #0b638d
    }

    div.posts div.post div.viewAbstract:hover {
        text-shadow: none
    }

    .loadMorePosts,
    a.newPostsNotification {
        border: 0 !important;
        background-color: rgba(255, 255, 255, .95) !important;
        border-radius: 30px !important;
    }

    div.navigationWall span.title {
        color: #fff;
        text-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
        font-weight: bold;
    }

    .navigationWall a {
        border: 0 !important;
        background-color: rgba(255, 255, 255, .75) !important;
        backdrop-filter: blur(10px);
        border-radius: 30px
    }

    .loadMoreComments,
    #footer > div {
        background-color: rgba(255, 255, 255, .75) !important;
        backdrop-filter: blur(10px);
        margin-top: 15px;
        border-radius: 10px;
        border: 0 !important;
    }

    div.posts div.post {
        background-color: rgba(255, 255, 255, .75) !important;
        backdrop-filter: blur(10px);
        margin-top: 15px;
        border-radius: 30px
    }

    div.posts div.post:hover {
        background-color: rgba(255, 255, 255, .9) !important;
        border: 0 !important
    }

    div.posts div.firstPost div.view {
        margin-top: 0 !important
    }

    div.posts div.post div.viewFull div.comments div.comment div.details p.message {
        font-size: 14px;
        color: rgba(0, 0, 0, .9);
    }


    div.posts div.post div.viewFull {
        margin: 15px 0 0;
        border-radius: 30px;
    }

    div.posts div.post div.viewFull div.comments div.comment {
        border-top: 1px solid #ffffff4d;
    }

    div.posts div.post div.viewFull div.comments div.newComment {
        background: rgba(255, 255, 255, .5);
        border-top: 0;
        border-radius: 0 0 30px 30px;
    }

    #wallPostEditor {
        padding: 0;
        margin-bottom: 20px;
    }

    div.wallEditorBox form textarea {
        padding: 10px;
        box-sizing: border-box;
        background-color: rgba(255, 255, 255, .75) !important;
        backdrop-filter: blur(15px);
        color: #004160 !important;
        margin-top: 10px;
        margin-left: -10px;
        border-radius: 10px;
        border: 0;
        resize: none;
        width: 540px;
    }

    .btn-flat,
    a.btn-flat:visited,
    button.ajaxUploader-button {
        border: 0 !important;
        background-color: rgba(255, 255, 255, .8) !important;
        backdrop-filter: blur(10px);
        border-radius: 30px;
        color: #0b638d !important;
    }

    .specialbutton-flat,
    a.specialbutton-flat:visited {
        color: #fff !important;
        background-color: #0b638d !important
    }


    li.user-list-item {
        background-color: rgba(255, 255, 255, .75);
        backdrop-filter: blur(30px);
        border-radius: 30px;
        margin-top: 5px !important;
        border: 0;
    }

    li.user-list-item:hover {
        background-color: rgba(255, 255, 255, .8) !important;
    }

    li.user-list-item span.nickname,
    div.posts div.post div.view div.details a.nickname {
        color: #0b518f
    }

    li.user-list-item span.description,
    div.posts div.post div.view div.details span.date {
        color: #0186c6
    }

    .panel li.user-list-item {
        background-color: rgba(255, 255, 255, .6) !important;
    }

    .panel li.user-list-item:hover {
        background-color: rgba(255, 255, 255, .7) !important;
    }

    .toolbar-popover-content li.user-list-item {
        border-radius: 10px;
    }

    div.toolbar-popover.notification-center div.toolbar-popover-content ul li.notification a.view-notification span.user-picture,
    li.user-list-item span.user-picture {
        width: 50px;
        height: 50px;
        border-radius: 50%;
    }

    div.toolbar-popover.notification-center div.toolbar-popover-content ul li.notification a.view-notification span.user-picture img,
    li.user-list-item span.user-picture img {
        width: 50px;
        height: 50px;
        margin-top: 0;
        object-fit: cover;
    }

    div.toolbar-popover.notification-center div.toolbar-popover-content ul li.notification.unread {
        background-color: #BEEAFF;
        border-color: #83DCFF;
    }

    li.user-list-item span.actions span.action-button {
        display: inline-block;
        padding: 0 10px;
        border-radius: 2px;
        height: 18px;
        line-height: 18px;
        margin-right: 5px;
        background-color: rgba(255, 255, 255, .6) !important;
        border-radius: 5px;
        color: #444 !important;
        font-size: 9px;
    }

    li.user-list-item span.actions span.action-button:hover {
        background-color: rgba(255, 255, 255, .7) !important;
    }

    li.user-list-item span.mobile {
        right: 5px;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content {
        overflow: hidden;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content ul li {
        border: 0;
        margin-bottom: 3px;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content ul li a {
        color: #0d5575;
        background-color: rgba(255, 255, 255, .5) !important;
        border-radius: 10px;
        box-sizing: border-box;
        border: 0;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content ul li a:hover {
        color: #0d5575;
        background-color: rgba(255, 255, 255, .75) !important;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content ul li a.mio-profilo:hover {
        color: #3d9601;
    }

    div.toolbar-popover.mychatta div.toolbar-popover-content ul li a.modland:hover {
        color: #ff8000;
    }

    #sideexternal ul.navbar-tabs li.tab {
        background-color: rgba(255, 255, 255, .5) !important;
        line-height: 40px;
        border: 0 !important;
        border-radius: 0 0 20px 20px;
    }
    #sideexternal ul.navbar-tabs li.tab.active {
        background-color: rgba(255, 255, 255, .75) !important;
    }
    #sideexternal ul.navbar-tabs li.tab:hover {
        background-color: rgba(255, 255, 255, .6) !important;
    }

    div.toolbar-popover.notification-center div.toolbar-popover-content ul li.notification {
        border-radius: 10px;
        margin-bottom: 5px;
        background: rgba(255, 255, 255, .5);
        border: 0;
    }

    div.toolbar-popover.notification-center div.toolbar-popover-content ul li.notification:hover {
        background-color: rgba(255, 255, 255, .75);
    }

    .site-background-layer {
        background: rgba(0, 0, 0, 0.5) !important;
        opacity: 1 !important;
        backdrop-filter: blur(10px);
    }

    div.toolbar-popover {
        background: rgba(255, 255, 255, .6);
        border-radius: 10px;
        box-shadow: 0 5px 10px rgba(0, 0, 0, .3);
    }

    div.toolbar-popover.kisses .gameboard {
        background: none;
    }

    div.toolbar-popover div.toolbar-popover-content {
        background: none;
        overflow-y: auto;
    }

    div.toolbar-popover div.toolbar-popover-content ul li {
        border: 0;
    }
    .toolbar-popover messages div.toolbar-popover-content ul li {
        box-sizing: border-box;
        width: 378px !important;
    }

    div.toolbar-popover div.toolbar-popover-content ul li.info {
        color: rgba(0, 0, 0, .6);
        background: rgba(255, 255, 255, 0.25);
        border-radius: 30px;
    }

    div.messages-chat-talkdialog {
        border: 0;
        background: none !important;
    }

    div.messages-chat-dockbar div.messages-chat-dockbar-tab,
    div.messages-chat-talkdialog div.message-chat-talk-info {
        background: #222;
        border-radius: 10px 10px 0 0;
    }

    div.messages-chat-talkdialog div.message-chat-talk-messages {
        background: rgba(255, 255, 255, .9);
        backdrop-filter: blur(10px);
        border-bottom: 0;
    }

    div.messages-chat-talkdialog div.message-chat-talk-messages div.message.Out div.message-body::after {
        border-left: 5px solid rgb(165, 219, 249)
    }

    div.messages-chat-talkdialog div.message-chat-talk-messages div.message.Out div.message-body {
        background-color: rgb(165, 219, 249)
    }

    div.messages-chat-talkdialog div.message-chat-talk-messages div.message div.message-body {
        background: rgb(255, 255, 255);
    }

    div.messages-chat-talkdialog div.message-chat-talk-messages div.message.In div.message-body::after {
        border-right: 5px solid rgb(255, 255, 255);
    }

    div.toolbar-popover.messages div.toolbar-popover-content ul li span.talk-actions {
        position: absolute;
        right: -210px;
        top: 0;
        width: 200px;
        height: 42px;
        display: block;
        -webkit-transition: right 250ms ease-in-out;
        -moz-transition: right 250ms ease-in-out;
        -ms-transition: right 250ms ease-in-out;
        -o-transition: right 250ms ease-in-out;
        transition: right 250ms ease-in-out;
    }

    .picture {
        border-radius: 10px
    }
}
