h1,h2{color:#fff;text-shadow:rgba(255,255,255,0) 0 1px 0;padding-top:10px;}
.popup_menu{background:#000;opacity:0.99;border-bottom:4px solid #fff;height:305px;z-index:999;}
#search{background:#222;}
.popup_menu .mobiletitle{width:100%;}
.mobilebuttonbottom{width:120px;}
.arrowchat_label{font-weight:bold;}
input[src="iPhone/iPhoneLogin.png"]{margin-left:40% !important;margin-right:40% !important;}
li.link a{color:#88aace !important;}
li.link a:hover{text-decoration:underline !important;}
.page-margin .tcat{font-size:20pt;background:#111;padding-top:5px;padding-bottom:5px;}
.page-margin img[src*="avatars"]{width:auto;height:50px;}
.page-margin ul{color:#fff;margin-top:10px;margin-bottom:10px;border-radius:0px;width:99.3%;}
.page-margin img[src$="user_online.gif"]{content:url("http://icons.iconarchive.com/icons/hopstarter/scrap/32/Aqua-Ball-Green-icon.png");max-width:13px;max-height:13px;overflow:hidden;margin-bottom:7px;margin-left:10px;}
.page-margin img[src$="user_offline.gif"]{content:url("http://icons.iconarchive.com/icons/hopstarter/scrap/32/Aqua-Ball-Green-icon.png");max-width:13px;max-height:13px;overflow:hidden;margin-bottom:7px;margin-left:10px;filter:grayscale(100%);}
.page-margin img[src$="user_invisible.gif"]{content:url("http://icons.iconarchive.com/icons/hopstarter/scrap/32/Aqua-Ball-Green-icon.png");max-width:13px;max-height:13px;overflow:hidden;margin-bottom:7px;margin-left:10px;filter:hue-rotate(270deg) saturate(9);}
a[onClick="javascript:$('.popup_menu').toggle();"]{visibility:hidden;width:70px;height:70px !important;position:absolute;right:-45px;top:-20px;}
