style.css:
@-moz-document domain("web.telegram.org"){
html, body {
    font: 14px/20px'Open Sans', "Lucida Grande", "Lucida Sans Unicode", Arial, Helvetic, Verdana, sans-serif;
    color: rgba(255, 255, 255, 0.8) !important;
    background: #353941 !important;
}
a, a.tg_radio {
    color: #fff;
}
a:hover {
    color: #5f85db;
}
h1, h2, h3, h4, h5 {
    color: #fff;
}
.tg_head_split, .tg_head_logo_dropdown .dropdown-toggle:hover, .tg_head_logo_dropdown .dropdown-toggle:focus, .tg_head_logo_dropdown .dropdown-toggle:active, .dropdown.open .tg_head_btn, .tg_head_btn:hover, .login_head_bg, .md_modal_head {
    background: #26282b;
}
.login_form_wrap {
    background: #353941;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2), 1px 0 0 rgba(0, 0, 0, .1), -1px 0 0 rgba(0, 0, 0, .1);
}
.im_dialogs_scrollable_wrap a.im_dialog {
    padding: 16px;
}
.dropdown-menu {
    border: none !important;
    background: #26282b;
}
.tg_head_logo_dropdown .dropdown-menu>li>a, .tg_head_peer_dropdown .dropdown-menu>li>a, .countries_scrollable_wrap a.countries_modal_country, .login_footer_wrap, .logo_footer_learn_more_link, .logo_footer_learn_more_link:active, .logo_footer_learn_more_link:focus, .logo_footer_learn_more_link:hover {
    color: #fff;
}
.tg_head_peer_dropdown .dropdown-menu>li>a:hover, .countries_scrollable_wrap a.countries_modal_country:hover {
    background: #5f85db;
}
.tg_head_logo_dropdown .dropdown-menu>li>a:hover {
    background: #353941;
}
.im_dialogs_scrollable_wrap .active a.im_dialog:hover, .im_dialogs_scrollable_wrap .active a.im_dialog_selected, .im_dialogs_scrollable_wrap .active a.im_dialog, .im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected {
    background: #5f85db;
}
.im_message_wrap {
    max-width: 85%;
    padding: 0 15px;
}
.im_send_panel_wrap {
    max-width: 85%;
}
.im_message_author, .im_message_fwd_author, .login_form_head {
    color: #fff;
}
.login_form_lead {
    color: rgba(255, 255, 255, 0.7);
}
.im_message_reply {
    background: #26282b;
    border-radius: 5px;
    padding: 10px 15px;
}
.im_message_reply_border {
    background: #5f85db;
    width: 6px;
    height: 56px;
    border-radius: 5px 0 0 5px;
    margin-left: -15px;
    margin-top: -10px;
}
.im_message_reply_author {
    color: #5f85db;
}
.im_page_wrap, .tg_head_split {
    max-width: 100%;
}
.im_page_wrap {
    border: none;
    background: #353941;
    height: 100vh !important;
}
.im_dialogs_col_wrap {
    background: #26282b;
    border-right: 2px solid transparent;
    height: 100vh;
}
.im_dialog_peer {
    color: #fff;
    font-size: 14px;
}
a.im_dialog .im_dialog_chat_from_wrap, a.im_dialog .im_short_message_media, a.im_dialog .im_short_message_service {
    color: #90b8f8;
}
.im_short_message_text, .im_history_empty, .im_history_not_selected {
    color: rgba(255, 255, 255, 0.7) !important;
}
.md-input {
    background: #353941 !important;
    color: #fff;
}
.im_dialogs_search_field {
    background-color: #353941;
    background-position: -6px -1030px;
    border: 1px solid transparent;
    color: #fff;
}
.im_dialogs_search_field:active, .im_dialogs_search_field:focus {
    background-color: #26282b;
}
.im_record {
    width: 13px;
}
.im_record_bg {
    background: #F63737;
    width: 40px;
    padding: 13px 12px 13px 12px;
}
.im_record_bg:hover {
    background: #BB2727;
}
.form-control.no_outline {
    border-color: rgba(255, 255, 255, 0.7);
}
.form-control.no_outline:focus {
    border: 1px solid #5f85db;
}
.btn-primary {
    background: #5f85db;
}
.btn-primary:hover {
    background: #90b8f8;
}
.btn-md-primary, .btn-md, .btn-md:focus, .btn-md:hover {
    color: #5f85db;
}
.im_edit_cancel_link {
    background: #F63737;
    color: #fff;
}
.im_edit_cancel_link:hover {
    background: #BB2727;
    color: #fff;
}
.im_message_selected .im_message_outer_wrap, .im_history_select_active .im_message_outer_wrap:hover {
    background: #5f85db;
}
.im_message_selected a, .im_message_views_cnt, .im_message_date {
    color: #fff !important;
}
.im_dialogs_scrollable_wrap a.im_dialog_searchpeer, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer:hover, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer_selected {
    background: #5f85db;
}
.im_dialog_message {
    color: rgba(255, 255, 255, 0.7);
}
.composer_emoji_tooltip {
    border: none;
    background: #353941;
}
.im_send_form {
    max-width: 100%;
}
.im_send_field_wrap {
    margin-bottom: 20px;
}
.composer_rich_textarea:focus, .composer_textarea:focus {
    color: #fff;
    border-color: #5f85db;
    font-size: 14px;
    font-weight: 500;
}
.modal-content {
    background: #353941;
}
a.tg_checkbox, p.tg_checkbox {
    color: #fff;
    font-weight: bold;
}
.btn-link:focus, .btn-link:hover {
    color: #5f85db !important;
}
.icon-tooltip-tail {
    background: #353941;
    border-color: #353941;
}
.composer_emoji_tooltip_categories {
    background: #353941 !important;
}
.icon-new-group, .icon-contacts, .icon-settings, .icon-faq, .icon-about {
    opacity: 0.5;
}
.im_message_date, .im_message_fwd_date {
    font-size: .75em;
    color: #fff;
    font-weight: 600;
    letter-spacing: 0.05em;
}
.im_message_file_button_dl_audio, .tg_slider_thumb, .audio_player_seek_slider .tg_slider_track_fill, .audio_player_volume_slider .tg_slider_track_fill, .audio_player_volume_slider .tg_slider_thumb {
    background: #5f85db;
}
.im_send_dropbox_wrap, .im_recorder_time {
    color: #fff;
}
.im_history_message_wrap {
    margin-top: 4px;
}
}
