@-moz-document domain("sciencefiles.org") {
/* ==UserStyle==
@name         SCIENCEFILES.ORG Dark Theme (2020)
@namespace    de.sidneys.userstyles
@version      0.9.1
@description  Dark Theme for SCIENCEFILES – KRITISCHE SOZIALWISSENSCHAFTEN / Dunkles Farbschema für SCIENCEFILES – KRITISCHE SOZIALWISSENSCHAFTEN
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   VARIABLES
   ==================================== */

:root
{
    --color-accent-1: hsl(32, 98%, 60%);
    --color-accent-2: hsl(202, 100%, 65%);
    --color-accent-3: hsl(22, 96%, 42%);
    --color-container-15: hsl(0, 0%, 15%);
    --color-container-20: hsl(0, 0%, 20%);
    --color-container-25: hsl(0, 0%, 25%);
    --color-container-30: hsl(0, 0%, 30%);
    --color-container-50: hsl(0, 0%, 50%);
    --color-container-60: hsl(0, 0%, 60%);
    --color-text-50: hsl(0, 0%, 50%);
    --color-text-67: hsl(0, 0%, 67%);
    --color-text-90: hsl(0, 0%, 90%);
    --color-text-95: hsl(0, 0%, 95%);
    --color-text-100: hsl(0, 0%, 100%);
    --color-text-100-translucent-65: hsla(0, 0%, 100%, 65%);
    --font-family-itcfranklin: "ITC Franklin", "HelveticaNeue", "Helvetica Neue Light", "Helvetica Neue", "Helvetica", "Arial", "Lucida Grande", "sans-serif";
    --font-family-postoniwide: "Postoni Wide", "Georgia", "serif";
}
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   FONTS
   ==================================== */

/* Postoni Wide
   ==================================== */

@font-face
{
    font-family: "Postoni Wide";
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/PostoniWide-Regular.otf") format("opentype");
}

@font-face
{
    font-family: "Postoni Wide";
    font-style: normal;
    font-weight: 700;
    font-display: swap;
    src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/PostoniWide-Bold.otf") format("opentype");
}

@font-face
{
    font-family: "Postoni Wide";
    font-style: italic;
    font-weight: 400;
    font-display: swap;
    src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/PostoniWide-Italic.otf") format("opentype");
}

@font-face
{
    font-family: "Postoni Wide";
    font-style: italic;
    font-weight: 700;
    font-display: swap;
    src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/PostoniWide-BoldItalic.otf") format("opentype");
}

/* ITC Franklin
   ==================================== */

@font-face
{
   font-family: "ITC Franklin";
   font-style: normal;
   font-weight: 400;
   font-display: swap;
   src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/ITC_Franklin-Light.otf") format("opentype");
}

@font-face
{
   font-family: "ITC Franklin";
   font-style: normal;
   font-weight: 700;
   font-display: swap;
   src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/ITC_Franklin-Bold.otf") format("opentype");
}

@font-face
{
   font-family: "ITC Franklin";
   font-style: normal;
   font-weight: 200;
   font-display: swap;
   src: url("https://raw.githubusercontent.com/bella-strange/userstyles/master/assets/fonts/ITC_Franklin-Thin.otf") format("opentype");
}
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   GLOBAL
   ==================================== */

/* Container
   ==================================== */

html,
html *
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-smooth: auto;
}

body
{
	color: var(--color-text-67);
    background: unset;
    background-color: var(--color-container-20);
}

hr {
	border: 0;
	height: 0.5px;
    margin-bottom: 1.4rem;
    margin-top: 1.4rem;
    background-color: var(--color-text-100-translucent-65);
}

/* Typography
   ==================================== */

h1,
h2,
h3,
h4,
h5,
h6
{
	color: var(--color-text-95);
    font-family: var(--font-family-itcfranklin);
	/*font-weight: 400;*/
}

h1.title
{
	font-family: var(--font-family-postoniwide);
	line-height: 1.2 !important;
    font-size: 2.7rem !important;
    font-style: normal;
    font-weight: 700 !important;
    color: var(--color-text-100);
}

.post.excerpt h2.title a
{
	font-family: var(--font-family-itcfranklin);
    color: var(--color-text-100);
    font-weight: 700 !important;
}

.post.excerpt .post-content
{
	font-family: var(--font-family-itcfranklin);
    color: var(--color-text-90);
}

.widget h3
{
	font-family: var(--font-family-itcfranklin);
	line-height: 1.25;
    font-size: 1.25rem;
    font-weight: 800;
    color: var(--color-text-100);
}

.widget li
{
	font-family: var(--font-family-itcfranklin);
	line-height: 1.25;
    font-size: 1rem;
    font-weight: 300;
    color: var(--color-text-90);
}

#sidebars .widget a
{
	color: var(--color-text-100);
}
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   ELEMENTS
   ==================================== */

/* Navigation
   ==================================== */

.nav-previous a, .nav-next a
{
	background: var(--color-container-15);
}

#navigation ul li
{
	padding-right: 1rem;
}

#navigation ul li:last-child
{
	padding-right: 0;
}

#navigation .menu-item-has-children > a::after
{
	right: 2rem;
}

#navigation .menu-item-has-children > a
{
	padding-right: 2rem;
}

#navigation li a
{
	padding: 0 1rem 0 0;
}

#navigation li:last-child a
{
	padding-right: 0;
}

#navigation ul ul
{
	border-color: var(--color-container-25);
}

#navigation ul ul li
{
	padding-left: 1rem;
	background: var(--color-container-15);
	border-color: var(--color-container-25);
}

#navigation ul ul a
{
	color: var(--color-text-67);
}

/* Widgets
   ==================================== */

.article
{
	width: 75%;
	max-width: unset;
}

.sidebar.c-4-12
{
	width: 25%;
	max-width: unset;
	padding-left: 2rem;
}

#page
{
	padding-top: 0.7em;
}

.single_post
{
    /* background-color: var(--color-container-15); */
    background-color: var(--color-container-30);
}

.post.excerpt
{
    padding: 1rem 1.5rem 2rem 1.5rem;
    margin-bottom: 1rem;
    background-color: var(--color-container-30);
}


.single_post header
{
	margin-top: unset;
	margin-bottom: unset;
}

.single_post header h1
{
	margin-top: 10px;
	margin-bottom: 20px;
}

.related-posts,
.postauthor
{
    background-color: transparent;
}

.related-posts .post.excerpt
{
    background-color: var(--color-container-15);
}

.related-posts #featured-thumbnail
{
	max-width: unset;
}

#jp-relatedposts
{
	margin: unset;
    padding: unset;
}

#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post
{
	width: 30%  !important;
	margin-right: 1rem !important;
	padding-right: 0 !important;
}

/* Typography
   ==================================== */

.post-single-content,
.post-single-content span
{
	font-family: "Georgia", "Times New Roman", "serif" !important;
    font-size: 1.25rem !important;
	line-height: 1.75 !important;
}

.pf-content p,
.pf-content p *,
.DPSlot_container,
.DPSlot_container *
{
	color: var(--color-text-90) !important;
	font-weight: 300 !important;
}

.article ol li
.article ol li span,
.article ul li,
.article ul li span,
.article blockquote,
.article blockquote span,
.post-single-content ol li span
{
	font-family: "Georgia", "Times New Roman", "serif" !important;
	color: var(--color-text-100) !important;
}

.article blockquote
{
	font-style: italic;
}

.article ul,
.article blockquote
{
	margin: 0.7rem 3rem;
	padding: 0;
}

.pf-content p,
.pf-content p span
{
	margin-bottom: 0.7rem;
	text-align: left;
}

.pf-content p span a
{
	color: var(--color-text-100) !important;
    text-decoration: underline;
}

.pf-content p span a:hover
{
	color: var(--color-accent-1) !important;
}

#tabber .inside li .meta b, footer .widget li a:hover, .fn a, .reply a, #tabber .inside li div.info .entry-title a:hover, #navigation ul ul a:hover, .single_post a, a:hover, .sidebar.c-4-12 .textwidget a, #site-footer .textwidget a, #commentform a, #tabber .inside li a, .copyrights a:hover, a, .sidebar.c-4-12 a:hover, .top a:hover, footer .tagcloud a:hover
{
	color: var(--color-accent-1);
}

span.sticky-post, .nav-previous a:hover, .nav-next a:hover, #commentform input#submit, #searchform input[type="submit"], .home_menu_item, .primary-navigation, .currenttext, .pagination a:hover, .readMore a, .mts-subscribe input[type="submit"], .pagination .current, .woocommerce nav.woocommerce-pagination ul li a:focus, .woocommerce nav.woocommerce-pagination ul li a:hover, .woocommerce nav.woocommerce-pagination ul li span.current, .woocommerce-product-search input[type="submit"], .woocommerce a.button, .woocommerce-page a.button, .woocommerce button.button, .woocommerce-page button.button, .woocommerce input.button, .woocommerce-page input.button, .woocommerce #respond input#submit, .woocommerce-page #respond input#submit, .woocommerce #content input.button, .woocommerce-page #content input.button
{
	background-color: var(--color-accent-3);
}

#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post .jp-relatedposts-post-context,
#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post .jp-relatedposts-post-date,
#jp-relatedposts .jp-relatedposts-items-visual .jp-relatedposts-post
{
	opacity: unset;
}

#jp-relatedposts h3.jp-relatedposts-headline
{
	font-size: unset;
	font-weight: unset;
}

#jp-relatedposts h3.jp-relatedposts-headline,
#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post
{
    font-family: var(--font-family-itcfranklin);
}

#jp-relatedposts .jp-relatedposts-items-visual h4
{
    margin-top: 0.5rem !important;
    line-height: 1.25 !important;
}

#jp-relatedposts .jp-relatedposts-items-visual p
{
    font-size: 0.8rem !important;
    line-height: 1.25;
}

#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post .jp-relatedposts-post-context
{
    display: none;
}

.related-posts .title,
.postauthor-top h3,
#jp-relatedposts h3.jp-relatedposts-headline,
.postauthor h4
{
    color: var(--color-text-100);
}
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   HIDDEN ELEMENTS
   ==================================== */

/* Sidebar
   ==================================== */

#paypal_donations-4,
#blog-stats-3,
.stb-black-container,
#text-29,
#jetpack_widget_social_icons-2,
#text-28,
#text-24,
#rss_links-3,
#yadawiki_toc_widget-3,
#linkcat-1356
{
	display: none;
}


/* Footer
   ==================================== */

#text-25,
#text-26,
#text-27
{
	display: none;
}

.footer-widgets
{
	padding: 0
}

.footer-widget
{
	margin: 0
}


/* Widgets
   ==================================== */

.heateor_sss_sharing_container,
#glt-translate-trigger
{
	display: none;
}


/* Ads
   ==================================== */

body > table
{
    display: none;
}

.OUTBRAIN
{
    display: none;
}

.OUTBRAIN + p
{
	margin-bottom: 0;
}

.OUTBRAIN + p + hr,
hr + p + hr,
hr + hr
{
    display: none;
}
}

@-moz-document domain("sciencefiles.org") {
/* ====================================
   COMMENT SECTION
   ==================================== */

/* Ads
   ==================================== */

#comments,
.total-comments
{
    background-color: transparent;
}

.total-comments
{
    color: var(--color-text-100);
}

.commentlist p
{
	font-family: var(--font-family-itcfranklin);
    color: var(--color-text-90);
}
}
