style.css:
@-moz-document url-prefix("https://www.gamepro.de/xenforo/"), url-prefix("https://gamepro.de/xenforo/") {
::selection {
    background-color:#bbc
}
::-webkit-scrollbar-track {
    background: rgb(33, 33, 33);
}
.messageHeading {
    background-color:rgb(21, 44, 66);
    box-shadow: inset 0 0 2em 0em black
}
body {
    background-color:#111b1c;
    color:rgb(250, 250, 250, 0.75);
}
#moderatorBar {
    background-color:#111b1c;
}
.breadBoxTop, .breadBoxBottom {
    background-color:#000;
    padding:0;
}
.breadcrumb .crust a.crumb, .breadcrumb .crust:last-child a.crumb, .titleBar h1 {
    color:rgba(250, 250, 250, .75)
}
.breadcrumb .crust a.crumb:hover, #pageDescription, .PageNav .pageNavHeader {
    color:rgba(255, 255, 255, 0.75);
}
.breadcrumb .crust:last-child a.crumb {
    font-weight:900;
}
.navTabs .navTab.selected .navLink {
    background-color: rgb(17, 27, 28, 0);
    padding-top: 0px;
    margin-top: 0px;
    box-shadow: 0px -5px 0px 0px rgb(21, 44, 66, 0) inset;
    font-weight:900;
}
::-webkit-scrollbar-thumb {
    background-color:rgb(21, 44, 66);
}
a.callToAction span {
    color:rgba(255, 255, 255, 0.75);
    background-color:#000;
}
#header, .navTabs .navTab.Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(21, 44, 66);
    box-shadow: inset 0 0 2em 1em #000;
}
a.callToAction span:hover {
    background-color:rgb(17, 27, 28)
}
a.callToAction {
    padding:0;
}
navTabs .navTab.selected .tabLinks a:focus {
    color:rgb(21, 44, 66);
    text-decoration:none;
    outline:0
}
.navTabs .navTab.selected .tabLinks .Popup a:hover, .navTabs .navTab.selected .tabLinks .Popup a:focus {
    color:inherit;
    background:none;
    border-color:transparent;
    border-radius:0;
    text-shadow:none
}
#content .pageContent {
    background-color:#111b1c;
    box-shadow: 0 0 3em 1em #000;
}
a.callToAction span:hover, #header, .navTabs .navTab.Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(21, 44, 66);
}
#lbSpacer {
    height: 0px;
}
.PageNav a.currentPage, .PageNav a {
    background-color: rgb(21, 44, 66);
    border-radius:10px;
}
a:link, a:visited {
    color:rgba(255, 255, 255, 0.75);
}
.sectionMain {
    padding:0
}
.discussionListItem {
    background-color: rgb(17, 27, 28);
}
.discussionListItem:nth-of-type(even), .discussionListItem:nth-of-type(even) .posterAvatar, .discussionListItem:nth-of-type(even) .stats {
    background-color:rgba(33, 33, 33, 0.75)
}
.discussionListItems {
    background-color: #000;
}
.discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: rgba(255, 255, 255, 0);
}
.avatar img, .avatar .img, .avatarCropper {
    background-color: rgba(255, 255, 255, 0);
    padding: 0;
    border: 0;
}
.discussionListItem {
    background-color: rgb(255, 255, 255, 0);
    border-bottom:0;
    border-left:0;
    border-right:0;
    box-shadow: 0em 0em 3em 1em #000;
}
.discussionListItem .stats dl {
    padding: 0 5px;
    border-left: 1px solid rgb(21, 44, 66);
    border-right: 1px solid rgb(21, 44, 66);
}
.pairsJustified dd {
    color:rgba(255, 255, 255, 0.75);
}
.discussionListItem .iconKey span {
    background:none
}
.fa {
    color:rgba(255, 255, 255, 0.75)
}
body .itemPageNav a {
    color: rgb(255, 255, 255, 0.75);
    background-color: rgb(21, 44, 66);
    border: 1px solid rgb(21, 44, 66);
}
body .itemPageNav a:hover {
    background-color:rgb(21, 44, 66);
}
.discussionList .sectionHeaders, .nodeList .categoryStrip {
    border-right:0;
    background-color: rgb(21, 44, 66);
}
.discussionListItem:hover {
    background-color:rgb(21, 44, 66);
}
a:hover {
    color:#fff!important;
}
.footer, .footer_secondary, .cd-top.cd-is-visible, .cd-top {
    background-color: rgb(21, 44, 66)
}
#copyright {
    display:none;
}
.PageNav .scrollable {
    height:20px;
}
a.PageNavPrev, a.PageNavNext {
    padding:0;
    color:rgba(255, 255, 255, 0.75);
}
.PageNav a {
    font-size:11px;
    text-transform:none;
}
.PageNav a:hover {
    font-size:12px;
    background-color: rgb(21, 44, 66);
}
.pageNavLinkGroup {
    font-size:11px;
    padding:0;
}
.sectionFooter {
    background-color: rgb(21, 44, 66);
    box-shadow: inset 0em 0em 2em 0em #000;
}
#DiscussionListOptionsHandle a {
    background-color: rgb(21, 44, 66);
}
.nodeList .node.level_1 {
    margin-bottom:0;
}
.node.node .forumNodeInfo.unread .nodeIcon:after {
    color: rgb(21, 44, 66);
}
.nodeList .forumNodeInfo {
    background-color: rgb(17, 27, 28);
    box-shadow: inset 0em 0em 2em 0em #000;
    border-bottom: 1px solid rgb(0, 0, 0);
}
.node .unread .nodeTitle:after {
    background-color: rgb(21, 44, 66);
}
.nodeList {
    box-shadow: 0em 0em 3em 1em #000
}
.nodeList .forumNodeInfo:hover {
    background-color: rgb(21, 44, 66);
}
.idstats li {
    color:#bbc;
}
.secondaryContent:hover {
    background:none;
}
.lastThreadTitle {
    color:#bbc;
}
.lastThreadTitle {
    color:#bbc;
    background:none;
}
.sidebar .section .secondaryContent .footnote {
    color:#bbc;
}
.sidebar .section .secondaryContent h3, .sidebar .section .secondaryContent h3 a {
    color:#bbc;
    background-color: rgb(21, 44, 66);
}
.sidebar .section .secondaryContent h3::after {
    border-top: 8px solid rgb(21, 44, 66);
}
.button.primary, .button {
    background-color: rgb(21, 44, 66);
}
.statusEditorCounter {
    color: #bbc;
}
.messageUserBlock, .messageUserBlock div.avatarHolder, .messageUserBlock .extraUserInfo, .messageList .message {
    border:none;
    background-color:rgb(33, 33, 33);
}
#messageList .messageUserInfo {
    border-right:0;
    box-shadow: 0em -1em 2em -1em #000;
    border-radius: 100px;
}
.messageUserBlock .userTitle {
    color:#bbc;
}
.thread_view .messageMeta {
    background:none;
    border-top: 1px solid rgb(21, 44, 66);
    box-shadow: inset 0em 0em 2em 0em #000;
}
.bbCodeBlock .type {
    background:none;
    border-top: 1px solid rgb(21, 44, 66);
    box-shadow: inset 0em 0em 2em 0em #000;
    color:#bbc;
    border:none;
}
.messageText {
    color:rgba(255, 255, 255, 0.75);
}
.bbCodeBlock {
    border:none;
}
.message .messageInfo {
    border:none;
}
.ugc a:hover, .ugc a:focus {
    background:none;
}
.ugc a:link, .ugc a:visited {
    margin:0;
}
.bbCodeQuote .quoteContainer {
    background-color: rgb(21, 44, 66);
    box-shadow: inset 0em 0em 2em 0em #000;
    border-radius:0;
}
.statistic span, .secondaryContent {
    background:none;
    color:#bbc;
    border:none;
}
.LikeText:before {
    font-size:1em;
    color:#bbc;
}
.message .likesSummary {
    border:none;
}
.messageContent a:hover, .signature a:hover {
    all:unset;
}
.userBanner.bannerStaff {
    color: rgb(255, 255, 255, 0.75);
    background-color: rgb(21, 44, 66);
    border-color: rgb(21, 44, 66);
}
.bbCodeQuote .quoteContainer .quoteExpand {
    color: #bbc;
    background: linear-gradient(to bottom, rgba(21, 44, 66, 0) 0%, rgb(0, 0, 0) 80%);
}
#userfield-custom-trigger::before {
    color: rgb(117, 117, 117);
}
#userfield-custom-trigger>div {
    background-color: rgb(21, 44, 66);
}
.button.primary:hover, .button:hover {
    font-size:13px;
    background-color: rgb(21, 44, 66)
}
.Menu .secondaryContent{background-color: rgb(21, 44, 66,0.97)}
.Menu .primaryContent {background-color: rgba(0, 0, 0, 0.85);}
.navTabs .navTab.selected.PopupOpen .navLink, .navTabs .navTab.PopupClosed:hover {background-color: rgb(21, 44, 66);box-shadow: inset 0 0 2em 1em #000;}
.Menu .sectionFooter {background-color:#000;}
.Popup.PopupContainerControl.PopupOpen {background-color: rgb(21, 44, 66);}
.node:hover .nodeLastPost:hover, .node:focus .nodeLastPost:focus, .node .nodeLastPost {background:none;}
.nodeList .forumNodeInfo:hover .nodeLastPost{background:none;}
.pageNavLinkGroup a.PopupControl, .Popup .arrowWidget {color: rgba(255, 255, 255, 0.75);}
.discussionListItem.InlineModChecked, .discussionListItem.InlineModChecked .posterAvatar, .discussionListItem.InlineModChecked .main, .discussionListItem.InlineModChecked .stats, .discussionListItem.InlineModChecked .lastPost, .discussionListItem.deleted.InlineModChecked, .discussionListItem.moderated.InlineModChecked {background-color: rgb(21, 44, 66);}
#InlineModOverlay .selectionControl {background-color: #000}
/*0.0.3*/
.userBanner.bannerPrimary {color: rgb(255,255,255);background-color: rgb(21, 44, 66);border-color: rgb(21, 44, 66);}
.message .newIndicator span {background-color: rgb(21, 44, 66);}
.message .newIndicator {background-color: rgb(21, 44, 66);border: 1px solid rgb(21, 44, 66);}
.thread_view .threadAlerts, .xenPreviewTooltip .previewContent {background-color: rgb(21, 44, 66);box-shadow: 0em 1em 1em 0em #000;}
.previewTooltip blockquote {background-color: rgb(21, 44, 66)}
.hasJs .bbCodeSpoilerText {background-color: rgb(21, 44, 66);box-shadow: inset 0em 0em 2em 0em #000;border:none;border-radius:7px;}
@media (max-width: 480px) {
.Responsive .messageUserBlock {background: rgb(21, 44, 66);overflow: inherit;}
.Responsive .messageUserBlock div.avatarHolder {
    float: left;
    background: none;
    padding-left: 5px;
    margin-top: -57px;}
.Responsive .messageUserInfo {border-bottom:none;}}
.navPopup .PopupItemLinkActive:hover{background:none;}
.xenOverlay.memberCard .userInfo h3 a {
    color: rgb(224, 224, 224);}
.xenOverlay.memberCard .userInfo h4 {color: #bbc;}
.xenOverlay.memberCard {color: #bbc;background-color: rgb(21, 44, 66);}
.primaryContent, .mainContentBlock  {background-color: rgb(21, 44, 66);}
.textHeading, .explain .CurrentStatus {color:#bbc;}
.navigationSideBar .primaryContent {border-right: 1px solid;}
.heading, .xenForm .formHeader, .subHeading {color: rgb(224, 224, 224);background-color: rgb(33, 33, 33);}
.larger.textHeading, .xenForm .sectionHeader {color: rgb(224, 224, 224);}
.navigationSideBar a:hover {background-color:#000;}
.ugc a:link, .ugc a:visited {color: #bbc;}
.ugc a:hover {background:none; cursor:pointer;margin:3px;}
.textCtrl {color: #d7edfc;background: rgb(17, 27, 28)}
.searchResult .snippet a {color: #bbc;}
.avatar img, .avatar .img, .avatarCropper {border-radius:50px;}
.tabs {background-color:#000;}
.tabs li.active a, .tabs.noLinks li.active {color: rgb(224, 224, 224);background-color: rgb(21, 44, 66);}
.xenOverlay .formOverlay, .xenOverlay .formOverlay .heading, a.redactor_modal_btn.redactor_btn_modal_close.button:hover {color: #bbc;background-color: rgb(21, 44, 66);}
.redactor_toolbar {background-color: rgb(33, 33, 33);}
.redactor_toolbar li a {color: rgb(21, 44, 66);}
#ProfilePanes, .profilePage .tabs.mainTabs {background: rgb(21, 44, 66);}
.profilePage .tabs.mainTabs li a {background: rgb(21, 44, 66);color:#bbc}
.profilePage .tabs.mainTabs li.active a {background: #000;color:#bbc}
.navPopup .listPlaceholder ol.secondaryContent.Unread, .formPopup .controlsWrapper, #QuickSearch.active, .redactor_dropdown, .redactor_dropdown a.icon {background-color:rgb(21, 44, 66)}
.sectionMain, .pollBlock .questionMark, .pollBlock .buttons .button:hover, .pollBlock .pollContent, .pollResult .barContainer {background-color: rgb(21, 44, 66);}
.pollResult .bar {background: #adf !important;}
}
