style.css:
@-moz-document domain("hattrick.org") {
body {
    margin: 0 0 !important;
    width: auto !important;
    text-align: left !important;
    color: #373737 !important;
    background-color: darkgrey;
}
#footer {
    display: none !important;
}
div[class*="ad_"] {
    display: none!important
}
#page {
    background-color: darkgrey !important;
    border: 1px solid darkgreen !important;
}

#hattrickNoSupporter {
    width: 1024px !important;
}
h2.info,
h3 {
    color: darkgreen !important;
}
/*.subMenuBox ul li {
    color: red !important;
}

#ticker div a {
    color: red !important;
}*/

/*sidetext*/
.subMenuBox a,
.subMenuBox a,
.sidebarBox a.active {
    color: white !important;
}

#menu {
    background-color: grey!important;
}
.main .boxHead h2,
.sidebarBox h2,
.subMenuBox h2 {
    background-color: grey !important;
}
#menu a:hover {
    background-color: #666666 !important;
}
h1 {
    color: #FFFFFF !important;
}
.main .mainBox h2 {
    background-color: #999999 !important;
    border-bottom: 2px solid grey !important;
}
.main table th {
    border-bottom: 2px solid #333333 !important;
    background-color: #999999!important;
    color: white !important;
}
h2 {
    /*background-color:#282828 !important;*/
    background-color: darkgrey !important;
    border-bottom: 2px solid #999999 !important;
    color: darkgreen !important;
}
table tbody {
    background-color: lightgrey;
}
.main/* MAINSITE */
#menu {
    background-image: none !important;
    background-color: lightgrey !important;
}
.sidebarBox .boxBody,
.subMenuBox .boxBody,
.main .boxBody {
    background: lightgrey none repeat scroll 0 0 !important;
}
#header {
    background-image: none !important;
    background-color: darkgrey !important;
}
#top {
    background-image: none !important;
    background-color: darkgrey !important;
}
#content {
    background-image: none !important;
    background-color: darkgrey !important;
}

#bottom {
    background-image: none !important;
    background-color: darkgrey!important;
}
#page {
    padding: 0 0 !important;
}
/* LINKS */
a {
    color: #1c5106;
}
table tr.even {
    border-top: 1px #333333 solid !important;
}
table tr.odd {
    border-top: 1px #333333 solid !important;
}
table tr.topBorder td {
    border-top: 2px #333333 solid !important;
}
.alert,
.error,
.ok,
.main .ok{
    background-color: darkgrey !important;
    color:black;
    border: 1px solid #333333 !important;
}
.main .boxFooter,
.subMenu .boxFooter,
.sidebarBox .boxFooter,
.subMenuBox .boxFooter {
    background-color: lightgrey;
}

#shortcuts {
    top: 0 !important;
}
/* HATRICK */
.main table,
#sidebar table,
.subMenuBox table {
    background-color: #c4c4c4!important;
}
/* LINE UP */
.name {
    color: #000000 !important;
}
.ok {
    background-color: #282828 !important;
    border: 1px solid #333333 !important;
}
.user,
.national {
    background-color: #282828 !important;
}
.htbox-content {
    background-color: darkgrey;
}

.ht-tabs-last-row, .MatchTabs {
    background-color:lightgrey;
}

/* MAIN PAGE BORDERS */
.boxBody,
.main .boxBody {
    border-bottom: 0px solid darkgreen !important;
    border-left: 0px solid darkgreen!important;
    border-right: 0px solid darkgreen !important;
}

.pmContainer {
    background-color: darkgrey;
}
.pmSpeech {
    background-color: lightgrey;
}

/* FORUM */
.forumFolder,
.folderHeader {
    background-color: #063000;
}
.subMenuBox .threadItem.even {
    background-color: darkgrey;
}

.subMenuBox .threadItem.odd {
    background-color: grey;
}

.subMenuBox .threadItem{
    color: white;
}

.subMenuBox .postCountLink {
    color: black !important;
}

.frontPageList .folderitem.even, .frontPageList .folderitem.even .date.nowrap {
    background-color: grey !important;
    color:black;
}
.frontPageList .folderitem.odd, .frontPageList .folderitem.odd .date.nowrap {
    background-color: darkgrey !important;
    color:black;
}

.HTMLToolbarBodyTxtBox{
    background-color:lightgrey;
}
/*Tables*/
.indent .even{
    background-color:lightgrey;
}
.indent .odd{
    background-color:darkgrey;
}

}
