    /* color-scheme */
    --superLightGray: #9e9e9e;
    --theme-color-gray-500: rgb(85, 85, 85);
    --theme-color-gray-700: #222222;
    --theme-color-gray-800: #1c1c1c;
    --theme-color-gray-900: #161616;
    --theme-color-gray-toned: #1d2326;
    --theme-color-primary: /*[[mainColor]]*/ ;
    --theme-font-primary: monospace ;

    --theme-color-white-700: #d5d5d5;
    --theme-color-neumorph-1: #0b0b0b;
    --theme-color-neumorph-2: #2d2d2d;
    --neumorph-bg: linear-gradient(
      145deg,
      var(--theme-color-gray-toned),
      var(--theme-color-gray-700)
    ) !important;
    --neumorph-bg-inverted: linear-gradient(
      325deg,
      var(--theme-color-gray-toned),
      var(--theme-color-gray-700)
    ) !important;
    --neumorph-bs: -3px -3px 10px var(--theme-color-neumorph-1),
      3px 3px 10px var(--theme-color-neumorph-2) !important;
    --neumorph-bs-focused: -3px -3px 10px var(--theme-color-neumorph-1),
      3px 3px 10px var(--theme-color-neumorph-2),
      inset 0px 0px 2px 2px var(--theme-color-primary) !important;
    /* --alertMessage: "IMPORTANT: whatsapp released a new version of whatsapp web on 19.11.2020 breaking all the custom skins out there. I will release fixes in the next days to make it work again. Keep manually updating!!!"; */
    --alertMessage: "IMPORTANT: I fixed an important issue where the color customisation wouldn't work. Please UNISTALL the skin, then go to the skin URL, select a color of your choice and REINSTALL the skin! ";
