style.css:
@-moz-document domain(rnz.co.nz) {
img {
  filter: brightness(75%);
}

.c-primary-navigation .menu-item>a {
    color: antiquewhite;
    display: block;
    height: 100%;
    min-height: 60px;
    padding: 1.5em .75em 0;
    font-weight: 500;
}
.faux-link {
	color: antiquewhite !important;
}
#admin-bar {
    font-size: 15px;
    height: 36px;
    background-color: antiquewhite;
    color: #fff
}
#admin-bar a {
    display: block;
    padding: 0 0.75em;
    color: #fff
}
.c-navigation-tools .icon .icon__badge, .icon--red .icon__badge {
    color: #fff;
    background-color: #ed615a;
    -webkit-transition: background-color 0.15s ease;
    -moz-transition: background-color 0.15s ease;
    -o-transition: background-color 0.15s ease;
    transition: background-color 0.15s ease
}
.bullet-point:before.pod {
    background-color: #fff
}

.count {
    display: block;
    font-size: 15px;
    font-weight: 500;
    letter-spacing: -0.01563em;
    text-align: center;
    width: 30px;
    height: 30px;
    line-height: 30px;
    position: absolute;
    top: -15px;
    right: -15px;
    background-color: #ed615a;
    color: #fff;
    -webkit-border-radius: 15px;
    -moz-border-radius: 15px;
    -ms-border-radius: 15px;
    border-radius: 15px
}

input[type="submit"] {
    background-color: #2082aa;
    color: #fff;
    border: 1px solid #2082aa;
    font-weight: 500;
    font-size: 13px;
    text-transform: uppercase;
    padding: 0 .825em;
    vertical-align: middle;
    line-height: 34px
}
.btn, .more-button {
    display: inline-block;
    padding: .5em .625em .375em;
    font-weight: 500;
    color: #fff;
    background-color: #ed615a;
    line-height: 1.4;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    border-radius: 3px;
    font-size: 100%
}
.btn:hover, .btn:focus, .btn.focus, .btn:active, .btn.active, .open > .btn.dropdown-toggle, .more-button:hover, .more-button:focus, .more-button.focus, .more-button:active, .more-button.active, .open > .more-button.dropdown-toggle {
    color: #fff;
    background-color: #e8352c
}
.btn--opaque {
    color: #fff;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background-color: rgba(255, 255, 255, 0.3)
}

.u-blocklink .faux-link:visited {
    color: #d2ae41;
}
.u-blocklink .faux-link:hover {
    color: #d2ae41 !important;
}

h3, h4, h5, th {
    font-weight: 500;
    color: antiquewhite
}

.article__body p a:visited, .episode-body p a:visited, .page__body p a:visited, .article__body p .link:visited, .episode-body p .link:visited, .page__body p .link:visited {
    color: antiquewhite
}

a:focus, a:active, .link:focus, .link:active {
    color: antiquewhite
}
.link-reversed:visited {
    color: antiquewhite
}
h4 > .link-reversed {
    color: antiquewhite
}

h4 > .link-reversed--not-visited {
    color: antiquewhite
}
.o-digest__headline a:visited {
    color: antiquewhite
}
.o-tile__title a:visited {
    color: antiquewhite
}
.deprecated .o-feature-set--station.nat .u-blocklink .faux-link:not(:visited) {
    color: antiquewhite
}
.deprecated .o-feature-set--station.con .u-blocklink .faux-link:not(:visited) {
    color: antiquewhite
}
.deprecated .o-feature-set--station.int .u-blocklink .faux-link:not(:visited) {
    color: antiquewhite
}
html {
    font-family: sans-serif;
    -ms-text-size-adjust: 100%;
    -webkit-text-size-adjust: 100%;
    background-color: #23232d;
}
body {
    margin: 0;
    background-color: #23232d;
}
body {
    font-family:"Colfax", Arial, Sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    background-color: #23232d;
    color: antiquewhite;
}
.article__body p a::selection, .article__body p a > *::selection, .episode-body p a::selection, .episode-body p a > *::selection, .page__body p a::selection, .page__body p a > *::selection, .article__body p .link::selection, .article__body p .link > *::selection, .episode-body p .link::selection, .episode-body p .link > *::selection, .page__body p .link::selection, .page__body p .link > *::selection {
    background-color: #23232d;
    color: #d24141;
    text-shadow: none
}
.article__body p a::-moz-selection, .article__body p a > *::-moz-selection, .episode-body p a::-moz-selection, .episode-body p a > *::-moz-selection, .page__body p a::-moz-selection, .page__body p a > *::-moz-selection, .article__body p .link::-moz-selection, .article__body p .link > *::-moz-selection, .episode-body p .link::-moz-selection, .episode-body p .link > *::-moz-selection, .page__body p .link::-moz-selection, .page__body p .link > *::-moz-selection {
    background-color: #23232d;
    color: #d24141;
    text-shadow: none
}
textarea, input[type="text"], input[type="password"], input[type="file"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"] {
    min-width: 3em;
    width: 100%;
    background-color: #23232e;
    border: 1px solid #ddd;
    -webkit-transition: none;
    -o-transition: none;
    transition: none;
    font-size: 15px;
    line-height: 1.2;
    padding: 0.6em;
    -webkit-box-shadow: none;
    box-shadow: none;
    height: 36px
}
.c-tabs__menu > li.active > a, .c-tabs__menu > li.active > a:hover, .c-tabs__menu > li.active > a:focus {
    color: #d2ae41;
    background-color: #23232e;
    border: 1px solid #ebeae6;
    border-bottom-color: transparent;
    cursor: default
}
.btn--disabled {
    background-color: #23232d;
    pointer-events: none;
    cursor: default;
    font-weight: 400;
    opacity: .5
}
.btn--station-pod, .btn--station-pod:visited {
    background-color: #23232e;
}
.c-top-stories:before {
    content:'';
    display: block;
    height: 100%;
    max-height: 50em;
    max-height: 110vh;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    z-index: -1;
    background: #23232d;
    ;
}
.o-dropdown__footer {
    padding: 1em 0.75em;
    border-top: 1px solid #ebeae6;
    background: #23232d;
    box-sizing: border-box;
    width: 100%;
    display: table
}
.toggled-on .o-dropdown {
    box-sizing: border-box;
    display: block;
    float: none;
    position: absolute;
    z-index: 10;
    max-width: 100%;
    top: 100%;
    right: 0;
    background-color: #23232e;
    border: 1px solid #f0f0ef;
    border-color: rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -ms-border-radius: 4px;
    border-radius: 4px;
    overflow: hidden
}
.o-drawer-menu {
    position: fixed;
    top: 0;
    width: 300px;
    height: 100%;
    background-color: #23232e;
    -webkit-box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    overflow: hidden;
    z-index: 90;
    -ms-transform: translate3d(0, 0, 0);
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
    -webkit-transition: all 0.35s cubic-bezier(0.075, 0.82, 0.165, 1);
    -moz-transition: all 0.35s cubic-bezier(0.075, 0.82, 0.165, 1);
    -o-transition: all 0.35s cubic-bezier(0.075, 0.82, 0.165, 1);
    transition: all 0.35s cubic-bezier(0.075, 0.82, 0.165, 1)
}
.o-drawer-menu__header {
    top: 0;
    padding: 0 0.75em;
    height: 54px;
    background-color: #23232e;
    border-bottom: 1px solid #e0ded8;
    -webkit-box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
    display: table
}
.o-drawer-menu__footer {
    bottom: 0;
    height: 54px;
    border-top: 1px solid #ebeae6;
    background: #23232d;
    box-sizing: border-box;
    display: table;
    line-height: 54px
}
.o-podcast-subscribe .o-podcast-subscribe__list {
    position: relative;
    background-color: #23232e;
    margin: 0;
    max-width: none;
    min-width: 180px;
    overflow: visible;
    position: absolute;
    right: auto;
    top: 133%;
    width: auto;
    z-index: 5
}
.c-sub-nav--inline {
    position: relative;
    text-align: center;
    background-color: #23232e;
    margin-right: -4vw;
    margin-left: -4vw;
    border-bottom: 1px solid #ebeae6;
    white-space: nowrap
}
.c-page-header, .c-banner {
    background-color: #23232d;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-align: center
}
.storytime-banner-button {
    display: inline;
    padding: .5em 1em .275em;
    font-weight: 500;
    font-size: 16px;
    color: black;
    background-color: #23232d;
    border-radius: 3px;
    box-shadow: 0px 2px 4px 0px rgba(0, 0, 0, 0.5);
    cursor: pointer
}
.article__body .c-play-controller--full-width, .episode-body .c-play-controller--full-width, .page__body .c-play-controller--full-width {
    padding: 1.25em 1em 1em;
    background-color: #23232d;
    margin-top: 1em;
    margin-bottom: 1em
}
.c-quick-six .o-digest .o-play__controller {
    display: inline-block !important;
    background-color: #23232d;
    padding: 0.375em;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    border-radius: 3px
}
.c-stations {
    margin-bottom: 0;
    background-color: #23232d;
    text-align: center
}
.c-navigation-tools__link:hover .icon__badge, .c-navigation-tools__link:active .icon__badge {
    background-color: #23232e;
    color: #ed615a;
    border-color: #ed615a
}
.nc_list {
    display: block;
    max-width: 100%;
    padding: .5em .75em;
    background-color: #23232e;
    border: 1px solid #f0f0ef;
    border-color: rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    -ms-border-radius: 2px;
    border-radius: 2px
}
.u-boxed {
    background-color: #23232d;
    padding: 1.125em
}
a:hover, .link:hover {
    color: #d2ae41 !important;
}
.u-blocklink:hover .link--not-visited:visited, .u-blocklink:hover a {
    color: #d2ae41;
}
.article__body p a, .episode-body p a, .page__body p a, .article__body p .link, .episode-body p .link, .page__body p .link {
    text-decoration: none;
    text-decoration: underline\9;
    background-image: linear-gradient(to bottom, rgba(64,68,65,0) 50%, rgba(64,68,65,0.8) 50%);
    background-repeat: repeat-x;
    background-size: 2px 0.1em;
    background-position: 0 1.125em;
    text-shadow: none !important;
}
}
