style.css:
@-moz-document domain(id.sdbb.ch)
{
*
  {
    --main-color: #F2F2F2;
    --main-bg-color: #181A1B;
    --second-bg-color: #3F3F3F;
    scrollbar-color: #3F3F3F #1C1E1F !important;
  }

 body, #kc-login, .btn.btn-primary.btn-block.btn-lg, .alert
  {
    background-color: var(--main-bg-color) !important;
  }

 .card-pf, .form-control
  {
    background-color: var(--second-bg-color) !important;
    border-color: black !important;                        
  }


 h1, h2, h3, h4, h5, p, .control-label, .form-control, #kc-current-locale-link, #kc-page-title, .alert
  {
    color: var(--main-color) !important;                   
  }
}

@-moz-document domain(lsr.sdbb.ch)
{
*
  {
    --main-color: #F2F2F2;
    --main-bg-color: #181A1B;
    --second-bg-color: #3F3F3F;
    scrollbar-color: #3F3F3F #1C1E1F !important;
  }

 body, .parentMenu.active, #OrganizationInputBox, .k-item, .k-list-container, .k-multiselect-wrap.k-floatwrap, .k-widget, #exportLink, #header-bottom, .parentMenu.active, .k-alt, tr, tbody tr th, .k-calendar-view, #PublishedFrom_dateview, #PublishedTo_dateview, .dropdown-menu.dropdown-menu-right.show
  {
    background-color: var(--main-bg-color) !important;
  }

 .footer, .k-widget.k-dropdown.select.form-control, .k-in:hover, .k-window-titlebar.k-dialog-titlebar.k-header, .box
  {
    background-color: var(--second-bg-color) !important;
  }
 
 .section.section-bg, .k-textbox, .k-numeric-wrap.k-state-default, .k-numeric-wrap.k-state-default:hover, .k-input.w-100.k-valid, #ApprenticeshipUnpublishedComment, .k-input.w-100, #PublicationRemark, #PublishedFrom, #PublishedTo
  {
    background-color: var(--main-bg-color) !important;
    border-color: var(--second-bg-color) !important;
    color: var(--main-color) !important;
  }

 #header-bottom, .k-window-titlebar.k-dialog-titlebar.k-header
  {
    border-color: var(--main-bg-color) !important;
  }

 h1, h2, h3, h4, h5, p, .k-item, .k-nodata, .custom-control-label,  #resetbutton, .logo-claim, #dropdownLanguageLink, .parentMenu span, .k-widget.k-dropdown.select.form-control span, #OrganizationInputBox, .k-multiselect-wrap.k-floatwrap, .k-widget, .k-in:hover, .breadcrumb-item, .k-button, .nav.nav-meta span, .fas.fa-sign-out-alt, #exportLink, tr, .fas.fa-ellipsis-h, .k-window-titlebar.k-dialog-titlebar.k-header, .fas.fa-plus-circle, .list-icon.m-0 span, .list-icon span, label, .headline.mb-4.d-flex.flex-justify-space-between.flex-align-baseline, tbody tr th, .lead, .breadcrumb a, .col.col-12, #PublicationStatusText, #PublicationStatusReason, .form-group-flex.flex-align-baseline, .text-body, .fas.fa-pen, .dropdown-menu.dropdown-menu-right.show, .small, .col.col-6, .k-icon.k-clear-value.k-i-close, .form-group-flex
  {
    color: var(--main-color) !important;                   
  }

 .parentMenu:hover span
  {
    color: var(--main-bg-color) !important;                   
  }

 .sdbb-link
  {
    color: #a4a4fb !important;                   
  }

 .footer
  {
    display: none;
  }
}
