style.css:
@-moz-document regexp("https://catwar.su/(?!(cw3|p|cwmod|microbe|design/20)).*") {
body {
	font-family: Arial, Helvetica, sans-serif;
	font-size: 12px;

	background-image: none;
	background-color: #000;
	color: #fff;
}

a, a:link, a:visited {
	color: #f90;
	outline-style: none;
	text-decoration: none;
}

#cwm-show-settings {
	color: #f90 !important;
}

a:hover {
	text-decoration: underline;
}

a.pol0 {
	color: #561832;
}

a.pol1 {
	color: #0f2189;
}

#error {
	z-index: 1;
	background-color: #f90;
	color: #000;
	font-size: 11pt;
}

body > span, #logo, #menu_div, #site_table, #footer {
	padding: 0 10% !important;
	width: 80% !important;
}

#branch {
	background: none;
	padding: 0;
	margin: 0;
}

#site_table {
	width: auto;
	background-color: #000;
	border-radius: 0;
}

#footer {
	padding-top: 5px !important;
	padding-bottom: 5px !important;

	background-color: #151515;
	border-top: 1px solid #292929;
	border-radius: 0;
}

body > span {
	height: 22px;
	color: #000;
	background: #000 !important;
}

body > span > a {
	color: #bbb !important;

	text-transform: uppercase;
	padding: 5px 20px;

	font-size: 11px;
	text-decoration: none;
	font-weight: normal;

	box-sizing: border-box;
}

body > span > a:hover {
	text-decoration: none;

	color: #fff !important;
	background: #333;
}

#logo {
	display: inline-block;

	height: 50px !important;
	padding-top: 10px !important;

	font-size: 36px;
	font-weight: bold;

	background-image: none !important;
	background-color: #1b1b1b;
	border-top: 1px solid #555;
}

#logo:before {
	content: 'Cat';
	padding: 0 3px;
	padding-left: 50px;
}

#logo:after {
	content: 'War';
	padding: 0 3px;
	color: #000;
	background-color: #f90;
	border-radius: 5px;
}

#menu_div {
	display: flex;
	justify-content: space-around;

	margin-top: 0;
	height: 45px;

	font-size: 11px;

	border-top: 1px solid #242424;
	background: #1b1b1b;
}

#menu_div > a {
	padding: 10.5px 0 0 0;
	border: solid transparent;
	border-width: 2px 1px;
	box-sizing: border-box;
	flex-grow: 1;
}

#menu_div > a:hover {
	text-decoration: none !important;
	border-color: #242424;
	background-color: #191919;
}

#emblem, [class^=kn] {
	display: inline-block;

	width: 100% !important;
	height: auto !important;

	font-size: 11px;
	font-weight: 700;
	text-transform: uppercase;

	color: #fff;
	background-image: none !important;
}

#emblem:hover, [class^=kn]:hover {
	background-image: none !important;
}

div.page_scroll {
	width: 30px !important;
	height: 30px !important;
}

div#page_down {
	background: none !important;
}

div#page_up {
	background: none !important;
}

#page_down:before {
	content: '↓';
	font-size: 25px;
	background-color: var(--main-color);
	border-radius: 1em;
}

#page_up:before {
	content: '↑';
	font-size: 25px;
	background-color: var(--main-color);
	border-radius: 1em;
	-moz-border-radius: 1em;
	-webkit-border-radius: 1em;
	-ms-border-radius: 1em;
	-o-border-radius: 1em;
}

#emblem:before {
	content: 'Игровая';
}

.kn1:before {
	content: 'Мой кот'
}

.kn2:before {
	content: 'Моя кошка'
}

.kn3:before {
	content: 'Лента'
}

.kn4:before {
	content: 'Список игроков'
}

.kn5:before {
	content: 'Вход'
}

.kn6:before {
	content: 'Личные сообщения'
}

.kn7:before {
	content: 'Об игре'
}

.kn8:before {
	content: 'Блоги'
}

.kn9:before {
	content: 'Выход'
}

.kn10:before {
	content: 'Чат'
}

.kn11:before {
	content: 'Модерация'
}

.kn12:before {
	content: 'Забыл пароль'
}

.kn13:before {
	content: 'Регистрация'
}

#branch:after {
	content: 'Самый большой в мире Кот война сайт';
	display: block;
	padding: 30px 0 5px;
	text-align: center;
	font-weight: 700;
	font-size: 1.17em;
	color: #666;
}

#site_table:after {
	content: 'Команда CatWar каждый день обновляет и добавляет котов-воителей. Вся игра в котов-воителей здесь 100% бесплатна. Вы можете загрузить или смотреть онлайн нашу поистине огромную коллекцию окрасов. CatWar – это самая большая и революционная игра про котов-воителей. Мы предлагаем поиграть в русских котов воителей онлайн, популярные игры с воителями и оруженосцами,а также, загрузку ваших блогов и артов. Общайтесь и знакомтесь бесплатно в ленте номер 1 на КэтВар. Мы постоянно работаем над добавлением новых функций, которые поддержат и удовлетворят Вашу любовь к котам-воителям. Напишите нам, если у вас есть вопросы или комментарии.';
	display: block;
	padding: 5px 0 0;
	color: #666;
	text-align: center;
	line-height: 18px;
}

font[color="#333333"] {
	color: #aaa;
}

font[color="#561832"] {
	color: #c85353;
}

font[color="darkgreen"], font[color="#006400"] {
	color: #1aaa1a;
}

font[color="#333366"] {
	color: #8888bb;
}

#site_table > *:first-child:not(#branch):not(#error)::before,
#site_table > #error:first-child + *:not([style="display: none;"])::before,
#site_table > #error:first-child + [style="display: none;"] + *::before {
	content: 'Вы используете мобильную версию сайта. Многие элементы стиля отображаются неверно.';
	display: block;
	padding: 2px;
	margin: 2px;
	font-size: 14px;
	font-weight: bold;
	text-align: center;
	color: darkred;
	background: #ddd;
}
}

@-moz-document url-prefix("https://catwar.su/blogs"), regexp("https://catwar.su/sniff(?!\\d).*"), regexp("https://catwar.su/idea(?!\\d).*") {
/*** Список блогов ***/
/** Список **/
.blogsList {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	grid-row-gap: 15px;
	grid-column-gap: 10px;
}

.blog {
	display: grid;

	grid-template-areas: 'ltext' 'ltitle' 'linfo' 'ltags';
	overflow-x: hidden;
	overflow-y: hidden;
}

.blog-read, .line {
	display: none;
}

.blog-title {
	grid-area: ltitle;
	font-size: 14px;
	font-weight: 400;
	overflow: hidden;
	line-height: 17px;
	max-height: 34px;
}

.blog-info {
	grid-area: linfo;
	font-size: 12px;
	color: #cacaca;
}

.blog-rating {
	color: #fff;
}

.blog-tags {
	grid-area: ltags;
	font-size: 12px;
	color: #cacaca;
}

.blog-info a, .blog-tags a {
	color: #cacaca !important;
}

.blog .blog-text {
	grid-area: ltext;

	height: 160px;
	padding: 10px;

	font-size: 14px;

	color: #b8b8b8;
	background-color: #111;

	overflow-x: hidden;
	overflow-y: hidden;
}


#page_form {
	height: 54px;
	line-height: 54px;
}

#prev_page_span, #next_page_span {
	color: transparent;
	user-select: none;
}

#next_page_span {
	float: right;
}

#prev_page, #next_page {
	display: inline-block;
	padding: 0 25px;
	height: 48px;

	font-size: 6px !important;
	line-height: 48px;

	color: transparent;
	background-color: #000;
	border: 3px solid #f90;
	border-radius: 5px;

	user-select: none;
}

#prev_page::before {
	content: url(https://ai.phncdn.com/www-static/images/leftArrow.png);
}

#next_page::after {
	content: url(https://ai.phncdn.com/www-static/images/rightArrow.png);
}

#prev_page::before, #next_page::after {
	position: relative;
	top: 3px;
}

#prev_page::after, #next_page::before {
	color: #f90;
	font-weight: bold;
	font-size: 16px;
}

#prev_page::after {
	content: 'Назад';
}

#next_page::before {
	content: 'Далее';
}
}

@-moz-document regexp("https://catwar.su/blog\\d+"), regexp("https://catwar.su/sniff\\d+"), regexp("https://catwar.su/idea\\d+") {
/*** Страница блога ***/
.view {
	position: relative;

	display: grid;
	grid-template-areas: 'btext' 'btitle' 'binfo';
	margin-bottom: 20px;
	padding: 0;

	background: #101010;
	border: 1px solid #313131;
	border-radius: 4px;
}

.line, span.comment-delete {
	display: none !important;
}

.view-title {
	grid-area: btitle;
	padding: 5px 10px;

	background: #1b1b1b;
	border-bottom: 1px solid #070707;
}

.view-info {
	grid-area: binfo;
	padding: 5px 10px;
}

.blog-text {
	grid-area: btext;
	max-height: 400px;
	overflow-y: auto;
	
	font-size: 14px;

	padding: 10px;
	color: #b8b8b8;
}

#view-tags {
	display: block;
}

.comment {
	border-radius: 4px;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	-ms-border-radius: 4px;
	-o-border-radius: 4px;
	border: 1px solid #313131;
	background-color: #101010;
	margin: 0 0 15px;
}

.comment-info > b {
	float: right;
	margin-left: 5px;
}

.comment-info:before {
	background: url(https://ci.phncdn.com/www-static/images/profile/redesign-stream-ui.png) -4px -4px no-repeat;
	content: '';
	width: 10px;
	height: 10px;
	display: inline-block;
	margin-right: 3px;
}

.comment-info > b + * {
	font-weight: bold;
	font-size: 14px;
	color: #f90;
	float: left;
	margin-right: 5px;
}

.view-comments {
	margin-bottom: 0;
	padding: .5em;
	color: #fff;
	background: #1b1b1b;
	font-size: 1.3em;
	font-weight: normal;
}

#commentsCount {
	color: #777;
}

#view_comments {
	background-color: #101010;
	border: 1px solid #1d1d1d;
	padding: 0 0 1.75em 0;
}

#closed_comments, #closed_comments_for_guest {
	position: relative;
	bottom: 1.3em;
	left: 0.25em;
}

.view-comment {
	display: grid;
	grid-template-areas: 'cava cinfo' 'cava cp' 'cava ctext';
	grid-template-columns: 75px auto;
	border: none;
	border-radius: 0;
}

.comment-info {
	grid-area: cinfo;
	color: #505050;
}

.view-comment > p:nth-child(2) {
	grid-area: cp;
}

.comment-text {
	grid-area: ctext;
	color: #b8b8b8;
}

.view-comment:before {
	grid-area: cava;
	content: '';
	height: 60px;
	width: 60px;
	background: no-repeat center/contain url(https://i.ibb.co/D9kzVs7/m-e8ap-G0y-mh-t-I1-Df-BCLHUL3-V7x-I-male.png);
	border-radius: 0;
}

a.comment-delete {
	margin-left: 5px;
}

a.comment-delete[data-candelete="0"] {
	content: url(https://i.ibb.co/QfKxMLw/image.png);
}

a.comment-delete[data-candelete="1"] {
	content: url(https://i.ibb.co/nkG4sQ9/image.png);
}

.poll-hasAnswered1 {
	color: black;
}

/** Инфа о посте **/
#view-rating {
	font-size: 18px;
	font-weight: bold;
}

img[src="img/rating_up.png"], img[src="img/rating_down.png"], img[src="/img/onlyText.png"], img[src="/img/star.png"] {
	width: 0px;
	height: 18px;
	padding-left: 18px;
	background-repeat: no-repeat;
}

.vote[data-vote="up"] {
	color: #848484;
	font-size: 10pt;
	padding-top: 2px;
	padding-left: 4px;
}

.vote[data-vote="up"]::after {
	content: 'круто';
}

.vote[data-vote="up"]:hover {
	color: #fff;
	text-decoration: none;
}

/* Иконки */
img[src="img/rating_up.png"], img[src="img/rating_down.png"], img[src="/img/star.png"] {
	background-image: url(https://ci.phncdn.com/www-static/images/sprite-pornhub-nf-2.png);
}

img[src="img/rating_up.png"] {
	background-position: 0 0;
}

.vote[data-vote="up"]:hover > img[src="img/rating_up.png"] {
	background-position: 0 -18px;
}

img[src="img/rating_down.png"] {
	background-position: -18px 0;
}

img[src="img/rating_down.png"]:hover {
	background-position: -18px -18px;
}

img[src="/img/star.png"] {
	background-position: -36px 0;
}

img[src="/img/star.png"]:hover {
	background-position: -36px -18px;
}

img[src="/img/onlyText.png"] {
	background-image: url(https://i.ibb.co/MSFfX4g/onlyText.png);
}

img[src="/img/onlyText.png"]:hover {
	background-image: url(https://i.ibb.co/FmQ5NbV/only-Tex2t.png);
}
}

