@-moz-document url("https://catwar.su/top") {
/*** Список игроков ***/
.kn4:after {
	content: '';
	width: 100%;
	height: 2px;
	margin: 0 auto;
	background: #f90;
	display: inline-block;
}

#top_div {
	width: 100%;
	background: none;
}

#top_div::before {
	content: 'Самые Трендовые Коты-Воители';
	color: #fff;
	font-size: 20px;
	font-weight: 700;
}

#top_div table {
	width: 100%;
	border: none;
}

#top_div td {
	float: right;
	border: none;
	color: #999;
}

#top_header > td > a {
	display: inline-block;
	padding: 0 23px;
	height: 25px;
	line-height: 25px;
	font-weight: 700;
	font-size: 14px;
	color: #7d7c7c;
	border: 1px solid transparent;
}

#top_header > td > a:hover {
	text-decoration: none;
	color: #cacaca;
	border: 1px solid #363636;
}

#top_header > td > a::after {
	content: '';
	width: 0;
	height: 0;
	font-size: 0;
	border-left: 5px solid transparent;
	border-right: 5px solid transparent;
	border-top: 5px solid #7d7c7c;
	position: relative;
	top: -1px;
	margin-left: 5px;
}

#top_header > td > a:hover::after {
	border-top: 5px solid #cacaca;
}

a.pol0, a.pol1 {
	color: #f90;
}

#top > tbody {
	display: grid;
	grid-template-columns: repeat(auto-fill, 132px);
	grid-column-gap: 1.313em;
}

#top > tbody > tr {
	display: inline-grid;
	grid-template-rows: 132px repeat(4, auto);
	width: 132px;
}

#top > tbody > tr:before {
	content: '';
	width: 132px;
	height: 132px;
	background: no-repeat center / contain url(https://d.zaix.ru/fs6S.png);
}

@media (max-width: 1248px) {
#top > tbody {
	grid-template-columns: repeat(auto-fill, 200px);
}

#top > tbody > tr {
	grid-template-rows: 200px repeat(4, auto);
	width: 200px;
}

#top > tbody > tr:before {
	width: 200px;
	height: 200px;
}


}

#cat_form > p {
	height: 26px;
	width: 462px;
	white-space: nowrap;
	background-color: #f90;
	border-radius: 4px 3px 3px 4px;
}

#cat {
	height: 22px;
	width: 400px;
	padding: 2px 5px;

	font-size: 14px;

	color: #cacaca;
	background-color: #363636;
	border: none;
	border-radius: 3px 0 0 3px;
}

#cat::placeholder {
	font-style: italic;
}

#cat_form > p > input[type="submit"] {
	width: 16px;
	height: 16px;
	margin-left: 16px;
	color: transparent;
	background: url(https://ci.phncdn.com/www-static/images/sprite-pornhub-nf.png) no-repeat;
	border: none;
	cursor: pointer;
}
}

@-moz-document url-prefix("https://catwar.su/chat") {
/*** Чат ***/
.kn10:after {
	content: '';
	width: 100%;
	height: 2px;
	margin: 0 auto;
	background: #f90;
	display: inline-block;
}

#main_table, .mess_tr > td, .chat_table {
	border: none !important;
}

#branch > p:first-child, #addToGroup_form, #typing, #confirm {
	padding-top: 8px;
	margin: 0;
	background: #1b1b1b;
	color: #fff;
}

/** Расположение элементов **/
#branch {
	display: grid;
	grid-template-areas: 'a1 a1' 'a2 a2' 'a3 a3' 'a4 a4' 'a5 a6' 'a5 a7' 'a5 a8' 'a9 a9' 'a10 a10' 'a11 a11' 'a12 a12' 'a13 a13' 'a14 a14' 'a15 a15' 'a16 a16';
	grid-template-columns: 20% 80%;
	grid-template-rows: repeat(4, auto) 400px 50px 150px repeat(8, auto);
}

#branch > p:first-child {
	grid-area: a1;
}

#addToGroup_form {
	grid-area: a2;
}

#typing {
	grid-area: a3;
}

#confirm {
	grid-area: a4;
}

#tabs {
	grid-area: a5;
}

#main_table {
	grid-area: a6;
}

#stickers_above {
	grid-area: a7;
}

#mess_form {
	grid-area: a8;
}

#tools {
	grid-area: a9;
}

#settings {
	grid-area: a10;
}

#openPrivateWith_form {
	grid-area: a11;
}

#createGroup_form {
	grid-area: a12;
}

#groupList {
	grid-area: a13;
}

#search_form {
	grid-area: a14;
}

#renameGroup_form {
	grid-area: a15;
}

#branch::after {
	grid-area: a16;
}

/** Вкладки **/
#tabs {
	margin: 0;
	height: 600px;
	background: #101010;
	border-bottom: 1px solid #000;
	overflow-y: auto;
}

#tabs li {
	display: block;
	padding: 12px 0 12px;

	background: none !important;

	border: none;
	border-bottom: 1px solid #1d1d1d;
	border-radius: 0;
	cursor: pointer;
}

#tabs li .tabName {
	color: #f90;
}

#tabs li#active {
	background: #f90 !important;
}

#tabs li.new:before {
	content: '!!! ';
	color: red;
	font-size: 1.25em;
}

#tabs li#active .tabName {
	color: black;
}

/** Список сообщений **/
.mess_tr {
	display: grid;
	grid-template-areas: 'avatar author time del' 'avatar mess time del';
	grid-template-columns: 90px auto 75px 10px;
	padding: 12px 0;
}

.mess_tr::before {
	grid-area: avatar;
	content: '';
	margin-left: 16px;
	width: 64px;
	height: 64px;
	background: no-repeat center/contain url(https://i.ibb.co/D9kzVs7/m-e8ap-G0y-mh-t-I1-Df-BCLHUL3-V7x-I-male.png);
}

.author_td {
	grid-area: author;
	width: auto;
}

.author_td > a {
	font-weight: bold;
}

.author_td > br {
	display: none;
}

.info::before {
	content: ' ';
}

.mess_td {
	grid-area: mess;
}

.mess_div {
	width: auto !important;
	overflow-x: auto;
}

.mess_div img {
	max-width: 100%;
}

.time_td {
	grid-area: time;
	font-weight: bold;
}

.del_td {
	grid-area: del;
}

/** Отправка сообщения **/
#stickers_above, #mess_form {
	background-color: #1b1b1b;
}

#mess {
	margin: 0;
	max-width: unset !important;
	background: white;
	color: rgba(67, 59, 54, .9);
	border: none;
	border-bottom: 1px solid #ccc;
}

#mess_submit {
	float: right;
	margin-top: 10px;
	margin-right: 10px;
	padding: 6px 15px;

	font: 400 13.3333px Arial;
	font-weight: 700;

	color: #000;
	background: #f90;
	border: none;
	border-radius: 5px;
	cursor: pointer;
}

#mess + p {
	margin: 0;
	width: calc(98% + 10px);
	background: white;
	color: #000;
}

#mess + p > small > a {
	color: #626262;
}
}

