style.css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://wallpaperaccess.com/full/173782.jpg");
  }

  html
  {
    background: fixed url("https://wallpaperaccess.com/full/806443.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 120%;
  }

  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #1f2021 100%);
  }

  .card--a
  {
    background-image: url("https://wallpaperplay.com/walls/full/d/b/e/9023.jpg");
    color: #fefeff;
    background-size: 100%;
  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #f78e0a;
  }

  .theme--pDefault .profile-title
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-body
  {
    color: #000000;
  }
}

@-moz-document domain(nitrotype.com)
{

  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #696969;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .bg--gradient
  {
    background: url("http://www.baltana.com/files/wallpapers-16/4K-Black-Background-Design-Desktop-Wallpaper-40660.jpg") no-repeat;
  }

  .theme--pDefault .profile-bg
  {
    background: url("http://www.baltana.com/files/wallpapers-16/4K-Black-Background-Design-Desktop-Wallpaper-40660.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("http://www.baltana.com/files/wallpapers-16/4K-Black-Background-Design-Desktop-Wallpaper-40660.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("http://www.baltana.com/files/wallpapers-16/4K-Black-Background-Design-Desktop-Wallpaper-40660.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
