style.css:
/* i really want this to be global */

html, body {
    display: block;
    margin: 0;
    background-color: #ffffff;
    padding: 0;
    color: #000000;
}

#navigation {
    display: block;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 10;
    border-bottom: 1px solid rgb(255, 255, 255);
    box-shadow: 0 0 3px rgb(255, 255, 255);
    background-color: #7171717a;
    width: 100%;
    height: 50px;
}

.account-nav .user-info {
    display: inline-block;
    padding: 14px 15px 4px 15px;
    max-width: 260px;
    height: 33px;
    overflow: hidden;
    text-decoration: none;
    text-overflow: ellipsis;
    white-space: nowrap;
    color: black;
    font-size: .8125rem;
    font-weight: normal;
}

#navigation .link > a {
    display: block;
    padding: 13px 15px 4px 15px;
    height: 33px;
    text-decoration: none;
    white-space: nowrap;
    color: black;
    font-size: .85rem;
    font-weight: bold;
}

#view {
    display: inline-block;
    margin-top: 50px;
    background-color: #ffffff;
    padding: 20px 0;
    min-width: 100%;
    min-height: 768px;
}

.box .box-header {
    display: block;
    clear: both;
    margin: 0;
    border-top: 1px solid white;
    border-bottom: 1px solid #ffffff;
    border-radius: 10px 10px 0 0;
    background-color: #ffffff;
    padding: 8px 20px;
    height: 20px;
    overflow: hidden;
}

.box {
    display: inline-block;
    border: 1px solid #ffffff;
    border-radius: 10px 10px 0 0;
    background-color: white;
    width: 100%;
}

h1, h2, h3, h4, h5, p {
    line-height: 1.7em;
    color: #000000;
}

style
.box .box-header h4, .box .box-header h5 {
    line-height: 1.1rem;
    font-size: 1.7rem;
}

a:link, a:visited, a:active {
    text-decoration: none;
    color: #444444;
}

.thumbnail .thumbnail-image img {
    margin-bottom: 2px;
    border: 1px solid #ffffff;
    border-radius: 20px;
}

h1, h2, h3, h4 {
    font-weight: 700;
    line-height: 32px;
    color: #000000;
}

h2 {
    font-size: 1.9923em;
    line-height: 1.1818em;
}

h3 {
    font-size: 1.4846em;
    font-weight: 500;
}

.preview .project-lower-container {
    margin-top: 1rem;
    background-color: rgb(255, 255, 255);
    padding: 1rem 0;
    min-height: 6rem;
}

.preview .remix-button {
    background-color: #00bd8869;
}

.preview .remix-button, .preview .see-inside-button {
    margin-top: 0;
    border-radius: .25rem;
    padding-top: .6875rem;
    padding-right: .9375rem;
    padding-bottom: .6875rem;
    padding-left: .875rem;
    height: 2.5rem;
    font-size: .875rem;
    color: #00e285;
}

.subactions .action-buttons .action-button {
    margin: 0 0 0 .5rem;
    border-radius: 19px;
    background-color: #4d97ff6b;
    padding: 0 .75rem;
    height: 2rem;
    text-decoration: none;
    line-height: .875rem;
    font-size: .75rem;
    font-weight: bold;
}

.button {
    display: inline-block;
    margin: .5em 0;
    border: 0;
    border-radius: .5rem;
    background-color: #9ec6ff;
    cursor: pointer;
    padding: 1em 1.25em;
    color: #4d97ff;
    font-size: .8rem;
    font-weight: bold;
}

.stage-header_stage-button_hkl9B {
    display: block;
    border: 1px solid hsl(0, 0%, 100%);
    border-radius: calc(0.5rem / 2);
    width: calc(2rem + 2px);
    height: calc(2rem + 2px);
    background: hsla(0, 100%, 100%, 1);
    padding: 0.375rem;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: pointer;
}

.compose-comment .textarea-row textarea:not(:focus) {
    border: 2px solid rgb(255, 255, 255);
}

.preview .project-description {
    margin-bottom: .75rem;
    border: 1px solid rgb(255, 255, 255);
    border-radius: 20px;
    background-color: #fafafa;
    padding: .5rem;
    width: calc(100% - (1rem + 2px));
    overflow: auto;
    white-space: pre-line;
    font-size: 1rem;
    line-height: 1.5rem;
    -webkit-flex: 1;
    -ms-flex: 1;
    flex: 1;
    overflow-wrap: break-word;
}

.preview img.avatar {
    border: 0;
    border-radius: 30px;
    width: 3rem;
    height: 3rem;
}

.account-nav .user-info .avatar {
    margin-right: 10px;
    border-radius: 20px;
    width: 24px;
    height: 24px;
    vertical-align: middle;
}

.dropdown {
    display: none;
    position: absolute;
    right: 0;
    border: 1px solid rgba(255, 255, 255, 0);
    border-radius: 0 0 20px 20px;
    background-color: #ffffffab;
    padding: 10px;
    min-width: 9rem;
    max-width: 16.25rem;
    overflow: visible;
    color: white;
    font-size: .8125rem;
    font-weight: normal;
}

.dropdown a:link, .dropdown a:visited, .dropdown a:active {
    background-color: transparent;
    color: black;
}

#footer {
    display: block;
    background-color: #ffffff;
    padding: 10px 0;
    color: #000000;
    font-size: .85rem;
}

dl dt {
    font-weight: bold;
    font-size: 20px;
}

.inplace-textarea {
    transition: all .2s ease;
    border: 2px dashed rgba(77, 151, 255, 0.25);
    border-radius: 20px;
    background-color: #fafafa;
    padding: .75rem 1rem;
    width: 100%;
    line-height: 1.75em;
    color: #575e75;
    font-size: 1rem;
    box-sizing: border-box;
    resize: none;
}

.thumbnail-column .thumbnail .thumbnail-image {
    margin: 8px auto;
    border-radius: 4px;
    box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.1);
    background-color: white;
