}

.thumbnail .thumbnail-title {
    margin-bottom: 1px;
    font-size: 1.05em;
    font-weight: 800;
}

.grid .thumbnail.project .thumbnail-image img {
    margin: 0 auto;
    border: 0;
    border-radius: 20px;
