}

.outer .categories li.active {
    opacity: 1;
    background-color: #a1f1da;
    color: #0fbd8c;
}

.outer .categories li {
    border: 0;
    background-color: rgba(171, 171, 171, 0.1);
    color: black;
}

.about .body {
    position: relative;
    margin: 20px 0 0;
    border: 1px solid #ffffff;
    border-radius: 10px;
    background: white;
    padding: 26px 40px 6px;
    width: calc(100% - 80px);
}

.title-banner.mod-messages {
    background-color: #ffffff;
    color: black;
}

.messages-social-list {
    border: 1px solid #d9d9d900;
    border-bottom-width: 0;
    border-radius: 30px;
    background-color: white;
}

.social-message.mod-unread {
    background-color: rgba(0, 161, 255, 0.1);
    border-radius: 30px;
}

.social-message {
    margin: 0;
    border-bottom: 1px solid #ffffff00;
    padding: 1rem;
    list-style-type: none;
}

.select select {
    transition: border .5s ease;
    margin-bottom: .75rem;
    border: 1px solid rgba(0, 0, 0, 0);
    border-radius: 30px;
    background: #f3f3f3 url(data:image/svg+xml;base64,) no-repeat right center;
    padding-right: 4rem;
    padding-left: 1rem;
    width: 100%;
    height: 3rem;
    color: #464646;
    font-size: .875rem;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}

.comment-text {
    position: relative;
    border: 1px solid #d9d9d900;
    border-radius: 30px 30px 30px 30px;
    padding: 1rem;
}

.comment-text:before {
    display: block;
    position: absolute;
    top: -1px;
    left: -13px;
    border-bottom: 12px solid #d9d9d900;
    border-left: 13px solid #d9d9d900;
    /* border-radius: 0 0 0 13px; */
    width: 0;
    content: "";
}

.comment-text:after {
    display: block;
    position: absolute;
    top: 0;
    left: -12px;
    border-bottom: 10px solid #ffffff00;
    border-left: 12px solid #ffffff00;
    border-radius: 0 0 0 12px;
    width: 0;
    content: "";
}

ul.curators li {
    background-color: #f9f9f9;
