style.css:
@-moz-document domain("bbs.hupu.com") {
* {
    font-family: "Microsoft YaHei", 微软雅黑, "MicrosoftJhengHei", 华文细黑, STHeiti, MingLiu;
}

/*隐藏回复人和作者*/
.endreply,
.t2,
.t4,
#hp-topbarNav,
.hp-header,
.hp-threeNav,
.forMyplate,
.bbs_banner {
    display: none !important;
}
body {
    background-color: #eee !important;
}
.for-list .author {
    display: none !important;
}
.ansour {
    float: right;
    text-align: right !important;
    margin-right: 10px !important;
    width: auto !important;
    line-height: 40px !important;
    height: 40px !important;
}
.t3 {
    float: right;
    margin-right: 0 !important;
}
/*帖子标题 begin*/
.titlelink {
    float: left;
    line-height: 40px;
    width: auto !important;
}
.titlelink .truetit {
    font-size: 13px;
    text-decoration: none !important;
    font-weight: 500;
}
.titlelink .light_r {
    display: none !important;
}
.titlelink .multipage {
    background: none !important;
    padding-left: 0 !important;
}
/*帖子标题 end*/
.bbs-content {
    margin-top: 30px !important;
    background-color: transparent !important;
}
.container_padd {
    border: none !important;
    background: transparent !important;
}
#container {
    background-color: transparent !important;
}
.bbsHotPit {
    border: 1px solid #dfdede;
    border-radius: 8px;
    padding: 10px !important;
    background-color: #fff;
}
.bbsHotPit .tabs_header {
    display: none !important;
}
.show-tit {
    background-color: #f0f0f0 !important;
    border-bottom: none !important;
    border-radius: 4px;
}
.bbs-showMyPlate {
    margin-right: -1px;
    background-color: #eee;
    text-align: right !important;
}
.bbs-showMyPlate li {
    height: 42px;
    line-height: 42px !important;
}
.bbs-showMyPlate li .num {
    display: none !important;
}
.bbs-showMyPlate li .showname {
    height: 40px !important;
    line-height: 40px !important;
}
.bbs-showMyPlate li a {
    float: right;
    padding: 0 10px 0 0 !important;
    height: 40px !important;
    text-align: right !important;
    background: none !important;
    font-size: 14px !important;
}
.bbs-showMyPlate li .selected {
    font-weight: 900;
    letter-spacing: 1px;
    background-color: #fff !important;
    border-radius: 6px 0 0 6px;
    width:auto !important;
    padding-left: 12px !important;
}

.showPlate {
    position: fixed;
    top: 18px;
}
/*更多版块*/
.morebbs {
    margin-top: 6px;
    text-align: right;
}
.morebbs a {
    text-decoration: none;
    color: #2B2B2B !important;
    margin-right: 10px;
}
.bbsHotPit .bbs_head .breadCrumbs {
    margin-left: 10px !important;
}
.bbsHotPit .bbs_head .left {
    background: none !important;
    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
}
.bbsHotPit .bbs_head .right .it-search {
    height: 18px;
    line-height: 18px;
    border-radius: 4px;
    border: 1px solid #dfdede !important;
    color: #424242;
    margin-right: -16px;
}
.bbsHotPit #sort select {
    height: 24px;
    line-height: 24px;
    border-radius: 4px;
    border: 1px solid #dfdede;
    color: #424242;
}
/*版块简介 begin*/
.pl_nav {
    display: none;
    border-radius: 8px;
    margin-bottom: 0 !important;
}
.pl_nav .pl_d {
    display: none !important;
}
.pl_nav .pl_top {
    margin-top: 0px !important;
}
.pl_nav .showshihuo {
    display: none !important;
}
/*版块简介 end*/
/*页脚*/
.hp-footer {
    border: none;
}
}

@-moz-document domain("bbs.hupu.com") {
/*右边推荐*/
#sub_sidebar {
    display: none;
}
.details {
    width: 917px !important;
}
.details #container_nopadd {
    border-radius: 8px;
}
.details #container_nopadd .toppage {
    display: none !important;
}
.details #container_nopadd .browse {
    display: none !important;
}
.details #container_nopadd #t_main {
    background-color: #eee !important;
}
.details #container_nopadd #t_main .breadCrumbs {
    display: none !important;
}
.details #container_nopadd #t_main .floor {
    background-color: #fff !important;
    margin: 8px 0 !important;
    border-radius: 6px;
    border: none !important;
}
.details #container_nopadd #t_main .floor .floor_box {
    background-color: #fff !important;
}
.details #container_nopadd #t_main .floor .headpic img {
    background: none !important;
    border-radius: 26px;
}

.details #container_nopadd #tpc {
    border: none !important;
}
.details #container_nopadd #tpc .cm {
    display: none !important;
}
.details #container_nopadd #tpc .floor-show .floor_box .case .subhead {
    display: none !important;
}
.details #container_nopadd #tpc .floor-show .floor_box .case .quote-content {
    font-weight: 400 !important; 
    font-size:16px !important;
}
/*热评*/
.details #container_nopadd #t_main .w_reply {
    border: none !important;
    background-color: #eee !important;
}
.details #container_nopadd #t_main .w_reply #highlights {
    display: none !important;
}
.details #container_nopadd #t_main .w_reply .floor {
    border-top: 4px solid #f4acac !important;
    background-color: #fff !important;
    margin: 6px 0 !important;
    border-radius: 6px;
}
.details #container_nopadd #t_main .w_reply .floor_box {
    background-color: #fff !important;
}
.details #container #container_nopadd {
    border: none !important;
}
.w_reply .floor_box,
.l_w_reply .floor_box {
    border: none !important;
    width: auto !important;
    padding-left: 0 !important;
}
.details #container_nopadd #t_main .floor .floor_box {
    background-color: transparent !important;
}
.w_reply .floor,
.l_w_reply .floor {
    width: 100% !important;
}
.floor_box .liangShare {
    display: none;
}
.f999 {
    display: none;
}
.floor_box .case br {
    display: none;
}
.floor_box .case {
    font-weight: 400;
}
.floor .floor-show {
    padding-top: 10px;
}
.floor_box {
    margin-left: 80px !important;
    padding-left: 0 !important;
}
blockquote {
    border-radius: 6px;
    border: none !important;
    font-weight:400;
}
.author {

    border: none !important;
}
.author .left .f666 {
     display: none !important;
}
.author .f666 {
   margin-right:10px !important;
}
.author .f666 a {
    display: none;
}
.author .f666 a:nth-child(2) {
    display: block;
}
.floor_box .author .left .post-owner{
    background-color:red !important;
    color:#fff !important;
        height: 22px;
    line-height: 22px;
    text-align: center;
    width:30px;
}
/*.floor_box .quote-content img{
    max-width:250px !important;
}*/
}
