style.css:
@-moz-document url("https://www.roblox.com/home"), url-prefix("https://www.roblox.com/games"), url-prefix("https://www.roblox.com/groups"), url-prefix("https://www.roblox.com/feeds") {
.dark-theme .content {
    background: rgba(76, 175, 80, 0);
    color: #ffffff !important;
    /*background*/
}
.light-theme .content {
    background-color: #000000 !important;
    color: #ffffff !important;
    /*background*/
}
#header {
    background-color: #9B0000 !important;
    border-color: #860000 !important;
    color: #ffffff !important;
    /*top bar*/
}
body {
    background-color: #000000 !important;
    /*background 2*/
}
#navigation {
    background-color: #323232 !important;
    /*the bar on the left*/
}
.rbx-header .age-bracket-label .age-bracket-label-username {
    display: inline;
    color: #ffffff;
    /*writing*/
}
.light-theme .rbx-header .text-header, .light-theme .rbx-header .text-header:active, .light-theme .rbx-header .text-header:focus, .light-theme .rbx-header .text-header:hover, .light-theme .rbx-header .text-header:link, .light-theme .rbx-header .text-header:visited {
    color: #ffffff;
    /*more writing*/
}
.dark-theme .rbx-header .rbx-navbar-search .input-field {
    padding-left: 33px;
    border-radius: 8px;
    border: 1px solid hsla(0, 0%, 100%, .2);
    background-color: #ffffff;
    color: #ffffff !important;
    /*search bar color*/
}
.gotham-font .font-footer, .gotham-font .font-footer:active, .gotham-font .font-footer:focus, .gotham-font .font-footer:hover, .gotham-font .font-footer:link, .gotham-font .font-footer:visited, .gotham-font .text-footer, .gotham-font .text-footer:active, .gotham-font .text-footer:focus, .gotham-font .text-footer:hover, .gotham-font .text-footer:link, .gotham-font .text-footer:visited, .gotham-font .xsmall, .gotham-font .xsmall:active, .gotham-font .xsmall:focus, .gotham-font .xsmall:hover, .gotham-font .xsmall:link, .gotham-font .xsmall:visited, .gotham-font .xxsmall, .gotham-font .xxsmall:active, .gotham-font .xxsmall:focus, .gotham-font .xxsmall:hover, .gotham-font .xxsmall:link, .gotham-font .xxsmall:visited {
    font-size: 10px;
    font-weight: 500;
    color: #ffffff !important;
    /*writing*/
}
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://www.rxwallpaper.site/wp-content/uploads/red-abstract-wallpaper-full-hd-wallpaper-and-background-image.jpg) !important;
    /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
}
.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #ffffff;
    /*color of the circle background behind players*/
}
.dark-theme .game-card-info .info-label {
    color: #ffffff !important;
    /*writing*/
}
.dark-theme .container-footer {
    background: rgba(76, 175, 80, 0);
    /*bar from below*/
}
.dark-theme .games-list-container {
    background: rgba(76, 175, 80, 0);
    /*games option*/
}
.scroller.next .arrow {
    background: rgba(76, 175, 80, 0) !important;
    right: 10px;
    /*arrow that shows games*/
}
.dark-theme .scroller {
    background: rgba(126, 0, 0, 0.5) !important;
    border: none;
    /*arrow that shows games 2*/
}
.dark-theme .scroller:focus, .dark-theme .scroller:hover {
    background-color: #9B0000 !important;
    /*arrow that shows games but on hover*/
}
div.game-card-native-ad {
    background: rgba(35, 68, 89, 0) !important;
    /*occasional sponsored ad sign*/
}
#chat-header {
    background-color: #393b3d !important;
}

.dark-theme .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    color: #fff;
    background-color: transparent;
    box-shadow: inset 0 -4px 0 0 #9B0000;
}
}

