/* ==UserStyle==
@name           Dark pluralistic.net
@namespace      USO Archive
@author         juankman94
@description    `Dark styles for Doctorow's awesome website`
@version        20200418.6.37
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix("https://pluralistic.net/") {
    :root {
        --bg-color: #ffffff;
        --fg-color: #000000;
        --link-color: #000000;
        --link-color-hover: #007acc;
        --brand-color: #1a1a1a;
        --brand-color-hover: #007acc;
    }

    @media (prefers-color-scheme: dark) {
        :root {
            --bg-color: #333333;
            --fg-color: #eeeeee;
            --link-color: yellow;
            --link-color-hover: orange;
            --brand-color: #ffffff;
            --brand-color-hover: yellow;
        }
    }

    .site {
        background-color: var(--bg-color);
        color: var(--fg-color);
    }

    .site-branding .site-title a {
        color: var(--link-color);
    }

    .site-branding .site-description {
        color: var(--brand-color);
    }

    a,
    a:link,
    a:visited {
        color: var(--link-color);
    }
    a:hover {
        color: var(--link-color-hover) !important;
    }

    h1.toch1 {
        color: var(--brand-color);
    }

    ul.toc li,
    .xToC {
        color: var(--fg-color);
    }

    blockquote,
    .post-password-form label,
    .post-navigation .meta-nav,
    .image-navigation,
    .comment-navigation,
    .widget_recent_entries .post-date,
    .widget_rss .rss-date,
    .widget_rss cite,
    .site-description,
    .author-bio,
    .entry-footer,
    .sticky-post,
    .taxonomy-description,
    .entry-caption,
    .comment-metadata,
    .pingback .edit-link,
    .comment-metadata a,
    .pingback .comment-edit-link,
    .comment-form label,
    .comment-notes,
    .comment-awaiting-moderation,
    .logged-in-as,
    .form-allowed-tags,
    .site-info,
    .wp-caption .wp-caption-text,
    .gallery-caption,
    .widecolumn label,
    .widecolumn .mu_register label {
        color: var(--fg-color);
    }
}
