@-moz-document domain("vice.com") {
/* ====================================
   VARIABLES
   ==================================== */

/* Overwritten
   ==================================== */

:root
{
	--base-background-color: hsl(0, 0%, 15%);
	--accent-color: hsl(0, 0%, 100%);
	--accent-text-color: hsl(0, 0%, 0%);
	--tumblr: #35465c;
	--newsletter: hsl(0, 0%, 100%);
	--longform-article-topic-color: hsl(0, 0%, 100%);
	--article-contributor-img-bg-color: #d8d8d8;
	--article-contributor-initials-color: #909090;
	--article-contributor-date-color: #aaa;
	--article-contributor-locale-color: #909090;
	--article-dek-color: hsl(0, 0%, 0%);
	--navbar-borderColor: rgba(0,0,0,0.15);
	--nav-footer-border-color: hsla(0,0%,100%,0.2);
	--nav-footer-accent-color: hsl(0, 0%, 100%);
	--nav-footer-accent-border-color: hsla(0,0%,100%,0.4);
	--nav-footer-accent-hover-color: hsla(0,0%,100%,0.6);
	--nav-section-tier1-color: hsl(0, 0%, 0%);
	--nav-section-tier2-color: #777;
	--nav-section-hover-color: #aaa;
	--network-bar-background-color: hsl(0, 0%, 0%);
	--network-bar-border-color: hsla(0,0%,100%,0.2);
	--article-image-caption-color: #777;
	--search-results-header-title-color: hsl(0, 0%, 0%);
	--search-results-header-count-color: #777;
	--search-results-header-cta-color: hsl(0, 0%, 0%);
	--search-results-footer-cta-color: hsl(0, 0%, 0%);
	--stream-card-title-color: hsl(0, 0%, 0%);
	--stream-card-summary-color: hsla(0, 0%, 100%, .6);
	--stream-card-contributor-color: hsl(0, 0%, 0%);
	--stream-card-date-color: hsla(0, 0%, 100%, .6);
	--grid-title-color: hsl(0, 0%, 100%);
	--grid-summary-color: #555;
	--grid-contributor-color: hsl(0, 0%, 100%);
	--blog-grid-title-color: hsl(0, 0%, 100%);
	--blog-grid-summary-color: #555;
	--large-grid-title-color: hsl(0, 0%, 100%);
	--large-grid-summary-color: #666;
	--lede-summary-color: #555;
	--lede-summary-hover-color: #555;
	--lede-contributor-color: hsl(0, 0%, 100%);
	--super-lede-dek-color: hsl(0, 0%, 0%);
	--super-lede-contributor-color: hsl(0, 0%, 0%);
	--newsletter-heading-color: hsl(0, 0%, 100%);
	--newsletter-subheading-color: hsl(0, 0%, 0%);
	--newsletter-email-border-bottom-color: #aaa;
	--upsell-first-line-color: hsl(0, 0%, 0%);
	--upsell-second-line-color: hsl(0, 0%, 0%);
	--upsell-button-background-color: hsl(0, 0%, 0%);
	--not-found-buttonColor: hsl(0, 0%, 100%);
	--canonical-date-color: #777;
	--link-button-color: hsl(0, 0%, 100%);
	--link-button-hover-color: hsl(0, 0%, 0%);
	--link-button-background-color: hsl(0, 0%, 0%);
	--link-button-hover-background-color: #aaa;
	--link-button-white-hover-color: hsl(0, 0%, 0%);
	--link-button-white-background-color: hsl(0, 0%, 0%);
	--link-button-white-hover-background-color: #aaa;
	--link-button-branded-color: hsl(0, 0%, 0%);
	--link-button-branded-hover-color: hsl(0, 0%, 0%);
	--link-button-outlined-border-color: #aaa;
	--linkButton-inlined-hover-color: hsl(0, 0%, 100%);
	--nav-section-upsell-color: hsl(0, 0%, 100%);
}

#root.vice,
:root {
 --accent-color-alt:#aaa;
 --accent-color-logo:hsl(0, 0%, 100%);
}

#root.vice {
 --accent-color-nav:hsl(0, 0%, 100%);
 --upsell-button-background-color:hsl(0, 0%, 15%);
 --link-button-white-hover-background-color:hsl(0, 0%, 100%);
 --link-button-outlined-border-color:hsl(0, 0%, 100%);
 --newsletter-email-border-bottom-color:hsl(0, 0%, 100%);
}
}

@-moz-document domain("vice.com") {
/* ====================================
   GLOBAL
   ==================================== */

/* Container
   ==================================== */

html,
html *
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
}

html.html--disable-scroll
{
    overflow-y: visible;
}

body
{
    background-color: hsl(0, 0%, 17%);
    color: hsl(0, 0%, 100%);
}

::selection,
::-moz-selection {
    color: hsl(0, 0%, 0%);
    background: hsla(0, 0%, 100%, 0.8);
}
}

@-moz-document domain("vice.com") {
.main-content,
.vice .latest-feed__item,
.hp-latest__chunk__feed,
.vice .vice-page__inner,
.fw-promo__text,
.fw-promo__image,
.sections-collection__tail .sections-collection__sections-card,
.sections-collection__tail .sections-collection__sections-card .sections-card__content
{
    background-color: hsl(0, 0%, 15%) !important;
}

.navbar-container--entered .titlebar,
.navbar-container--entering .titlebar,
.navbar-container--exiting .titlebar,
.titlebar, .networkbar
{
    background-color: hsl(0, 0%, 3%) !important;
}

.navpopup
{
    background-color: hsla(0, 0%, 0%, 0.75);
}

.html--disable-scroll #main-content
{
    filter: blur(8px);
}

.heading, a .heading, .heading a
{
    color: hsl(0, 0%, 100%);
}

.fw-promo__text .bb--link,
.fw-promo__text .byline,
.main-news-card .byline,
.sub-news-card-main .byline
{
    color: hsl(0, 0%, 100%) !important;
}


.article__body a
{
    color: hsl(0, 0%, 100%);
    text-decoration-line: underline;
    text-underline-offset: 0.5rem;
    transition: all 200ms cubic-bezier(0.42, 0, 0.58, 1);
}

.article__body a:hover
{
    color: hsla(0, 0%, 100%, 0.4);
    text-decoration-line: underline;
    text-underline-offset: 0.35rem;
}

.bb--link, .bb--link.black, .black .bb--link
{
    color: hsl(0, 0%, 100%);
    box-shadow: none;
    text-decoration-line: underline;
    text-underline-offset: 0.5rem;
    transition: all 200ms cubic-bezier(0.42, 0, 0.58, 1);
}

.bb--link:hover,
.bb--link.black:hover,
.black .bb--link:hover
{
    color: hsla(0, 0%, 100%, 0.7);
    box-shadow: none;
    text-decoration-line: underline;
    text-underline-offset: 0.35rem;
}

.article__body hr
{
    border-color: hsla(0, 0%, 100%, 0.25);
}

.article__body .article__blockquote,
.article__body .article__pull-quote
{
    color: hsl(0, 0%, 100%);
    border-left-color: hsl(0, 0%, 100%);
}

a, a:active, a:hover
{
    color: hsl(0, 0%, 100%);
}

.related-article-grid
{
    background: hsl(0, 0%, 100%);
    color: hsl(0, 0%, 0%);
}

.sections-lede,
.sections-lede__tail .sections-lede__sections-card,
.sections-lede__tail .sections-lede__sections-card .sections-card__content,
.sections-lede__head .sections-lede__sections-card .sections-card__content,
.sections-lede__head .sections-lede__sections-card,
.sections-collection__head .sections-collection__sections-card,
.sections-collection__head .sections-collection__sections-card .sections-card__content
{
    background-color: hsl(0, 0%, 15%);
}

.long-form-v2 .long-form-v2__content .long-form-v2__header-wrapper
{
    background: hsl(0, 0%, 15%);
}

.sections-collection
{
    background-color: hsl(0, 0%, 15%);
}

.related-article-grid__row .related-article-grid__row-item-title
{
    color: hsl(0, 0%, 0%);
}

.byline, a .byline, .byline a
{
    color: hsl(0, 0%, 100%);
}

.white.byline, .white .byline
{
    color: hsl(0, 0%, 0%);
}

.black.byline, .black .byline, .byline
{
    color: hsl(0, 0%, 100%);
}

.card-image, .card-image__placeholder-image
{
    background-color: hsl(0, 0%, 15%);
}

.long-form-v2 .long-form-v2__lede-image-wrapper
{
    background-color: hsla(0, 0%, 100%, .25);
}

.article-heading-v2 .article-heading-v2__contributionsSeparator
{
    border-top-color: hsla(0, 0%, 100%, .25);
}

.short-form-v2 .short-form-v2__heading
{
    border-top-color: hsl(0, 0%, 100%);
}

.recirculation-card
{
    border-bottom-color: hsla(0, 0%, 100%, .25);
}

.short-form-right-rail__heading
{
    border-bottom-color: 1px solid hsla(0, 0%, 100%, .25);
}

.embed-article-container
{
    border-bottom-color: hsla(0, 0%, 100%, .25);
    border-top-color: hsl(0, 0%, 100%);
}

.article-after-body .article-after-body__topics
{
    border-top-color: hsl(0, 0%, 100%);
}

.long-form-v2--light-separator
{
    border-top-color: hsla(0, 0%, 100%, .25);
}

.article-keep-reading--active .article-keep-reading__children-container::after
{
    background-image: linear-gradient(180deg, transparent, hsla(0, 0%, 15%, .02) 2%,hsl(0, 0%, 15%));
}

.article-keep-reading--active .article-keep-reading__button-container button
{
    background-color: hsl(0, 0%, 15%);
}

a, a:active, a:hover
{
    color: hsl(0, 0%, 100%);
}

.article-heading-v2 .article-heading-v2__contributions .article-heading-v2__social-link svg,
.sections-latest-tag__icon svg
{
    filter: invert(100%);
}

.vice__2019-footer
{
    background-color: hsl(0, 0%, 100%);
    color: hsl(0, 0%, 0%);
}

.vice__2019-footer__logo
{
    filter: invert(100%);
}

.bb--link.white, .white .bb--link
{
    color: hsl(0, 0%, 0%);
    box-shadow: inset 0 -1px 0 hsla(0, 0%, 0%, .2);
}

.vice .vice-page__inner
{
    background-color: hsl(0, 0%, 0%);
}

.sections-collection__tail .sections-collection__sections-card,
.sections-collection__tail .sections-collection__sections-card .sections-card__content
{
    background-color: hsl(0, 0%, 0%);
}

.hp-latest
{
    border-top-color:hsla(0, 0%, 100%, 25%);
}

.hp-latest__chunk__feed
{
    border-right-color:hsla(0, 0%, 100%, 25%);
}

.vice .latest-feed__item
{
    background-color: hsl(0, 0%, 0%);
}

.paginator__arrow
{
    color: hsl(0, 0%, 100%);
}

.fw-promo__text a:active div.heading,
.fw-promo__text a:focus div.heading,
.fw-promo__text a:hover div.heading
{
    color: hsl(0,0%,100%);
}

.grid__wrapper__card__text__title
{
    color: hsl(0, 0%, 100%);
}

.grid__wrapper__card__text__summary,
.grid__wrapper__card__text__contributor
{
    color: hsl(0, 0%, 77%);
}

.long-form-v2 .long-form-v2__content .long-form-v2__credit a
{
    color: hsl(0, 0%, 100%);
    -webkit-box-shadow:inset 0 -1px 0 hsl(0, 0%, 100%);
    box-shadow:inset 0 -1px 0 hsl(0, 0%, 100%);
}

.titlebar__logo__icon
{
    width: 175px;
    height: 55px;
    mask-size: cover;
    transition: all 200ms cubic-bezier(0.42, 0, 0.58, 1);
    /* mask-image: linear-gradient(-10deg, hsla(0, 0%, 100%, 0.06) 30%, hsla(0, 0%, 90.2%, 0.26) 60%); */
    /* mask-image: linear-gradient(0deg, hsla(0, 0%, 100%, 0.2) 3%, hsla(0, 0%, 100%, 1.0) 30%, hsla(0, 0%, 100%, 1.0) 83%, hsla(0, 0%, 100%, 0.8) 99%); */
}

.titlebar__logo__icon:hover
{
    opacity: unset;
    filter: opacity(0.4);
}

.titlebar__logo__icon svg
{
    width: 175px;
    height: auto;
}

.lede-card__text,
.left-lede,
.center-lede .primary__text__inner,
.news-home-feed,
.homepage-v2__lede-container__left
{
	background-color: hsl(0, 0%, 15%);
}
}

@-moz-document domain("vice.com") {
.primary-topic__section-image {
	filter: invert(10000%);
}

.article__body p {
	color: hsl(0, 0%, 80%);
}

.article__body p b {
    filter: brightness(150%);
}

.recirculation-card__heading-link:hover {
	color: hsla(0, 0%, 100%, .4);
}

.topics-collection,
.topics-collection .topics-collection__topics-card,
.topics-collection .topics-collection__topics-card .topics-card__image-link,
.topics-collection .topics-collection__topics-card .topics-card__content
{
	background-color: hsl(0, 0%, 15%);
}

.section-header__wrapper__title {
    color: hsl(0, 0%, 100%);
}

.video-infocard .info {
    color: hsl(0, 0%, 100%);
}

.video-infocard .topics-list a, .video-infocard .topics-list li, .video-infocard .description {
	color: hsl(0, 0%, 80%);
}

.section-header__wrapper {
	border-top-color: 3px solid var(--accent-color,hsl(0, 0%, 100%));
}

.vv-main-nav__element:hover .vv-main-nav__link, .vv-main-nav__element:focus .vv-main-nav__link, .vv-main-nav__element.active .vv-main-nav__link {
	background-color: hsl(0, 0%, 100%);
	color: hsl(0, 0%, 0%);
}

.vv-main-nav__element__dropdown__link-item
{
    color: hsl(0, 0%, 0%);
}

.vv-main-nav__element__dropdown__link-item:hover
{
    color: hsla(0, 0%, 0%, 0.4);
}

.btn.btn-branded {
	color: hsl(0, 0%, 0%);
	background-color: hsl(0, 0%, 100%);
}

.btn.btn-branded:active, .btn.btn-branded:focus, .btn.btn-branded:hover {
	color: hsl(0, 0%, 0%);
	background-color: hsl(0, 0%, 33%);
}

.paginator__progress--last-page {
	border-top-color: hsl(0, 0%, 100%);
}

button.btn-keep-reading {
	color: hsl(0, 0%, 100%);
	border-bottom-color: hsl(0, 0%, 100%);
}

}

@-moz-document domain("vice.com") {
.smart-header--light {
    color: hsl(0, 0%, 100%);
}

.article__header__rubric__label > img {
	filter: invert(10000%);
}

.article-newsletter-signup--light {
    background-color: hsl(0, 0%, 15%);
    color: hsl(0, 0%, 100%);
}

.split-header--light {
    color: hsl(0, 0%, 100%);
}
}
