/* welcome new chat */
div[class*="cozyMessage"][class*="groupStart"] {
    margin-top: 35px!important;
    position: relative;
}

/* Remove message highlight & timestamp */
div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] {
    background-color: transparent;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="divider-"] + div[class*="cozyMessage"][class*="groupStart"] {
    margin-top: -12px!important;
}

.latin24CompactTimeStamp-2V7XIQ.timestamp-3ZCmNB.timestampVisibleOnHover-2bQeI4.alt-1uNpEt {
    display: none;
}

/* Dividers */
div[class*="cozyMessage"][class*="groupStart"]::after {
    content: "";
    position: absolute;
    top: -20px;
    left: 20px;
    right: 20px;
    border-bottom: thin solid var(--background-modifier-accent);
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="divider-"] {
    margin: 20px 20px!important;
    top: 0;
    height: 12px;
    border-top: 0;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="divider-"] > span[class*="content"] {
    font-size: 14px;
    line-height: 22px;
    padding: 0 12px;
    background-color: #2c2c2c;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="divider-"] > span::before {
    content: "";
    position: absolute;
    width: 100%;
    top: 75%;
    left: 0;
    height: 0.5px;
    background-color: var(--background-modifier-accent);
    z-index: -1;
}

/* New messages */
div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="divider-"][class*="isUnread"] > span[class*="unreadPill"]::after {
    font-size: 10.5px;
    white-space: pre;
    content: " UNREAD MESSAGES";
}

.unreadPill-2HyYtt {
    font-size: 10.5px;
    padding: 0 8px
}

/* Mentions/ role mentions */
div[class*="cozyMessage"][class*="mentioned"] > div[class*="contents"] > div[class*="markup"] {
    background-color: rgba(255, 165, 0, 0.15)!important;
    padding: .1rem .2rem!important;
    padding-left: calc(6px + .2rem)!important;
    margin-left: -8px!important;
}

div[class*="cozyMessage"] > div[class*="contents"] > h2[class*="header"] span[class*="timestamp"] {
    margin-left: .15rem;
}



div[class*="cozyMessage"] > div[class*="contents"] img[class*="avatar"]:hover {
    opacity: 0.8;
}

/* Text Padding */
div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="cozyMessage"] {
    padding: 0;
    padding-left: 80px;
    padding-right: 10px;
}

/* Reaction Buttons */
div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] {
    padding-right: 10px;
    padding-left: 0;
    top: 0;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="cozyMessage"] > div[class*="buttonContainer"] > div[class*="buttons"][class*="isHeader"] {
    top: 1.375rem;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] {
    height: 1.375rem;
    background-color: transparent;
    box-shadow: none;
    display: flex;
    align-items: flex-start;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] > div[class*="emojiButton"],
div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] > div[class*="emojiSeparator"] {
    display: none;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] > div[class*="button"] {
    height: 1rem;
    width: 1rem;
    min-width: 1rem;
    padding: 0;
    background-color: transparent!important;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] > div[class*="button"] svg {
    height: 1rem;
    width: 1rem;
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="buttonContainer"] > div[class*="buttons"] > div[class*="wrapper"] > div[class*="button"]:last-child svg {
    transform: rotate(90deg);
}

div[class*="chatContent"] > div[class*="messagesWrapper"] div[class*="message"] > div[class*="contents"] > div[class*="markup"]::before {
    content: "";
    width: 2rem;
    height: 1rem;
    display: inline-flex;
    float: right;
}

/* Embeds */
div[class*="cozyMessage"] > div[class*="container"]:not([class*="button"]) {
    padding-top: 8px;
    padding-bottom: 8px;
    grid-row-gap: 8px;
}

