style.css:
@-moz-document url-prefix("http://boardwebgames.com/rojo") {
/* !!Paste import code here!! */

body, button, input, select, .waitUrl {
    font-family: "/*[[font]]*/", serif;
}

body {
    background-image: url(https://i.imgur.com/6sti4k7.jpg);
    background-size: 100%;
}

button {
    background: url(https://i.imgur.com/YgYaddf.jpg);
    background-size: cover;
}

button:active {
    background: url(https://i.imgur.com/YgYaddf.jpg);
    background-size: cover;
    transform: scale(1.05,1.05);
    transition: 0.05s all;
}

select {
    border-radius: 0;
}

#game {
    padding: 0 calc(var(--tileSize) * 0.2);
}

#lobbyList {
    border-color:#826644;
    background-color: #cab294;
}

#center:before {
    content: none;
}

.factory {
    background-image: url(https://i.imgur.com/3zUI8Cd.jpg);
    grid-gap: calc(var(--tileSize) * 0.1);
}

.factory .tile {
    box-shadow: 0 0 3px rgba(0,0,0,0.5)
}

.board {
    background-color: #f9f4ed;    
}

.intro #intro,
.player.activePlayer .board {
    background-image: url(https://i.imgur.com/27sez3r.jpg);
}

.overflow .lineTile,
.boardLine .lineTile,
.overflowPoints {
    border: solid calc(var(--tileSize) * 0.03) #a0a0a0;
    box-shadow: inset 1px 1px 2px 0px rgba(0, 0, 0, 0.3);
}

.overflowPoints {
    border-bottom: none;
}

#game .tile.tileFilled_true {
    border: none;
}

#game .tile.tileFilled_true.first {
    filter: drop-shadow(3px 3px 0px rgb(126, 164, 118));
}

#game .tile.tileFilled_true.blue {
    filter: drop-shadow(3px 3px 0px rgb(83, 114, 183));
}

#game .tile.tileFilled_true.yellow {
    filter: drop-shadow(3px 3px 0px rgb(144, 145, 87));
}

#game .tile.tileFilled_true.rojo {
    filter: drop-shadow(3px 3px 0px rgb(149, 57, 57));
}

#game .tile.tileFilled_true.white {
    filter: drop-shadow(3px 3px 0px rgb(148, 164, 145));
}

#game .tile.tileFilled_true.black {
    filter: drop-shadow(3px 3px 0px rgb(55, 55, 55));
}



}
