@-moz-document url-prefix("http://download.inveno.com") {
* {
    margin: 0;
    padding: 0;
}
@keyframes rainbow-border {
    0% {
        background-position: 0% 50%
    }
    50% {
        background-position: 100% 50%
    }
    100% {
        background-position: 200% 50%
    }
}
.body {
    position: absolute!important;
    height: 100% !important;
    width: 100% !important;
}
.is-vertical.el-container {
    position: absolute!important;
    height: 100% !important;
    width: 100% !important;
}
.el-main {
    background-color: #f7f7f7;
}
/*浮层样式*/
.table {
    background: #f000;
}
/*左侧栏样式*/
.el-menu-item,
.el-aside {
    color: #888686!important;
    background-color: #f7f7f7!important;
}
.el-menu.el-menu-vertical-demo {
    width: 200px!important;
    height: 0!important;
    padding-bottom: 30%!important;
    margin: 0!important;
    background-color: #fff0!important;
    border-color: #fff0!important;
}
li.el-submenu:nth-of-type(1) > .el-submenu__title,
li.el-submenu:nth-of-type(3) > .el-submenu__title {
    background-color: #0000!important;
    color: #888686b0!important;
    font-weight: bold!important;
}
/*更新成功提示样式*/
.el-message--success.el-message {
    position: absolute!important;
    background: #970830;
    top: 0%!important;
    left: 85%!important;
    height: 45px!important;
    opacity: 0.7!important;
    border-radius: 25px;
}
.el-icon-success.el-message__icon {
    color: #fff!important;
}
.el-message--success.el-message > .el-message__content {
    position: relative!important;
    left: 30%!important;
    top: -1%!important;
    font-size: 20px!important;
    color: #fff!important;
    font-weight: bold!important;
    text-shadow: 0px 0px 9px #b7b7b7!important;
}
/*更新失败提示样式*/
.el-message--warning.el-message {
    display: none!important;
    position: fixed!important;
    background: #970830!important;
    top: 1px!important;
    left: 21.5%!important;
    height: 43px!important;
    opacity: 0.9!important;
    border-color: #0000!important;
}
.el-icon-warning.el-message__icon {
    color: #fff!important;
}
.el-message__content,
div.el-message--warning.el-message:nth-of-type(3) {
    position: relative!important;
    color: #fff!important;
    left: 20%!important;
    top: -1%!important;
    font-size: 20px!important;
    font-weight: bold!important;
    text-shadow: 0px 0px 9px #b7b7b7!important;
}
/*----------------隐藏元素----------------*/
.el-header {
    display: none!important;
}
.iveno-breadcrumb {
    display: none!important;
}
.el-menu-item-group__title {
    display: none!important;
}
li.el-submenu:nth-of-type(2) {
    display: none!important;
}
li.el-submenu:nth-of-type(4) {
    display: none!important;
}
li.el-submenu:nth-of-type(5) {
    display: none!important;
}
li.el-submenu:nth-of-type(6) {
