@-moz-document url-prefix("http://download.inveno.com/ZhiziCms3/#/NewArticleAuditDetail") {
/*表内线条样式*/
.table td {
    border: 2px solid #bbb;
    height: 30px;
}
.audit_article {
    /*表框线条样式*/
    border: 5px solid #a3a8ac9c;
    color: #888686b0;
    /*表内文字样式*/
    font-size: 16px;
    background: #fff0;
}
/*剩余数样式*/
.article.table > span {
    border-radius: 25px!important;
    text-shadow: 0px 0px 10px #fff!important;
    animation: sun 8s infinite;
    position: relative!important;
    left: 40%!important;
    top: 15px!important;
    font-size: 1.5rem!important;
    font-family: "Comic Sans MS", cursive, sans-serif;
    z-index: 1!important;
    color: white!important;
    padding: 8px!important;
    cursor: crosshair!important;
    background: #3e3e3e;
}
.article.table > span::before {
    content: "";
    animation: rainbow-border 2s linear infinite;
    display: table-row!important;
    position: absolute!important;
    left: -5px;
    right: -5px;
    top: -5px;
    bottom: -5px;
    background: linear-gradient(90deg, #0ebeff, #ffdd40, #ae63e4, #47cf73, #0ebeff, #ffdd40, #ae63e4, #47cf73);
    background-size: 200% 200%;
    border-radius: 25px;
    filter: blur(5px);
    z-index: -1;
}
/*ID样式*/
tr:nth-of-type(1) > td:nth-of-type(2) {
    color: #940301;
    font-size: 20px;
    font-family: "Courier New", Courier, monospace;
    text-shadow: 0px 0px 9px grey;
    font-weight: bold;
    text-align: left
}
/*标题样式*/
tr:nth-of-type(2) > td:nth-of-type(2) {
    color: #970830;
    font-size: 35px;
    font-weight: bold;
    text-shadow: 0px 0px 9px grey;
    text-align: left
}
tr:nth-of-type(2) {
    height: 80px;
}
/*链接样式*/
tr:nth-of-type(4) > td:nth-of-type(2) > span:nth-of-type(1) {
    display: none!important;
}
tr:nth-of-type(4) > td:nth-of-type(2) > a:after {
    content: " ''" attr(href) "''";
    color: #2866bd;
}
tr:nth-of-type(4) > td:nth-of-type(2) > a:hover {
    border-bottom: #888686b0 2px solid
}
tr:nth-of-type(4) > td:nth-of-type(2) > a {
    position: relative;
    left: -19px;
    color: #888686b0;
    font-weight: bold;
}
/*正文格样式*/
tr:nth-of-type(9) > td:nth-of-type(1)::before {
    position: relative!important;
    top: 50px;
    font-size: xx-small!important;
    font-weight: bold;
    content: '(竖版视频两侧会有白边？不要怕，它还是竖版)';
}
tr:nth-of-type(9) > td:nth-of-type(2) > div:nth-of-type(1),
tr:nth-of-type(9) > td:nth-of-type(2) > div:nth-of-type(1) > div:nth-of-type(1) {
    height: 280px!important;
}
.video {
    height: 280px!important;
    width: 100%!important;
}
video {
    transform: translate(0%, 0%)!important;
    height: 100%!important;
    width: 30%!important;
}
/*地域信息样式*/
.el-tag--danger.el-tag {
    color: #fff!important;
    background-color: #970830;
