    background-color: #fff0;
    font-weight: bold;
}
.el-select .el-input__inner:hover {
    border-color: #2866bd;
}
.hover.el-select-dropdown__item > span {
    color: #2866bd!important;
    font-weight: bold;
}
.el-select-dropdown__item.selected {
    color: #2866bdb5!important;
    font-weight: bolder!important;
}
