style.css:
@-moz-document domain("app.logos.com") {
:root {
  --theme-blue: #45A1FF;
  --theme-header-bg: #41464B;
  --theme-background-color: #41464B;
  --theme-header-color: #fff;
  --theme-color: #fff;
  --theme-toolbar-background-color: #32383d;
  --theme-toolbar-color: #fff;
  --theme-highlight-border-color: #FF8433;
  --theme-lighter-background-color:#7e8182;
  --bible-study-theme-border-color: #02080D;
  
  --bible-study-theme-text-color: #ddd;
  --bible-study-theme-link-color: #76bfff;
	
  --bible-study-theme-card-stock-background-color: rgba(255,255,255,.7);
  --bible-study-theme-panel-background-color: #798085;

  --bible-study-theme-menu-item-selected-color: #32383d;
  --bible-study-theme-menu-item-background-color: #41464b;
  --bible-study-theme-menu-item-hover-color: #798085;
	
  --bible-study-theme-dropdown-background-color: #32383D;
  
  --bible-study-theme-button-filled-background-color: #32383D;
  --bible-study-theme-button-filled-border-color: #ffffff;
  --bible-study-theme-button-filled-hover-color: #ffffff;
  --bible-study-theme-button-filled-hover-background-color: #61666B;
 --bible-study-theme-button-filled-color: #ffffffaa;
  
  --bible-study-theme-button-bordered-background-color: #32383D;
  --bible-study-theme-button-bordered-hover-background-color:  #61666B;
  --bible-study-theme-button-bordered-hover-color: #ffffff;
	
  --bible-study-theme-button-icon-hover-background-color: #DDDDDD;
  --bible-study-theme-button-icon-background-color: #7E8182;
	
  --bible-study-theme-tab-button-active-background-color: var(--theme-toolbar-background-color);
  --bible-study-theme-active-item-thick-border-color: #ff8433;
	
  --bible-study-theme-sidebar-background-color: #41464Bee;
   
   --factbook-header-color:  #de0000;
    --factbook-color: #fff;
    
    --search-results-link: #76bfff;
}

.application-header {
	background: var(--theme-header-bg);
	color: var(--theme-header-color);
}
.application-header span {
	color: var(--theme-header-color);
}
.tab-strip-container {
	background: var(--theme-background-color);
	color: #fff;
	border-bottom: 1px solid var(--theme-highlight-border-color);
}
.panel-tab-wrapper.minimal, .panel-tab-wrapper.full {
	background: #475057;
	color: #fff;
	border: 0;
	border-left: 1px solid #D8D8D8;
}
.panel-tab-wrapper.minimal span, .panel-tab-wrapper.full span {
	color: #fff;
}
.panel-tab-wrapper.selected.minimal, .panel-tab-wrapper.selected.full {
	background: var(--theme-toolbar-background-color);
	border: 1px solid var(--bible-study-theme-border-color);
	border-bottom: 0;
}
.panel-container >[class^="panel"] >[class^="container_"] >[class^="layer"] {
	background: var(--theme-toolbar-background-color);
	border:0;
}
.content-pane-stack-has-content .tab-strip-container {
	border: 1px solid var(--bible-study-theme-border-color);
}
.tab-close-button svg > path, [class^="closeButton_"] svg > path {
	fill: #fff;
}
.tab-close-button:hover svg > path, [class^="closeButton_"]:hover svg > path {
	fill: red;
}
.panel-container[class^="mainWrapper"] >[class^="container_"] {
	background: var(--theme-background-color);
	color: white;
}
.panel-container[class^="mainWrapper"] >[class^="container_"] button {
	color: #fff;
}
[class^="sinaix-context-menu_"] {
	border: 1px solid var(--bible-study-theme-border-color);
}
[class^="sinaix-context-menu_"][class^="left-column"] {
	background: var(--theme-background-color);
	opacity: .9;
}
[class^="sinaix-context-menu_"][class^="left-column"] > * {
	background: #fff;
}
[class^="sinaix-context-menu_"][class^="right-column"] {
	background: #fffffff9;
}
header[class^="goBox_"] svg > path {
	fill:#999 !important;
}
.content-pane-stack {
	background: var(--theme-lighter-background-color);
}
.panel-container [class^="toolbarIconButton_"] > button > svg > path {
	fill: #fff;
}
.panel-container [class^="toolbarIconButton_"]:hover > button > svg > path {
	fill: #005EC4;
}

header [class^="tabItems"] svg > path, .panel-tab .tab-icon svg > path {
	fill: #fff;
}



.home-active .home-container [class^="background_"] button svg >path {
  fill: #fff;
}

.home-active .home-container [class^="background_"] button:hover svg >path {
  fill: #005EC4;
}

.home-active .home-container [class^="background_"] [class^="title_"] {
  color: #fff;
}



.selected_3QYE7 {
  background: #798085;
}

.selected_3QYE7 svg >path {
  fill: #fff !important;
}

.home-active .home-container [class^="card"] [class^="title_"] {
  color: #000;
}

.panel-container {
  background: var(--theme-background-color);
}
.panel-container [class^="container_"]{
background: var(--theme-background-color);
  }

.panel-container [class^="container_"] *{
  color: #fff;
}

.panel-container [class^="container_"] button:hover > span{
  color: #41464B !important;
}
[class^="container"] input {
  color: #41464b !important;
}
.panel-container [class^="container_"] [class^="expanderArrow"] > svg path,
.panel-container [class^="container_"] .more-and-search-links-container > svg path {
  fill: #fff;
}
.panel-container [class^="container_"] [class^="moreLink"] > span {
  color:var(--theme-blue) !important;
  opacity: .8;
}
.panel-container [class^="container_"] [class^="moreLink"] > span:hover {
  color:var(--theme-blue) !important;
  opacity: 1;
}

.panel-container {
  border-left-color: var(--bible-study-theme-border-color);
}

[class^="dropdownContent_"] {
  border: 1px solid black;
  background: var(--theme-toolbar-background-color);
  color: #cccccc;
}


[class^="dropdownContent_"] * {
  color: #cccccc;
}

[class^="dropdownContent_"] a[class^="link_"]:hover {
  color: #fff;
  background-color: #61666B;
}

[class^="dropdownContent_"] button[class^="item_"]:hover {
  color: #fff;
  background: #61666B;
}

[class^="dropdownContent_"] [class^="checkMark_"] svg > path {
  fill: green;
}

.panel-container [class^="container_"] button[class^="entryTitle"]:hover > span {
  color: var(--theme-blue) !important
}

.panel-container [class^="container_"] [class^="toolbarControls"] button svg > path {
  fill: var(--theme-blue);
}

.panel-container.left {
  border-right: 1px solid var(--bible-study-theme-border-color);
}

[class^="sidebarWrapper"] {
  background: var(--theme-background-color);
}

[class^="sidebarWrapper"] [class^="selectorIsActive"] {
  background: var(--theme-toolbar-background-color);
}
[class^="sidebarWrapper"] [class^="selectorButtonContainer_"] button > svg > path {
  fill: #fff;
}

[class^="sidebarWrapper"] [class^="toolbarWrapper"] [class^="container_"] {
  background: var(--bible-study-theme-active-item-thick-border-color);
}

.courses-tool__plan-browser-container {
  background: var(--theme-toolbar-background-color);
}
.plan-browser-sidebar {
	  background: var(--theme-background-color);
	color: var(--theme-color);
}

[class^="styles__container"] {
  background: var(--theme-background-color);
  color: var(--theme-color);
}
.word-and-transliteration::after {
    width: 50px;
    background: linear-gradient(to right, var(--theme-toolbar-background-color) 50%, transparent);
}
.word-and-transliteration > div {
  background: var(--theme-toolbar-background-color);
}

.word-by-word-verse-text > *{
  background: var(--theme-toolbar-background-color);
}
word-and-transliteration > div a::after, .word-by-word-resource-links > ul > li a::after, .word-by-word-verse-text a::after, .word-by-word-lemma-info a::after, .word-and-transliteration > div span::after, .word-by-word-resource-links > ul > li span::after, .word-by-word-verse-text span::after, .word-by-word-lemma-info span::after {
  background: var(--theme-toolbar-background-color)
}

[class^="searchPanel"] {
	color: var(--bible-study-theme-text-color);;
}
[class^="searchPanel"] [class^="container"] [class^="layer"] {
  background: var(--theme-toolbar-background-color);
}

.factbook-heading {
  color:  var(--factbook-header-color);
}

.factbook-content {
  color: var(--factbook-color);
}
.factbook-footer {
  color: var(--factbook-color);
}

[class^="basicSearchResult"] > a {
  color: var(--search-results-link);
} 
}
