.grey.button
{
    border: 2px solid #303031; /*Gray Button Border*/
    background-color: #E03131; /*Gray Button Color*/
}

.grey.button:hover
{
    border: 2px solid #303031; /*Gray Button Hover Border*/
    background-color: #CA2B2C; /*Gray Button Hover Color*/
}

.red.button
{
    border: 2px solid #303031; /*Red Button Border*/
    background-color: #E03131; /*Red Button Color*/ 
}

.red.button:hover
{
    border: 2px solid #303031; /*Red Button Hover Border*/
    background-color: #CA2B2C; /*Red Button Hover Color*/
}

p.info, div.info, span.info
{
    font-style: italic; /**/
