style.css:
@-moz-document domain("planetagracza.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #777;
        }
        ::-webkit-scrollbar-thumb:hover {
             background-color: #d41111;
			 -webkit-box-shadow: inset 0 0 10px rgba(0, 0, 0, .9);
        }

body.custom-background, #header-text-nav-container, #content .post .article-content, #secondary, #secondary .widget, #primary > .theiaStickySidebar, .field.s, .clearfix.main-content-section, .wp-post-image.size-colormag-featured-image.attachment-colormag-featured-image, #primary, #header, #header nav ul li ul, html, #main, .list--blog .item, .planetagracza-block-newest-discounts .list .list-items, #search-overlay {
    background: #252525;
    color: #c6c6c6;
}
input, select, textarea, button {
	background: #252525;
}
h1, h2, h3, h4, h5, h6, #content .post .article-content .entry-title a, #content .post .article-content .entry-title a {
    color: #fff;
}
.search-form-top {
    background: #303030;
	border-color: #404040;
}
.searchform {
    border-color: #404040;
    box-shadow: 0 0 6px rgba(200, 200, 200, .2) inset;
}
.comment-content {
	background: #303030;
	border-color: #404040;
}
#comments {
	border-color: #404040;
}
input[type="text"], input[type="email"], input[type="password"], textarea, input, select, option {
	background: #252525;
	border-color: #404040;
	color: #c6c6c6;
}
.posted-on a, .byline a, .comments a, .comments span, .tag-links a, .edit-link a, #content .post .article-content .below-entry-meta .posted-on .fa, #content .post .article-content .below-entry-meta .byline .fa, #content .post .article-content .below-entry-meta .comments .fa, #content .post .article-content .below-entry-meta .tag-links .fa, #content .post .article-content .below-entry-meta .edit-link .fa, .comments-area .comment-edit-link, .comments-area .comment-permalink, .comments-area .comment-date-time, .comments-area .comment-author-link {
	color: #aaa;
}
iframe {
	border-color: #404040 !important;
}
#comments i {
	color: #bbb;
}
.widget_featured_posts .article-content .entry-title a {
	color: #c6c6c6;
}
.widget_featured_posts .single-article {
    box-shadow: 0 1px 3px 0 rgba(142, 142, 142, .1);
}
.footer-widgets-wrapper {
	border-color: #404040;
}
[src='data:image/png;base64,'], #header .site-title a, .comments-placeholder h2 .count {
	filter: invert(100);
}
a, input, select, textarea, button, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
    color: #766aff;
}
.comments-placeholder h2 .count .value {
	filter: inherit;
}
table, .wp-pagenavi a:hover, .wp-pagenavi span.current {
	border-color: #766aff;
}
.post-categories a:before {
	background: #303030;
}
.post-categories a[href="https://planetagracza.pl/recenzje-gier/pc-recenzje/"]:before {
	background: #c6c6c6;
}
}
