style.css:
@-moz-document domain("note.com") {
:root {
  --bg_primary:rgb(32,32,32);
  --bg_secondary:rgb(40,40,40);
  --bg_tertiary:rgb(52,52,52);
  --bg_clear:rgba(0,0,0,0);
  --bg_key:rgba(65, 201, 180, 0.1);
  --bg_dropdown:rgb(16,16,16);

  --text_primary:rgb(200,200,200);
  --text_secondary:rgb(240,240,240);
  --text_tertiary:rgb(184,184,184);
  --text_key:rgb(65, 201, 180);

  --border_primary:rgb(60,60,60);
  --border_secondary:rgb(52,52,52);
  --border_key:rgb(65, 201, 180);
}

html{background:var(--bg_primary);}
body{background:var(--bg_primary) !important;color:var(--text_primary) !important;}
body *{border-color:var(--border_primary) !important;}

a{transition:all 0.1s ease-in-out;}
a:hover{color:var(--text_secondary) !important;}

a[class$="__link"]{color:var(--text_primary) !important;}
a[class$="__link"]:hover{color:var(--text_secondary) !important;}
p[class$="__message"]{color:var(--text_secondary) !important;}

.a-link{color:var(--text_primary) !important;}
.a-link:hover{color:var(--text_secondary) !important;}
.a-button{background-color:var(--bg_secondary) !important;color:var(--text_key) !important;border-color:var(--border_key) !important;}
.a-tag{background-color:var(--bg_secondary) !important;}
.a-tag__label{color:var(--text_primary) !important;}

.btn--size_small{background:var(--bg_secondary) !important;color:rgba(200,200,200,0.3) !important;}
.btn--size_small[disabled]{background:var(--border_primary) !important;color:var(--text_primary) !important;}

.o-navBar{background:var(--bg_primary) !important;color:var(--text_primary) !important;}
.o-navbarMenu__category{border-color:var(--border_secondary) !important;}
.a-navItem__item:hover{color:var(--text_secondary) !important;}
.a-navItem__item.is-active{color:var(--text_primary) !important;}

.activation,
.activationMessage{background:var(--bg_key) !important;}

.o-homescreenBanner,
.o-homescreenBanner--type_ios{background:var(--bg_secondary) !important;}

.m-bar{background:var(--bg_secondary) !important;color:var(--text_secondary) !important;}

.m-accordion__header{color:var(--text_secondary) !important;}
.m-accordion__header .m-accordion__icon{color:var(--text_primary) !important;}

.m-userCandidates{background:var(--bg_secondary) !important;}
.m-userCard{background:var(--bg_primary) !important;color:var(--text_primary) !important;}

#__layout > div{background:var(--bg_primary) !important;}

/* form */
.m-iconWithtext__form{background:var(--bg_secondary) !important;}
.a-input{background:var(--bg_primary) !important;color:var(--text_primary) !important;}
.a-input::placeholder{color:var(--text_primary) !important;}

/* dropdown */
.m-submenu__item--all:before{background-image:none !important;}
.m-submenu__category{background:var(--bg_primary) !important;}
.m-submenu__category:before{background:var(--bg_primary) !important;border-color:var(--border_primary) !important;}
.m-submenu__categoryTitle{color:var(--text_secondary) !important;}
.a-balloon__body:before{background:var(--bg_dropdown) !important;border:none !important;}
.a-balloon__body,
.m-pulldown__body,
.m-notificationList{background:var(--bg_dropdown) !important;box-shadow:none !important;}
.o-usermenuPopover__item--separate::before{background:var(--border_primary) !important;}
.o-usermenuPopover__link:hover,
.o-usermenuPopover__onboardingItem:hover,
.m-notificationList__item:hover,
.m-basicBalloonList__link:hover{background:var(--bg_secondary) !important;}
.m-notificationList__item--empty:hover{background:none !important}
.o-postNote__itemLink:hover .o-postNote__itemLabel{color:var(--text_secondary) !important;}
.o-searchWindow__item:hover{background:var(--bg_secondary) !important;}

/**/
.modal-content,
.m-basicModalContent,
.m-basicModalContent__footer{background:var(--bg_primary) !important;}
.m-basicModalContent__header{color:var(--text_secondary) !important;}

/* editor */
.o-header{background:var(--bg_primary) !important;}
.o-noteTips__body{background:var(--bg_primary) !important;}
.o-onboardingNotice{background:var(--bg_secondary) !important;color:var(--text_secondary) !important;}
.m-tagInput__body,
.o-hashtagInput__contest,
.o-advancedSetting{background:var(--bg_secondary) !important;}
.o-noteManual__body{background:var(--bg_primary) !important;}

/* help*/
.article-body *{color:var(--text_primary) !important;}
.collapsible-sidebar .sidenav-item{color:var(--text_primary) !important;border-color:var(--bg_primary) !important;}
.collapsible-sidebar .sidenav-item:hover{background:var(--bg_secondary) !important;}
.collapsible-sidebar .sidenav-item.current-article{border-color:var(--text_primary) !important;}


/* ====================
Home
==================== */
.p-searchHeader__pageTitle{background:var(--bg_primary) !important;}

.o-timeline__insert,
.o-timelineHome__item--insert{background:var(--bg_secondary) !important;}
.o-timeline__insertTitle{color:var(--text_secondary) !important;}

.o-textNote,
.o-imageNote,
.o-talkNote{background:var(--bg_clear) !important;}
.o-textNote__link:hover,
.o-textNote:before,
.o-imageNote__link:hover,
.o-imageNote:before,
.o-talkNote:before{background:var(--bg_secondary) !important;}

.o-recommendedUsers:before{background: linear-gradient(270deg,hsla(0,0%,100%,0) 0, var(--bg_primary)) !important;}
.o-recommendedUsers:after{background: linear-gradient(-270deg,hsla(0,0%,100%,0) 0, var(--bg_primary)) !important;}
.m-userDescCard{background:var(--bg_secondary) !important;}

/* sub */
.o-magazineCard{background:var(--bg_primary) !important;}
.m-avatars__item:before{border-color:var(--bg_primary) !important;}

.m-userPromoteCard,
.o-sidebarTwitter{background:var(--bg_secondary) !important;}


/* ====================
Article
==================== */
.o-navbarNoteDetail__body{background:var(--bg_primary) !important;color:var(--text_primary) !important;}

.p-article__warning{background:var(--bg_secondary) !important;color:var(--text_primary) !important;}

/* main */
.o-noteContentText__title{color:var(--text_secondary) !important;}
.o-noteContentText__body{color:var(--text_primary) !important;}
.o-noteContentText__body h3{color:var(--text_secondary) !important;}
.o-noteContentText__body blockquote{background:var(--bg_secondary) !important;}

.o-indexHeading{background:var(--bg_secondary) !important;}
.o-indexHeading__item{border-color:var(--border_secondary) !important;}
.o-indexHeading__link{color:var(--text_tertiary) !important;}

.external-article-widget-title{color:var(--text_primary) !important;}
.external-article-widget a:hover .external-article-widget-title{color:var(--text_tertiary) !important;}

.external-article-widget-description{color:var(--text_primary) !important;}
.external-article-widget-url{color:var(--text_tertiary) !important;}

.o-supportAppeal{background:var(--bg_secondary) !important;}
.o-supportAppeal__contents{background:var(--bg_secondary) !important;}
.o-sliblingNote:before{background:var(--border_primary) !important;}

.o-magazineHaving__item{background:var(--bg_primary) !important;}

.o-magazineItemWithNotes{background:var(--bg_secondary) !important;}

.p-article__recommend{background:var(--bg_secondary) !important;}
.m-noteCard{background:var(--bg_primary) !important;border-color:var(--border_secondary) !important;}

.pswp__item{background:var(--bg_secondary) !important;}

/* Comment message */
.o-commentArea__message{color:var(--text_tertiary) !important;}

.o-shareBooster{background:var(--bg_secondary) !important;}
.o-shareBooster:before{background:var(--bg_secondary) !important;border:none !important;}


/* ====================
Others
==================== */
/* creator */
.o-creatorProfile{background-color:var(--bg_primary) !important;}
.m-profileStatus__followCount{color:var(--text_primary) !important;}
.o-creatorMenu{background-color:var(--bg_primary) !important;}
.o-creatorMenu__nav:before{display:none;}
.o-creatorContent{background-color:var(--bg_primary) !important;}
.o-circleDefault__summary,
.o-circleDefault__planItem,
.o-circleBlurPost__title{background-color:var(--bg_secondary) !important;}
.o-circleBlurPost__content{background-color:var(--bg_primary) !important;}
.o-circle-card{background-color:var(--bg_secondary) !important;}

/* magazine */
.o-magazine{background-color:var(--bg_primary) !important;}
.o-magazine__sectionTitle{color:var(--text_secondary) !important;}
.o-magazineSmallCard{background-color:var(--bg_secondary) !important;}

/* contents */
.p-contests{background-color:var(--bg_primary) !important;}
.o-contests__column-title{color:var(--text_secondary) !important;}

/* categories */
.p-category{background-color:var(--bg_primary) !important;}
.o-categoryList__title{color:var(--text_secondary) !important;}
.m-categoryCard__content{background-color:var(--bg_secondary) !important;}

.renewal-c-card{background:var(--bg_secondary) !important;}

/* creator page */
.o-noteContentImage__title{color:var(--text_secondary) !important;}

/* hashtag */
.p-hashtag__link{background:var(--bg_secondary) !important;}

/* login */
.register-container{background:var(--bg_secondary) !important;}

/* follow_recommend */
.renewal-l-header__inner{background:var(--bg_secondary) !important;}
.p-footerBar{background:var(--bg_secondary) !important;}
.renewal-c-button--secondaryMedium{background:var(--bg_secondary) !important;}
.renewal-p-magList__item{background:var(--bg_secondary) !important;}
.renewal-p-magList__description > p > content-link > a{color:var(--text_primary) !important;}

/* photo gallery */
.o-textEditor__gallery{background:var(--bg_primary) !important;}
.o-imageGallery__header{background:var(--bg_primary) !important;}
.o-imageGallery__search .o-imageGallery__searchField{}
.o-imageGallery__search::before{background:none !important;}
.o-imageGallery__body div[data-v-2496cb5a] div[class]{border-color:var(--bg_primary) !important;}



}
