style.css:
@-moz-document url-prefix("https://elotrolado.net") {
body {
    background-color: #000;
}

.new .wrap .body {
    background: #3e3e3e;
    color: white;
}

.new .wrap {
    background-color: #000;
}

.nolmargin, .nohmargin {
    background: #383941;
}

.col-lg-16 {
    background: black;
}

.col-lg-8 {
    background: black;
}

a {
    color: #1ee8d0;
}

activetopics a:not(.normal) span{
    background: black;
}

.new .wrap h2 a {
    color: burlywood;
}

.section .topic {
    color: #fedec0;
}

.section .topic .posts, .section .topic .views, .section .topic .topics, .section .forum .posts, .section .forum .views, .section .forum .topics {
    background: #3c3b3a;
}

section .topic .title, .section .topic .lastpost, .section .forum .title, .section .forum .lastpost {
    background: #3c3b3a;
}

.row {
    background: #3c3b3a;
}

.container-fluid {
    background-color: #3c3b3a;
}


#menu>a.openable.openable-left {
    color: bisque;
}


#menu>a {
    color: #ff7f00;
}

#breadcrumbs, #breadcrumbs a {
    color: #a94442;
}

.section .topic .author a, .section .topic .author .mods, .section .forum .author a, .section .forum .author .mods {
    color: #ca731e;
}

.section .topic .topicbtn, .section .forum .topicbtn {
    background-color: #949494;
}

.section .topic, .section .forum {
    background: #383941;
}

section .topic, .section .forum {
    background: #383941;
    border-bottom: 1px solid #777;
}

.forum-actions .pages .jumpto, .topic-actions .pages .jumpto, #ucp-actions .pages .jumpto, #mcp-actions .pages .jumpto, #news-actions .pages .jumpto, #memberlist-actions .pages .jumpto {
    color: bisque;
}

.section .topic .pages a, .section .forum .pages a {
    background: #ececec;

}

.section .topic .title a, .section .forum .title a {
    color: #ececec;
}

.section .post .content .message, #pm-review .post, #topic-review .post {
    word-wrap: break-word;
    overflow-wrap: break-word;
    color: whitesmoke;
    background: #4c4c4c;
}

blockquote cite {
    background: #585454;
}

blockquote {
    background-color: #353535;
    border-left: 5px solid #4e4e4e;
    background: #292929;
}

.section .post .content {
    background: #454545;
}

.section .post {
    background: #4c4c4c;
}

.section .post .profile {
    background-color: #353535;
}

.section .post .profile .status {
    color: #ececec;
}

.section .post .profile .author {
    color: #ececec;
}

.section .post .content .message a.mention {
    color: #89c277;
}

.section .post .profile .about {
    color: #ababab;
}

blockquote blockquote {
    background-color: #333;
}


* {
    background: #3c3b3a;
}

blockquote blockquote blockquote {
    background-color: #52524f;
}

#shortnews {
    background-color: #4c4949;
}

#shortnews h3 a {
    color: #ececec;
}

body {
    color: #bababa;
}

#news-single #news-inner-wrap {
    background: #4c4c4c;
}

#news-about-sub {
    color:#d1d1d1;
    background-color: #5a5a5a;
}
    
#news-about {
    background: #505050;
}

#news-singlenew-title h1 {
    color: #ececec;
}

#news-singlenew-subtitle {
    color: #ececec;
    background-color: #3e3e3e;
}

#news-singlenew-title {
    background-color: #3a3a3a;
}

.section .topic .lastpost .lasttime a, .section .forum .lastpost .lasttime a {
    color: #afaeae;
}

.section .header {
    background: #3c3b3a;
}

.new .meta {
    background: #2a241c;
}

.btn-embed {
    color: #78c95f;
}

.section .topic .title .posted, .section .topic .title .star, .section .forum .title .posted, .section .forum .title .star {
    background-color: #cccccc;
}

#bodyContent p {
    color: #eaf5ea;
}

b, strong {
    color: #fce4c2;
}

h1, h2, h3, h4, h5, h6 {
    color: #cfcfcf;
}

div#content {
    color: #d9d9d9;
}

#bodyContent a, #bodyContent a.external {
    color: #cacaca;
}

.tab-content {
    background-color: #333;
}

.panel .panel-body.colored {
    background-color: #777;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #fff;
    background-color: #777;
}

#h-p-content, #h-p-content a {
    color: #ececec;
}

#h-popup {
    background: #777;
}

#u-nick a.active {
    color: #ececec;
}

#news-comments .n-c-text {
    color: #ececec;
}

#news-single #news-inner-wrap #news-body {
    color: #ececec;
}
}
