    height: 32px;
    cursor: pointer;
    margin-top: 0;
    vertical-align: initial;
    filter: invert(100%);
}


element.style {
}
.header .menuBox .menu .subMenu .subItem.iconHover:hover .hover[data-v-2f3b2e68] {
    opacity: 1;
}
.header .menuBox .menu .subMenu .subItem.iconHover .hover[data-v-2f3b2e68] {
    display: inline-block;
    position: absolute;
    -webkit-transform: translate(-50%,-50%);
    transform: translate(-50%,-50%);
    top: 50%;
    left: 50%;
    opacity: 0;
}

.icon {
    width: 24px;
    height: 24px;
    display: inline-block;
    overflow: hidden;
    vertical-align: middle;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: 50%;
    content: " ";
    margin-top: -2px;
    -webkit-tap-highlight-color: transparent;
    -webkit-transition: all .5s;
    transition: all .5s;
}
button, div, i, li, ul {
    -webkit-tap-highlight-color: transparent;
}
* {
    outline: none!important;
}
用户代理样式表
i {
    font-style: italic;
}
