@-moz-document url-prefix("https://go.sspai.com/") {
/* Matrix 和专题等特殊页面的问题 */
header#header header {
    background-color: #fff !important;
    height: 60px;
    border-bottom: 1px solid #292525 !important;
    transition: all .3s;
}

header#header header h1 {
    font-size: 24px;
    font-size: 1.5rem;
    font-family: -apple-system,BlinkMacSystemFont,PingFang SC,Hiragino Sans GB,Microsoft YaHei,\\5FAE\8F6F\96C5\9ED1,helvetica neue,helvetica,ubuntu,roboto,noto,segoe ui,Arial,sans-serif;
    text-align: center;
    color: #292525;
    margin: 0;
    height: 60px;
    line-height: 60px;
    font-weight: 400;
    position: absolute;
    width: 100%;
}

header#header header .logo {
    display: block;
    position: absolute;
    left: 0;
    z-index: 1;
    background-image: url(https://gitee.com/WhiteMountain/CSS/raw/master/logo_white.ce70a4d2.png);
    background-size: auto 100%;
    height: 38px;
    margin: 11px 0;
    width: 108px;
    font-size: 0;
}

.topic-plaza>header nav {
    /* margin-left: -15px; 这是它的源代码，我甚至不知道这行脑残的 -15px 边距干吗用，去掉就好很多了。 */
}
}

@-moz-document url-prefix("https://sspai.com/series") {
/* 少数派的代码太乱了……每个网页的设计都不一样，但是有些元素又共用。 */
/* 这段代码主要是订阅栏目的界面 */
li, ul {
    padding: 8px;
    margin: 0 auto;
    line-height: normal;
    background: #fff;
}

.header .menuBox .menu.is-active[data-v-2f3b2e68] {
    background: #fff !important;/* 付费订阅页面的专属问题 */
}

element.style {
}
.home-tabs .search[data-v-530d6aa2] {
    position: absolute;

    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
/* 这里的代码绝对有问题，是那个搜索框的，但是我目前没有好的解决方法，但是效果还行。 */
/* 搜索框点击后背景还是会变灰色，目前我找不到相关代码。 */
    margin: 20px;
}
.search-value .el-input__inner {
    background: #fff;
    padding: 0;
    height: 20px;
    color: #8e8787;
    border: none;
}

}

@-moz-document url-prefix("https://sspai.com/special/nomo") {
html, body {
    margin: 0;
    padding: 0;
    background: #fff;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    -webkit-overflow-scrolling: touch;
    font-family: -apple-system, BlinkMacSystemFont, PingFang SC, Hiragino Sans GB, Microsoft YaHei, "\5FAE\8F6F\96C5\9ED1", helvetica neue, helvetica, ubuntu, roboto, noto, segoe ui, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;

}

.header .back_link {
    color: #7d7d7d;

    opacity: 0.8;
    font-size: 16px;
    line-height: 22px;
    text-decoration: none;
    display: flex;
    align-items: center;
    margin-right: 6px;
    font-family: PingFang SC, -apple-system, BlinkMacSystemFont, Hiragino Sans GB, Microsoft YaHei, "\5FAE\8F6F\96C5\9ED1", helvetica neue, helvetica, ubuntu, roboto, noto, segoe ui, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
}

.icon.icon-back {
    background-image: url(http://cdn.sspai.com/special/nomo/assets/images/back.svg);
    width: 20px;
    height: 20px;
    filter:invert(50%);
}

.icon.icon-share_weibo {
    background-image: url(http://cdn.sspai.com/special/nomo/assets/images/share_weibo.svg);
    width: 30px;
    height: 30px;
    position: relative;
    z-index: 1;
    filter:invert(50%);
}

.icon.icon-share_wecaht {
    background-image: url(http://cdn.sspai.com/special/nomo/assets/images/share_wecaht_2.svg);
    width: 30px;
    height: 30px;
    position: relative;
    filter:invert(50%);
}



.body .title_txt {
    margin: 16vh auto;
    max-width: 657px;
    font-size: 20px;
    line-height: 1.4;
    color: #000;
    text-align: center;
    font-family: PingFang SC, -apple-system, BlinkMacSystemFont, Hiragino Sans GB, Microsoft YaHei, "\5FAE\8F6F\96C5\9ED1", helvetica neue, helvetica, ubuntu, roboto, noto, segoe ui, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
}


element.style {
}
.body .cardList .card .name {
    font-size: 22px;
    line-height: 27px;
    color: #000;
    font-family: 'din';
}

.footer .footer_link a {
    color: #000;
    font-size: 14px;
    line-height: 22px;
    font-family: PingFang SC, -apple-system, BlinkMacSystemFont, Hiragino Sans GB, Microsoft YaHei, "\5FAE\8F6F\96C5\9ED1", helvetica neue, helvetica, ubuntu, roboto, noto, segoe ui, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
}


element.style {
}

* {
    outline: none!important;
}
* {
    margin: 0;
    padding: 0;
}
.footer .ssp_text {
    font-family: PingFang SC, -apple-system, BlinkMacSystemFont, Hiragino Sans GB, Microsoft YaHei, "\5FAE\8F6F\96C5\9ED1", helvetica neue, helvetica, ubuntu, roboto, noto, segoe ui, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
    margin-top: 16px;
    color: #000;
    font-size: 12px;
    line-height: 1.54;
}

.footer .ssp_text .ssp_link {
    color: #000;
    margin: 0 10px;
    position: relative;
}


/* 接下来是相机详情页，感谢少数派网页代码的注释（笑） */
.body .nomoDetails {
    color: #000;
}

.body .layer_box {
    position: fixed;
    z-index: 999;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    overflow: auto;
    background: #fff;
}

.body .nomoDetails .content .right .txt {
    font-size: 16px;
    line-height: 22px;
    color: #000;
}

.icon.icon-close {
    background-image: url(http://cdn.sspai.com/special/nomo/assets/images/close.svg);
    width: 32px;
    height: 32px;
    position: absolute;
    right: 21px;
    top: 64px;
    z-index: 1;
    filter:invert(100%);
}
}
