style.css:
@-moz-document domain("live.bilibili.com") {
.room-bg{background:#f2f3f5 !important;}
.live-room-app .app-content .app-body{width:1861px;max-width:none;}
.live-room-app .app-content .app-body .player-and-aside-area{margin-bottom:65px;}
#head-info-vm,.gift-control-panel .right-part .gift-presets,.m-guard-ent,.live-room-app .app-content .app-body .player-and-aside-area .aside-area .rank-list-section{display:none !important;}
#gift-control-vm{height:60px !important;}
.gift-control-panel{height:60px !important;}
.left-part-ctnr .gift-left-part{padding: 5px 0 0 5px !important;;}
.live-room-app .app-content .app-body .player-and-aside-area .aside-area .chat-history-panel {height: calc(100% - 145px);}
}

@-moz-document domain("t.bilibili.com") {
.home-content .right-panel,.user-panel .content .user-pic,.home-content .center-panel .section-block,.home-content .center-panel .card-list .most-viewed-panel,.home-content .center-panel .card-list .feed-card .tab-bar{display:none !important;}
.mini-header{width:1000px;margin:0 auto;border-bottom-right-radius: 5px;border-bottom-left-radius: 5px;}
.nav-link .nav-link-ul .nav-link-item:nth-child(2),
.nav-link .nav-link-ul .nav-link-item:nth-child(3),
.nav-link .nav-link-ul .nav-link-item:nth-child(5),
.nav-link .nav-link-ul .nav-link-item:nth-child(6),
.nav-link .nav-link-ul .nav-link-item:nth-child(7),
.nav-link .nav-link-ul .nav-link-item:nth-child(8){display:none;}
.nav-user-center>div:last-child{display:none;}
.nav-user-center .user-con .item:nth-child(2),
.nav-user-center .user-con .item:nth-child(4),
.nav-user-center .user-con .item:nth-child(6),
.nav-user-center .user-con .item:nth-child(7){display:none;}
#app .home-page .home-container .home-content{position:relative;width:1000px;display:block;}
#app .home-page .home-container .home-content .left-panel{width: 100%;height: 70px;margin: 0;overflow: hidden;display: block;/*display:none !important;*/}
.home-content .user-wrapper{height:70px;width:calc(50% - 5px);float:left;}
.home-content .user-panel,.home-content .user-panel .content{height:70px;width:100%;}
.home-content .user-panel .content .user-head{top:7px;}
.home-content .user-panel .content .user-name{position: absolute;left: 85px;margin: 0;top: 22px;display: block;font-size: 16px;padding: 0;height: 24px;line-height: 24px;}
.home-content .user-panel .content .bottom{position: absolute;right: 10px;margin: 0;top: 16px;height: auto;width: auto;}
.home-content .user-panel .content .bottom .number-part{height:auto;width:50px;}
.home-content .user-panel .content .bottom .number-part .numbers{padding:0;}
.home-content .user-panel .content .bottom .number-part .text{padding:0;}
.home-content .adaptive-scroll{display:block;width:calc(50% - 5px);float:right;height:70px;}
.home-content .live-panel{width:100%;margin:0;padding:0;height:70px;}
.home-content .live-panel .title{padding: 0;float: left;}
.home-content .live-panel .title>span:first-child{width: 28px;display: block;float: left;height: 40px;line-height: 20px;margin: 15px 0 0 10px;font-size: 14px;}
.home-content .live-panel .title .count{line-height:70px;}
.home-content .live-panel .more-button{top:25px;}
.home-content .live-panel .live-panel-item .live-detail{display:none;}
.home-content .live-panel .live-up-list{display:block;}
.home-content .live-panel .live-panel-item{float: left;margin: 13px 0 0 10px;}
#app .home-content .center-panel{margin:0;width:100%;}
.home-content .center-panel .card-list{margin:0;}
.feed-card .card .main-content{width: calc(100% - 140px);margin: 0 50px 0 90px;}
.card .video-container{width:100%}
.card .video-container .text-area{width: calc(100% - 231px);}
.card .video-container .text-area .title{margin:10px 0 0 0;height:20px;line-height:20px;}
.card .video-container .text-area .content{margin:10px 0 0 0;height:60px;line-height:20px;max-height:60px;-webkit-line-clamp:3;}
.card .video-container .text-area .video-container .text-area .view-danmaku{margin:9px 0 0 0;height:18px;line-height:18px;}
.card .imagesbox{width:100%}
.card .imagesbox .zoom-list{width:100% !important;}
.feed-card .card .main-content .button-bar .single-button{width:33.33%;text-align:center;}
}
