style.css:
@-moz-document url-prefix("https://datastock.biz/") {
/*! CSS Used from: https://datastock.biz/css.php?css=public%3Anormalize.css%2Cpublic%3Acore.less%2Cpublic%3Aapp.less&s=4&l=2&d=1587390457&k=edf6cfeed2b8b8213808b1692c79649ca17f77fa */
button,hr,input{overflow:visible;}
[type=checkbox]{box-sizing:border-box;padding:0;}
body{margin:0; background: #101018;}
footer,header,nav{display:block;}
h1{font-size:2em;margin:.67em 0;}
hr{box-sizing:content-box;height:0;}
a{background-color:transparent;-webkit-text-decoration-skip:objects;}
b{font-weight:bolder;}
img{border-style:none;}
button,input,textarea{font-family:sans-serif;font-size:100%;line-height:1.15;margin:0;}
button{text-transform:none;}
[type=submit],button{-webkit-appearance:button;}
[type=submit]::-moz-focus-inner,button::-moz-focus-inner{border-style:none;padding:0;}
[type=submit]:-moz-focusring,button:-moz-focusring{outline:ButtonText dotted 1px;}
textarea{overflow:auto;}
[hidden]{display:none;}
button,input,textarea{font-family:"Open Sans",BlinkMacSystemFont,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif;line-height:1.5;}
img{max-width:100%;height:auto;}
b{font-weight:700;}
.block-filterBar {padding: 12px 12px;font-size: 12px;color: #d6d9e0;background: #1d1d26;border-bottom: 1px solid #586171;}
a{color:#fafafa;text-decoration:none;}
.block--messages .message, .block--messages .block-row {color: #dae1f3;background: #1d1d26;border: 0px solid #4a5568;border-radius: 4px;}
.block-header {padding: 12px 12px;margin: 0;font-weight: 400;text-decoration: none;font-size: 15px;color: #d6d9e0;font-weight: 700;background: rgb(35, 35, 45);border: 0px solid rgba(255,255,255,0);}
a:hover{color:#fefefe;text-decoration:underline;}
*{box-sizing:border-box;}
body{overflow-y:scroll!important;}
[data-xf-click],a[tabindex]{cursor:pointer;}
[dir=auto]{text-align:left;}
textarea{word-wrap:normal;}
img{-ms-interpolation-mode:bicubic;}
.has-pointer-nav :focus{outline:0;}
.has-pointer-nav ::-moz-focus-inner{border:0;}
.has-pointer-nav .iconic>input:focus+i:before,.has-pointer-nav .iconic>input:focus+i:after{outline:0;}
.u-concealed{text-decoration:inherit!important;color:inherit!important;}
a.u-concealed:hover{text-decoration:underline!important;}
.u-dimmed{color:#abb4c8;text-align: left;}
.u-muted{color:#7c869d;}
.u-dt[title]{border:none;text-decoration:none;}
.u-indentDepth1{text-indent:1em;}
.u-hidden{display:none;}
.u-hidden.u-hidden--transition{display:none;opacity:0;-webkit-transition:all .25s ease, -xf-opacity .25s ease;transition:all .25s ease, -xf-opacity .25s ease;overflow-y:hidden;height:0;-webkit-transition-property:all,-xf-height;transition-property:all,-xf-height;}
.p-breadcrumbs-wrap::before {content: '';display: block;position: absolute;top: 0;left: 0;right: 0;bottom: 0;border-width: 0px;border-style: solid;border-color: rgba(186,191,201,0.25);border-radius: inherit;pointer-events: none;z-index: 2;}
.u-srOnly{position:absolute;height:1px;width:1px;margin:-1px;padding:0;border:0;clip:rect(0 0 0 0);overflow:hidden;}
.u-bottomFixer{position:fixed;left:0;right:0;bottom:0;z-index:800;pointer-events:none;}
.u-anchorTarget{display:block;height:0;width:0;visibility:hidden;pointer-events:none;position:absolute;}
.pairs{padding:0;margin:0;overflow:hidden;}
.pairs>dt{padding:0;margin:0;color:#7c869d;}
.pairs>dt:after{content:":";}
.pairs>dd{padding:0;margin:0;}
.pairs.pairs--inline{display:inline;}
.block-filterBar {font-size: 12px;color: #d6d9e0;background: #1d1d26;}
.blockMessage {padding: 12px 12px;color: #dae1f3;background: #1d1d26;border: 1px solid #4a5568;border-radius: 4px;}
.pairs.pairs--inline>dt,.pairs.pairs--inline>dd{display:inline;}
.pairs.pairs--justified:before,.pairs.pairs--justified:after{content:" ";display:table;}
.pairs.pairs--justified:after{clear:both;}
.pairs.pairs--justified>dt{float:left;max-width:100%;margin-right:6px;}
.pairs.pairs--justified>dd{float:right;text-align:right;max-width:100%;}
.pairs.pairs--rows{display:inline-table;margin-right:6px;}
.pairs.pairs--rows:last-of-type{margin-right:0;}
.pairs.pairs--rows>dt{display:table-row;font-size:80%;}
.pairs.pairs--rows>dt:after{content:'';display:none;}
.pairs.pairs--rows>dd{display:table-row;}
.tooltip--preview .tooltip-content-inner .tooltip-content-cover {background: transparent;background: linear-gradient(to bottom, rgba(45,48,55,0) 160px, #23232d 200px);}
.listInline{list-style:none;margin:0;padding:0;}
.listInline>li{display:inline;margin:0;padding:0;}
.listInline.listInline--comma>li:after{content:", ";}
.listInline.listInline--comma>li:last-child:after{content:"";display:none;}
.listInline.listInline--bullet>li:before{content:"\00B7\20";}
.listInline.listInline--bullet>li:first-child:before{content:"";display:none;}
.block{margin-bottom:20px;}
.block-container{color:#dae1f3;background:#1d1d26;border:0px solid #4a5568;border-radius:4px;}
@media (min-width:650px){
.block-container:not(.block-container--noStripRadius)>:first-child{border-top-left-radius:3px;border-top-right-radius:3px;}
.block-container:not(.block-container--noStripRadius)>:last-child{border-bottom-left-radius:3px;border-bottom-right-radius:3px;}
}
.block-header{padding:12px 12px;margin:0;font-weight:400;text-decoration:none;font-size:15px;color:#d6d9e0;font-weight:700;background:rgb(35, 35, 45);border:0px solid rgba(255,255,255,0);}
.block-header:before,.block-header:after{content:" ";display:table;}
.block-header:after{clear:both;}
.block-header a{color:inherit;text-decoration:none;}
.block-header a:hover{text-decoration:underline;}
.block-minorHeader{padding:12px 12px;margin:0;font-weight:400;text-decoration:none;font-size:inherit;color:#d6d9e0;font-weight:700;}
.block-minorHeader:before,.block-minorHeader:after{content:" ";display:table;}
.block-minorHeader:after{clear:both;}
.block-minorHeader a{color:inherit;text-decoration:none;}
.block-minorHeader a:hover{text-decoration:underline;}
.block-body{list-style:none;margin:0;padding:0;}
.block-row{margin:0;padding:12px 12px;}
.block-row:before,.block-row:after{content:" ";display:table;}
.block-row:after{clear:both;}
.block-row.block-row--minor{font-size:12px;}
.block-footer{padding:12px 12px;font-size:12px;color:#abb4c8;background:#1a1a23;border-top:1px solid #444e5f;}
.block-footer:before,.block-footer:after{content:" ";display:table;}
.block-footer:after{clear:both;}
.block-footer:not(.block-footer--split) .block-footer-counter{float:left;}
.block-row p:first-child{margin-top:0;}
.block-row p:last-child{margin-bottom:0;}
@media (max-width:650px){
.block-container{margin-left:-10px;margin-right:-10px;border-radius:0;border-left:none;border-right:none;}
}
.button,a.button{display:inline-block;display:inline-flex;align-items:center;justify-content:center;text-decoration:none;cursor:pointer;border:1px solid transparent;white-space:nowrap;-webkit-transition:background-color .25s ease;transition:background-color .25s ease;font-size:12px;border-radius:4px;padding-top:7px;padding-right:10px;padding-bottom:7px;padding-left:10px;text-align:center;color:#fff;background:#919cb0;border-color:#828fa5 #a0a9bb #a0a9bb #828fa5;}
.button:not(.button--splitTrigger):hover,a.button:not(.button--splitTrigger):hover,.button:not(.button--splitTrigger):focus,a.button:not(.button--splitTrigger):focus,.button:not(.button--splitTrigger):active,a.button:not(.button--splitTrigger):active{background-color:#93a4c3;}
.button.button--primary{color:#fff;background:#63ba5f;min-width:80px;border-color:#52b24d #74c271 #74c271 #52b24d;}
.button.button--primary:not(.button--splitTrigger):hover,.button.button--primary:not(.button--splitTrigger):focus,.button.button--primary:not(.button--splitTrigger):active{background-color:#65cd61;}
.button.button--cta,a.button.button--cta{color:#fff;background:#63ba5f;border-color:#52b24d #74c271 #74c271 #52b24d;}
.button.button--cta:not(.button--splitTrigger):hover,a.button.button--cta:not(.button--splitTrigger):hover,.button.button--cta:not(.button--splitTrigger):focus,a.button.button--cta:not(.button--splitTrigger):focus,.button.button--cta:not(.button--splitTrigger):active,a.button.button--cta:not(.button--splitTrigger):active{background-color:#65cd61;}
.button.button--scroll,a.button.button--scroll{background:#1d1d26;padding:5px 8px;box-shadow:1px 2px 2px 0 rgba(0,0,0,0.25);}
.button.button--icon>.button-text:before,a.button.button--icon>.button-text:before{font-family:'Font Awesome 5 Pro';font-size:inherit;font-style:normal;font-weight:400;text-rendering:auto;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;}
.button.button--icon>.button-text:before,a.button.button--icon>.button-text:before{font-size:120%;vertical-align:-.1em;display:inline-block;margin:-0.255em 6px -0.255em 0;}
.button.button--icon--write>.button-text:before,a.button.button--icon--write>.button-text:before{content:"\f044";display:inline-block;width:1em;}
.button.button--icon--reply>.button-text:before{content:"\f3e5";display:inline-block;width:1.13em;}
.button.button--icon--search>.button-text:before{content:"\f002";display:inline-block;width:.93em;}
.button.button--icon--bolt>.button-text:before,a.button.button--icon--bolt>.button-text:before{content:"\f0e7";display:inline-block;width:.5em;}
.u-scrollButtons{position:fixed;bottom:30px;right:5px;z-index:900;-webkit-transition:opacity .25s ease;transition:opacity .25s ease;opacity:0;display:none;}
.u-scrollButtons .button{display:block;}
.input{font-size:13px;color:#dae1f3;background:#3b3f48;border:1px solid #556177;border-bottom-color:#444e5f;border-radius:4px;padding:6px;display:block;width:100%;line-height:1.5;text-align:left;word-wrap:break-word;-webkit-appearance:none;-moz-appearance:none;appearance:none;-webkit-transition:all .25s ease;transition:all .25s ease;}
.input::-webkit-input-placeholder{color:rgba(218,225,243,0.4);}
.input::-moz-placeholder{color:rgba(218,225,243,0.4);}
.input:-moz-placeholder{color:rgba(218,225,243,0.4);}
.input:-ms-input-placeholder{color:rgba(218,225,243,0.4);}
.input:focus{outline:0;background:#424650;}
.input:focus::-webkit-input-placeholder{color:rgba(218,225,243,0.5);}
.input:focus::-moz-placeholder{color:rgba(218,225,243,0.5);}
.input:focus:-moz-placeholder{color:rgba(218,225,243,0.5);}
.input:focus:-ms-input-placeholder{color:rgba(218,225,243,0.5);}
textarea.input{min-height:0;max-height:400px;max-height:75vh;resize:vertical;}
textarea.input.input--fitHeight{resize:none;}
@media (max-width:568px){
.input{font-size:16px;}
}
.iconic{display:inline-block;position:relative;}
.node-subNodeFlatList {display: block;grid-gap: 5px;grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));}
.iconic>input{height:1px;width:1px;margin:-1px;padding:0;border:0;clip:rect(0 0 0 0);overflow:hidden;position:absolute;left:0;width:auto;height:auto;}
.iconic>input+i{display:inline-block;min-width:1em;height:.9em;text-align:left;}
.iconic>input+i:before,.iconic>input+i:after{font-family:'Font Awesome 5 Pro';font-size:inherit;font-style:normal;font-weight:inherit;text-rendering:auto;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;position:absolute;left:0;top:0;opacity:1;}
.iconic>input+i:after{opacity:0;}
.iconic>input:disabled+i:before{opacity:.3;}
.iconic>input:disabled:checked+i:after{opacity:.3;}
.iconic>input:checked+i:before{opacity:0;}
.iconic>input:checked+i:after{opacity:1;}
.iconic>input:focus+i:before,.iconic>input:focus+i:after{outline:Highlight solid 2px;-moz-outline-radius:5px;}
@media (-webkit-min-device-pixel-ratio:0){
.iconic>input:focus+i:before,.iconic>input:focus+i:after{outline:-webkit-focus-ring-color auto 5px;}
}
.iconic .iconic-label:before{content:'';display:inline-block;width:.5em;}
.iconic>input[type=checkbox]+i:before{content:"\f0c8";display:inline-block;width:.875em;}
.iconic>input[type=checkbox]+i:after{content:"\f14a";display:inline-block;width:.875em;}
.u-inputSpacer{margin-top:6px;}
.inputGroup{display:flex;align-items:stretch;max-width:100%;}
.inputGroup .inputGroup-text{flex-grow:0;display:flex;align-items:center;white-space:nowrap;vertical-align:middle;padding:0 6px;}
.inputGroup .inputGroup-text:first-child{padding-left:0;}
.inputGroup .input{flex-shrink:1;min-width:0;}
.tabs--standalone {padding: 0;font-weight: 400;border: 1px solid #4a5568;color: #d6d9e0;background: #31323c;font-size: 0;}
@media (max-width:540px){
.inputGroup .input:not(.input--autoSize):not(.input--numberNarrow){width:100%;}
}
.badgeContainer:after{display:inline-block;padding:2px 4px;margin:-2px 0;font-size:80%;line-height:1;font-weight:400;color:#4f5766;background:#e1e8f8;border-radius:2px;}
.badgeContainer:after{content:attr(data-badge);display:none;}
.menuTrigger{cursor:pointer;}
.message-responseRow {margin-top: -1px;background: #1d1d26;border: 1px solid #444e5f;padding: 6px;}
.menuTrigger:after{font-family:'Font Awesome 5 Pro';font-size:inherit;font-style:normal;font-weight:400;text-rendering:auto;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;content:"\f0d7";font-weight:900;margin-left:.2em;unicode-bidi:isolate;}
.menu{display:none;opacity:0;-webkit-transition:all .25s ease, -xf-opacity .25s ease;transition:all .25s ease, -xf-opacity .25s ease;position:absolute;z-index:200;margin:8px 0 0;min-width:240px;max-width:320px;border-radius:3px;box-shadow:0 5px 10px 0 rgba(0,0,0,0.35);}
.menu.menu--structural{margin-top:0;}
.fr-box.fr-basic .fr-wrapper {background: #1d1d26;}
.menu.menu--wide{width:350px;max-width:calc(100% - 10px);}
.menu.menu--medium{width:300px;max-width:calc(100% - 10px);}
.menu-content{margin:0;padding:0;list-style:none;text-align:left;font-size:12px;color:#dae1f3;background:#23232d;border-radius:3px;border-top:3px solid #919cb0;}
.menu-content>:last-child{border-bottom-left-radius:3px;border-bottom-right-radius:3px;}
.menu-header{padding:12px 15px;margin:0;font-weight:400;text-decoration:none;font-size:14px;color:#dae1f3;background:#3b3f48;border-bottom:1px solid #444e5f;}
.menu-header:before,.menu-header:after{content:" ";display:table;}
.menu-header:after{clear:both;}
.menu-row{margin:0;padding:6px 15px;}
.menu-row:before,.menu-row:after{content:" ";display:table;}
.menu-row:after{clear:both;}
.menu-row:empty{padding:0;}
.menu-linkRow{display:block;padding:6px 15px 6px 12px;border-left:3px solid transparent;color:#dae1f3;text-decoration:none;}
.menu-linkRow:hover,.menu-linkRow:focus{color:#fefefe;text-decoration:none;background:rgba(145,156,176,0.1);border-left-color:rgba(145,156,176,0.5);}
.menu-linkRow:hover:focus,.menu-linkRow:focus:focus{outline:0;}
.menu-separator{margin:0 7.5px;padding:0;border:none;border-top:1px solid #444e5f;}
.menu-footer{padding:12px 15px;font-size:12px;color:#abb4c8;background:#3b3f48;border-top:1px solid #444e5f;}
.menu-footer:before,.menu-footer:after{content:" ";display:table;}
.menu-footer:after{clear:both;}
.menu-footer:not(.menu-footer--split) .menu-footer-controls{float:right;}
.menu-footer.menu-footer--split{display:flex;align-items:center;}
.menu-footer.menu-footer--split .menu-footer-main{flex-grow:1;}
.menu-footer.menu-footer--split .menu-footer-opposite{margin-left:auto;}
.offCanvasMenu{display:none;position:fixed;top:0;bottom:0;left:0;right:0;z-index:500;-webkit-transition:none .5s ease;transition:none .5s ease;-webkit-transform:scale(1);-ms-transform:scale(1);transform:scale(1);-webkit-tap-highlight-color:rgba(0,0,0,0);}
.offCanvasMenu a{-webkit-tap-highlight-color:initial;}
.offCanvasMenu .offCanvasMenu-closer{float:right;cursor:pointer;text-decoration:none;-webkit-tap-highlight-color:initial;padding:12px;margin:-12px;}
.offCanvasMenu .offCanvasMenu-closer:hover{text-decoration:none;}
.offCanvasMenu .offCanvasMenu-closer:after{font-family:'Font Awesome 5 Pro';font-size:inherit;font-style:normal;font-weight:400;text-rendering:auto;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;content:"\f00d";}
.offCanvasMenu-backdrop{position:absolute;top:0;bottom:0;left:0;right:0;background:rgba(0,0,0,0.25);opacity:0;-webkit-transition:all .5s ease-in-out;transition:all .5s ease-in-out;}
.offCanvasMenu-content{position:relative;width:280px;max-width:85%;height:100%;padding-bottom:44px;overflow:auto;-webkit-transition:all .5s ease-in-out;transition:all .5s ease-in-out;-webkit-overflow-scrolling:touch;box-shadow:2px 0 5px 0 rgba(0,0,0,0.25);-webkit-transform:translateX(-280px);-ms-transform:translateX(-280px);transform:translateX(-280px);}
.offCanvasMenu-header{padding:12px;margin:0;font-size:18px;font-weight:400;}
.offCanvasMenu-header:before,.offCanvasMenu-header:after{content:" ";display:table;}
.offCanvasMenu-header:after{clear:both;}
.offCanvasMenu-header a{color:inherit;text-decoration:none;}
.offCanvasMenu-header a:hover{text-decoration:underline;}
.offCanvasMenu-separator{padding:0;margin:0;border:none;border-top:1px solid transparent;}
.offCanvasMenu-link{display:block;padding:12px;font-size:14px;text-decoration:inherit;}
.offCanvasMenu-link:hover{text-decoration:inherit;}
.offCanvasMenu-linkHolder{display:flex;}
.offCanvasMenu-linkHolder .offCanvasMenu-link{flex-grow:1;}
.offCanvasMenu-linkHolder .offCanvasMenu-link:hover{background:none;}
.offCanvasMenu--nav .offCanvasMenu-content{color:#d6d9e0;background:#4f5766;}
.offCanvasMenu--nav .offCanvasMenu-content a{color:inherit;}
.offCanvasMenu--nav .offCanvasMenu-header{background:rgba(0,0,0,0.1);border-bottom:1px solid rgba(0,0,0,0.2);}
.offCanvasMenu--nav .offCanvasMenu-separator{border-top-color:rgba(0,0,0,0.2);}
.offCanvasMenu--nav .offCanvasMenu-linkHolder{text-decoration:none;}
.offCanvasMenu--nav .offCanvasMenu-linkHolder:hover{background:rgba(0,0,0,0.04);}
.tabs{display:flex;}
.avatar{display:inline-flex;justify-content:center;align-items:center;border-radius:2px;vertical-align:top;overflow:hidden;}
.avatar img{background-color:#2d3037;}
.avatar.avatar--default.avatar--default--dynamic{font-family:'Arial',sans-serif;font-weight:normal;text-align:center;text-decoration:none!important;line-height:1;-webkit-user-select:none;-moz-user-select:none;-ms-user-select:none;user-select:none;}
.avatar:hover{text-decoration:none;}
.avatar.avatar--xxs{width:24px;height:24px;font-size:14px;}
.avatar.avatar--xs{width:32px;height:32px;font-size:19px;}
.avatar.avatar--s{width:48px;height:48px;font-size:29px;}
.avatar img:not(.cropImage){text-indent:100%;overflow:hidden;white-space:nowrap;word-wrap:normal;display:block;border-radius:inherit;width:100%;height:100%;}
.avatar:not(a){cursor:inherit;}
.contentRow{display:flex;}
.contentRow-figure{vertical-align:top;white-space:nowrap;word-wrap:normal;text-align:center;}
.contentRow-figure img,.contentRow-figure .avatar{vertical-align:bottom;}
.contentRow-main{flex:1;min-width:0;vertical-align:top;padding-left:12px;}
.contentRow-main:before{content:'';display:block;margin-top:-.18em;}
.contentRow-main.contentRow-main--close{padding-left:6px;}
.contentRow-lesser{font-size:12px;}
.contentRow-faderContainer{position:relative;overflow:hidden;}
.contentRow-faderContent{max-height:150px;overflow:hidden;}
.contentRow-fader{position:absolute;top:87px;left:0;right:0;height:75px;background:transparent;background:linear-gradient(to bottom, rgba(45,48,55,0) 0%, #1d1d26 80%);}
.contentRow-minor{font-size:12px;color:#7c869d;}
.contentRow-extra{float:right;padding-left:6px;font-size:11px;}
.hScroller{overflow-y:hidden;position:relative;-webkit-tap-highlight-color:rgba(0,0,0,0);-webkit-overflow-scrolling:touch;}
.hScroller-scroll{display:block;white-space:nowrap;word-wrap:normal;overflow-x:hidden;}
.hScroller-scroll.is-calculated{overflow-x:scroll;padding-bottom:30px;margin-bottom:-30px;}
.hScroller-action{position:absolute;top:0;bottom:0;width:40px;padding:0 5px;display:none;cursor:pointer;}
.hScroller-action.is-active{display:block;display:flex;align-items:center;}
.hScroller-action:after{font-family:'Font Awesome 5 Pro';font-size:inherit;font-style:normal;font-weight:400;text-rendering:auto;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;font-size:120%;}
.hScroller-action.hScroller-action--start{left:0;justify-content:flex-start;text-align:left;}
.hScroller-action.hScroller-action--start:after{content:"\f053";display:inline-block;width:.63em;}
.hScroller-action.hScroller-action--end{right:0;justify-content:flex-end;text-align:right;}
.hScroller-action.hScroller-action--end:after{content:"\f054";display:inline-block;width:.63em;}
.label-append{display:inline-block;}
.far{font-family:'Font Awesome 5 Pro';position:relative;font-weight:400;}
.fas{font-family:'Font Awesome 5 Pro';position:relative;font-weight:900;}
.fas,.far{-moz-osx-font-smoothing:grayscale;-webkit-font-smoothing:antialiased;display:inline-block;font-style:normal;font-variant:normal;text-rendering:auto;line-height:1;}
.fa-arrow-up:before{content:"\f062";}
.fa-caret-right:before{content:"\f0da";}
.fa-chevron-double-right:before{content:"\f324";}
.fa-comments:before{content:"\f086";}
.fa-edit:before{content:"\f044";}
.fa-file-alt:before{content:"\f15c";}
.fa-globe:before{content:"\f0ac";}
.fa-rss:before{content:"\f09e";}
.fa-search:before{content:"\f002";}
.fa-smile-plus:before{content:"\f5b9";}
