.fsp .tabGroup:last-child .tabs .hScroller-action--end{justify-content:unset;}
.fsp .tabGroup .item{border-bottom:1px solid #3e4656;padding:3px;margin-top:4px;}
.fsp .tabGroup .item:first-child{margin-top:0;}
.notice.notice--primary {color: #dae1f3;background: #363941;}
.fsp .tabGroup .item .icon{color:#d6d9e0;margin-right:2px;font-size:12px;}
.fsp .tabs--list{font-size:15px;margin-bottom:5px;color:#919cb0;min-height:30px;width:100%;display:block;}
.fsp .tabs--scroller .hScroller-action{color:#919cb0;}
.fsp .tabs--scroller .hScroller-action:hover{color:#d6d9e0;}
.fsp .tabs .tab{padding-bottom:5px;margin-right:10px;white-space:nowrap;cursor:pointer;}
.fsp .tabs .tab:not(.is-selected):hover{border-bottom:3px solid #353941;}
.fsp .tabs .tab:last-child{margin-right:0;}
.fsp .tabs .tab.is-selected{border-bottom:3px solid #919cb0;}
.fsp .overflowed{overflow:hidden;white-space:nowrap;word-wrap:normal;text-overflow:ellipsis;box-sizing:border-box;}
@media (max-width:900px){
.fsp{display:block;}
.fsp .refreshButton{display:none;}
.fsp .tabs--list{font-size:13px;}
.fsp .tabGroup{display:block;width:100%;margin-bottom:5px;}
.fsp .tabGroup:last-child{margin-bottom:0;}
.fsp .tabGroup:last-child .tabs{padding-right:0;}
.fsp .tabGroup:last-child .tabs .hScroller-action--end{justify-content:flex-end;}
}
