style.css:
@-moz-document url-prefix("https://time.is/") {
/*==============*/
/* Time.is Dark */
/* v1.0         */
/*==============*/

body
{
   background: #111111 !important;
   color: #AAAAAA !important;
}

#locw
{
color: #EEEEEE !important;
}

a:link
{
color: #00FFFF;
}

a:hover
{
color: #FFFF00;
}

a:active
{
color: #00FF00;
}

a:visited
{
color: #FF00FF;
}

.re-ad-h1
{
color: #EEEEEE !important;
}

.re-ad-button
{
  filter:invert(1)
}

.social_buttons
{
  filter:invert(1);
}

.fb-button
{
   filter: hue-rotate(180deg);
}

.twitter-button
{
   filter: hue-rotate(180deg);
}

.s1, .s2, .s3, .s4, .s5, .s6, .s7, .s8, .s9, .s10, .s11, .s12, .s13, .s14, .s15, .s16
{
 background: none !important;
 color: #000000 !important;
}

.s1:hover, .s2:hover, .s3:hover, .s4:hover, .s5:hover, .s6:hover, .s7:hover, .s8:hover, .s9:hover, .s10:hover, .s11:hover, .s12:hover, .s13:hover, .s14:hover, .s15:hover, .s16:hover
{
 background: none !important;
 color: #FFFFFF !important;
}


#time-7, #time-6, #time-5, #time-4, #time-3, #time-2, #time-1, #time-0
{
background: #333333;
border: none;
}

#time-7:hover, #time-6:hover, #time-5:hover, #time-4:hover, #time-3:hover, #time-2:hover, #time-1:hover, #time-0:hover
{
color: #00AA00;
}

.time, .monospace
{
color: #EEEEEE !important;
}

.txtin
{
background-color: #000000 !important;
color: #888888 !important;
}

.jvectormap-container
{
filter: hue-rotate(-45deg)
}

section h2 span
{
color:#00AAFF !important;
}

.neg_diff
{
border-right:4px solid #00AAFF !important;
text-align:right;
}

::selection
{
background:#00CCFF !important;
color:#000000 !important;
}

section#largest_cities h2 a:link, section#largest_cities h2 a:visited
{
font-weight:900;
font-size:82px;
line-height:80px;
color:#00AAFF;
}
}
