style.css:
@-moz-document regexp(".*dndbeyond.com\\/profile\\/.+\\/characters\\/\\d+[^\\/builder]"), regexp(".*dndbeyond.com/characters/\\d+") {
/* Fix the backdrop position, and add a gradient */
body#site {
	background-position-y: 116px !important;
	background-size: cover !important;
	background-color: #333 !important;
}

.ct-character-sheet::before {
	background-image: linear-gradient(rgba(0, 0, 0, 0.80), rgba(0, 0, 0, 0));
	background-color: transparent;
	height: 600px
}

@media screen and (max-width: 1024px) {
	.ct-character-sheet::before {
		background-image: linear-gradient(rgba(0, 0, 0, 1), rgba(0, 0, 0, 0));
		height: 100px
	}
}

@media screen and (max-width: 768px) {
	#site-main,
	body {
		background-color: #222
	}
}

.ct-saving-throws-box__abilities,
.ct-saving-throws-box__info,
.ct-tooltip,
.ct-senses,
.ct-proficiency-groups__group-items,
.ct-skills__col--skill,
.ct-signed-number__number,
.ct-attack-table,
.ct-actions__attacks-per-action,
.ct-actions-list__basic,
.ct-feature-snippet,
.ct-actions-list__spells,
.ct-spells-level__spells-col,
.ct-spells-spell__label,
.ct-spells-spell__range,
.ct-spells-spell__damage,
.ct-spells-level-casting__slot-group-label,
.ct-spells-spell__save-value,
input.ct-spells-filter__input,
.ct-tab-list__nav-item:hover,
.ct-equipment-overview__weight-carried,
.ct-inventory__row-header,
.ct-inventory-item__action,
.ct-inventory-item__weight,
.ct-inventory-item__quantity,
.ct-inventory-item__cost,
.ct-inventory-item__location,
.ct-content-group__content,
.ct-description__traits,
.ddbc-ability-summary__primary,
.ct-proficiency-bonus-box__value,
#content,
.ddbc-tab-list__nav-item--is-active,
.ddbc-tab-list__nav-item:hover,
span.ddbc-spell-name,
.ct-sidebar__inner,
.ddbc-collapsible--minimal > .ddbc-collapsible__header,
.content-container a:visited,
.content-container a:active,
a.ct-campaign-pane__character-name-link {
	color: #c9c8c5;
}

.ct-proficiency-bonus-box__heading,
.ct-proficiency-bonus-box__label,
.ct-speed-box__label {
	color: #838383;
}

.ct-combat__summary-label--outline,
.ct-inspiration__label {
	color: #dfdada;
	text-shadow: 1px 1px 0 #333, -1px -1px 0 #333, -1px 1px 0 #333, 1px -1px 0 #333;
}

.ddbc-no-proficiency-icon,
.ct-feats-manage-pane__feat-content{
	background-color: #393939 !important;
	border-color: #737373;
	border-style: solid;
}

.ddbc-proficiency-icon {
	border: #fff solid 1px;
	border-radius: 50%;
	background: white;
}

.ct-button__content {
	color: black;
	font-weight: 900;
	font-size: 10px;
}

.ct-tab-list__nav-item--active, .ct-character-sheet--dice-enabled .integrated-dice__container {
	color: #ddd;
}

.ddbc-item-name--rarity-common {
	color: #b9b9b9;
}

*::-webkit-scrollbar {
	width: 1em !important;
	background-color: rgba(98, 98, 98, .34);
}

*::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
}

*::-webkit-scrollbar-thumb {
	background-color: #626262f7 !important;
	outline: 1px solid #3d4044 !important;
}

.ct-sidebar__pane-content,
.ct-sidebar__pane-gap,
.ct-editor-box,
.ct-component-carousel__placeholders {
	background-color: rgba(34, 34, 34, .95);
}

.ct-sidebar__pane-top svg path[fill="#FEFEFE"],
.ct-sidebar__pane-bottom svg path[fill="#FEFEFE"] {
	fill: rgba(34, 34, 34, .95);
}

.ct-item-detail__tag,
.ddbc-collapsible__header,
.ct-spell-detail__tag {
	background-color: #131313
}

path[fill="#FEFEFE"],
polygon[fill="#FEFEFE"] {
	fill: rgba(23, 23, 23, .95);
}

.ct-mobile-divider__repeat svg path[fill="#FEFEFE"],
.ct-mobile-divider__edge svg path[fill="#FEFEFE"] {
	fill: #333
}

path[fill="#242528"],
circle[fill="#383838"],
path[fill="#383838"] {
	fill: white;
}

.ct-spell-slot-manager__group-level-name {
	font-size: 10px;
}

.i-req-attunement,
.i-concentration,
.ddbc-combat-attack__icon,
.ddbc-healing-icon__icon,
.ddbc-note-components__component--aoe-icon,
.ddbc-damage-type-icon,
.ct-condition-manage-pane__condition-icon,
.ddbc-manage-icon:hover .ddbc-manage-icon__icon {
	filter: invert(1);
}

.site .ct-theme-button--outline.ct-button,
.ct-condition-manage-pane__condition-content,
.ct-equipment-manage-pane__item > .ddbc-collapsible__content {
	background: #2d2d2d;
	border-color: #535353;
	box-shadow: none;
}

.site .ct-theme-button--outline.ct-button:hover,
.site .ct-theme-button--outline.ct-button:active,
.site .ct-theme-button--outline.ct-button:focus {
	background: #1a1a1a
}

.ct-button__content {
	color: white;
}

.ddbc-tab-options--layout-pill .ddbc-tab-options__header-heading:not(.ddbc-tab-options__header-heading--is-active),
.ct-character-sheet input[type=number],
input[type="text"],
input[type="number"],
input[type="search"],
input[type="password"],
input[type="email"],
textarea,
select,
.ct-filter__query[type=search] {
	background: #2d2d2d;
	color: #c6c6c6;
}

.ddbc-tab-options--layout-pill .ddbc-tab-options__header-heading:not(.ddbc-tab-options__header-heading--is-active):hover,
input[type="text"]:hover,
input[type="number"]:hover,
input[type="search"]:hover,
input[type="password"]:hover,
input[type="email"]:hover,
textarea:hover,
select:hover,
.ct-health-summary__hp-item-input {
	background: #3e3e3e;
	color: #c6c6c6;
}

.ct-filter__query[type=search] {
	background: 10px #2d2d2d url("https://www.dndbeyond.com/Content/Skins/Waterdeep/images/icons/search-grey.svg") no-repeat;
	background-size: 15px;
}

.ct-filter__query[type=search]:focus,
.ct-filter__query[type=search]:hover {
	background: 10px #3e3e3e url("https://www.dndbeyond.com/Content/Skins/Waterdeep/images/icons/search-grey.svg") no-repeat;
	background-size: 15px;
}

.site .ct-button.ct-theme-button--filled:disabled {
	background-color: #444;
}

.site .ct-button.ct-theme-button--filled:disabled:hover,
.site .ct-button.ct-theme-button--outline:disabled,
.site .character-button-block-large:disabled,
.site .character-button-block-oversized:disabled,
.site .character-button-block-small:disabled,
.site .character-button-block:disabled,
.site .character-button-large:disabled,
.site .character-button-medium:disabled,
.site .character-button-modal-cancel:disabled,
.site .character-button-modal:disabled,
.site .character-button-outline:disabled,
.site .character-button-oversized:disabled,
.site .character-button-small:disabled,
.site .character-button:disabled,
.site .character-button:disabled:hover {
	background-color: #151515;
}

.ct-spell-manager__spell > .ddbc-collapsible__content {
	background-color: #2d2d2d;
}

.ct-sidebar td {
	background-color: #424242;
}

.ct-sidebar tr:nth-child(odd) td {
	background-color: #2f2f2f;
}

.ct-sidebar thead td,
.ct-sidebar thead th,
.ddbc-ability-score-manager__component-value {
	background-color: #2b2a2a;
	color: #d9d9d9;
}

select.ct-select {
	color: black;
}

a {
	color: #f9cca1;
}

.character-select,
.ct-select {
	background: #373737;
	color: #d9d9d9 !important;
}

.ddbc-creature-block,
.ct-vehicle-block__block {
	background: #1c1c1c;
}

.ddbc-creature-block__name,
.ddbc-creature-block__attribute,
.ddbc-creature-block__tidbit,
.ct-vehicle-block__name,
.ct-vehicle-block__attributes,
.ct-vehicle-block__abilities,
.ct-vehicle-block__tidbit,
.ct-vehicle-block__section-header {
	color: #d9d9d9;
	border-color: #d9d9d9;
}

.ddbc-creature-block:after,
.ddbc-creature-block:before,
.ddbc-creature-block__separator-img {
	filter: saturate(0);
}

.ct-combat-tablet__initiative-label {
	text-shadow: 1px 1px 0 #220404, -1px -1px 0 #220404, -1px 1px 0 #220404, 1px -1px 0 #220404;
	color: #fff;
}

.ct-combat-tablet__cta-button,
.ct-character-header-tablet__button {
	background-color: #1c1c1c;
	color: #eeebeb;
}

.ct-main-mobile__saving-throws,
.ct-mobile-divider__label,
.ct-main-mobile .ct-senses,
.ct-features-mobile,
.ct-spells-mobile,
.ct-sidebar__mask,
.ct-proficiency-groups-mobile,
.ct-description-mobile .ct-description,
.ct-notes-mobile .ct-notes,
.ct-extras-mobile,
.ct-actions-mobile .ddbc-tab-options,
.ct-skills-mobile .ct-skills {
	background: #333;
}
span.ct-spells-filter__adv-filter-button {
    color: #262626;
}

.ct-slot-manager__slot--used:before {
    height: 12px !important;
    margin-top: 3px !important;
    width: 12px !important;
}
}

@-moz-document regexp(".*dndbeyond.com/(campaigns|characters)/?") {
body {
	background-color: #333 !important;
}

.page-title,
.ddb-characters-listing-header-primary-title,
.ddb-characters-listing-actions__sort-label{
		color: #ddd;
}

.ddb-characters-listing-actions__search-box {
	background: #111;
	border: 1px solid #444;
}

.ddb-campaigns-character-card-footer {
	
	border-top: 1px solid #111;
	background: #222
}

.ddb-campaigns-character-card{
	background: #222;
	border: 1px solid #111;
}
.ddb-campaigns-character-card::after {
	display:none;
}

.ddb-campaigns-character-card-footer {
	border-top: 1px solid #111;
}

.ddb-campaigns-character-card-header-cover-image::after {
	background-color: rgba(0,0,0,0.8);
}

.ddb-characters-listing-actions__sort-label {
		background: #222;
	border-left: 1px solid #111;
	border-top: 1px solid #111;
	border-bottom: 1px solid #111;
}

.ddb-characters-listing-actions__sort-box {
	background: #444;
	border: 1px solid #111;
	color: #ddd
}

.ddb-characters-listing-actions,
.b-breadcrumb-a{
	background: rgba(1,1,1,.4);
	border-top: 0px;
}

body .ddb-character-app-n7la4l {
	border: 1px solid #111;
}
}

@-moz-document regexp(".*dndbeyond.com/characters/.+/builder/.+") {
.body-rpgcharacterbuilder {
	background: #333 !important;
}

body, .ddbc-tab-list__nav-item--is-active {
	color: white !important;
}

.classes-manage-hitpoints-summary {
	color: #111;
}

.ct-optional-feature, .collapsible-body, .collapsible-header:hover, .collapsible-opened>.collapsible-header, .collapsible-body:after, .collapsible-header, .class-spell-list-header {
	background: #262626;
}

.ddbc-tab-list__nav-item:hover {
	color: white;
}

.class-manager-tabs .ddbc-tab-list__nav-item-callout .ddbc-chevron-svg path, .manage-race-tabs .ddbc-tab-list__nav-item-callout .ddbc-chevron-svg path{
	fill: #9b9b9b;
}

.collapsible-body {
	border-left: none;
	border-right: none;
	margin: 0;
}

.ddbc-truncated-content--is-hidden .ddbc-truncated-content__content:after {
	background: linear-gradient(0deg,#262626 6%,hsla(0,0%,100%,0));
}


.character-button {
	color: black;	
}


.character-button-outline {
	background-color: #222;
	color: white;
}

.character-button-outline:active, .character-button-outline:focus, .character-button-outline:hover {
	background-color: #252525;
	box-shadow: inset 0 0 4px 2px #353535;
}

.filter-constraint-query[type=search], input[type="text"]:focus, input[type="number"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="email"]:focus, textarea:focus, .ability-score-calc-override-value input[type=number], .builder-field-value[type=number], .builder-field-value[type=text] {
	background-color: #222;
	color: white;
}

input[type="text"]:hover, input[type="number"]:hover, input[type="search"]:hover, input[type="password"]:hover, input[type="email"]:hover, textarea:hover {
	background-color: #252525;
	color: white;
}

.body-rpgcharacterbuilder {
	color: #cecece;
}

.body-rpgcharacterbuilder th, .body-rpgcharacterbuilder td {
	background: #171717;
	color: #cecece;
	border: 1px solid #555;
}

.body-rpgcharacterbuilder tr:nth-child(odd) td, .ability-score-calc-component-value {
	background: #222;
}


.ct-spell-detail__tag {
	background: #222;
	box-shadow: inset 0 0 2px 0 #777;
}


.builder-field-toggle-description, .builder-field-select-description {
	color: #ddd;	
}

.ddbc-collapsible__header {
	background: #262626;
	margin-bottom:5px;
}

.character-button-outline:disabled {
    background-color: #282828;
    border-color: #424242;
    color: #c8c8c8;
}

}
