style.css:
@-moz-document domain("baidu.com"), domain("www.baidu.com") {
/* login */
.tang-pass-footerBar,
.tang-pass-pop-login div.tang-body {
	border-bottom-left-radius: 8px;
	border-bottom-right-radius: 8px;
	border-top-left-radius: 0;
	border-top-right-radius: 0;
}
.tang-pass-pop-login div.tang-title {
	border-top-left-radius: 8px;
	border-top-right-radius: 8px;
}
.tang-pass-pop-login .pass-text-input {
	border-radius: 4px;
}
.tang-pass-pop-login .pass-form-item-smsVerifyCode .pass-item-timer,
.tang-pass-pop-login .pass-form-item-smsVerifyCode .pass-item-time-timing {
	border-radius: 4px;
	height: 42px;
}
/* sacn qrcode */
.tang-pass-qrcode .tang-pass-qrcode-imgWrapper {
	margin: 0 auto !important;
}
#TANGRAM__PSP_10__QrcodeAnimation,
.Qrcode-status-guideAnim,
.tang-pass-pop-login ul.tang-pass-qrcode-ullist {
	display: none !important;
}

@media screen and (max-width: 1000px) {
	body {
		overflow-x: hidden;
	}
}

body {
	font-family: -apple-system, "Helvetica Neue", Helvetica, "Nimbus Sans L", Arial, "Liberation Sans", "PingFang SC", "Hiragino Sans GB", "Source Han Sans CN", "Source Han Sans SC", "Microsoft YaHei", "Wenquanyi Micro Hei", "WenQuanYi Zen Hei", "ST Heiti", SimHei, "WenQuanYi Zen Hei Sharp", sans-serif !important;
	background-color: #fdfdfd !important;
	min-width: 780px;
}
body {
	line-height: 1.6 !important;
}
.c-img {
	border-radius: 2px;
}
.old-pmd .c-img6 {
	width: 150px;
}
.op-bk-polysemy-album:hover,
.c-img:hover {
	box-shadow: none !important;
}
/* font-size++ */
/* 百度网页结果 */
.c-container,
.c-abstract,
.c-showurl,
.c-container td {
	font-size: 14px;
	line-height: 1.7;
	letter-spacing: 0.2px;
}
.new-pmd.c-container,
.new-pmd .c-abstract,
.new-pmd .c-showurl,
.new-pmd .c-container td {
	font-size: 14px;
	line-height: 1.7;
	letter-spacing: 0.2px;
}
.nvl-bookstore-wrap .chapter-title {
	font-size: 14px !important;
}
.c-container .t,
.c-default .t,
.t {
	font-size: 16px !important;
	line-height: 1.6 !important;
}
#u,
#head,
#tool,
#search,
#foot,
.bdpfmenu {
	font-size: 13px !important;
}
/* 百度知道结果 */
.list-wraper .dt {
	font-size: 16px !important;
	line-height: 1.7 !important;
	letter-spacing: 0.2px !important;
}
.list-wraper .dd,
.widget-pager .pager b,
.pager a {
	font-size: 14px !important;
	line-height: 1.6 !important;
}
.nums_text,
.search_tool,
.search_tool_conter span {
	font-size: 13px;
}
/* 百度资讯结果 */
.c-summary,
.new-pmd .c-color-text {
	font-size: 14px !important;
	line-height: 1.7 !important;
	letter-spacing: 0.2px !important;
}
/* 百度文库结果 */
.search-result dd .summary-box .summary {
	font-size: 14px !important;
	line-height: 1.7 !important;
	letter-spacing: 0.2px !important;
}
.search-result dd .info-box .info-box-bottom ul li,
.search-result dd .summary-box .detail .detail-info,
.search-result dd .summary-box .detail a,
.fc9 {
	font-size: 13px !important;
	line-height: 1.6 !important;
}
}

@-moz-document regexp("http[s]?:\\/\\/(www\\.)?[-\\w\\.]+baidu\\.com\\/?.*") {
