	padding: 0;
}
.s_form_nologin .s_btn_wr {
	border-bottom: none;
}
.s_form_nologin .s_ipt {
	height: 32px !important;
	margin: 4px 16px !important;
	padding: 0 !important;
	line-height: 32px;
	font: 17px arial;
}
.s_ipt {
	height: 40px;
	margin: auto 16px;
}
#su.bg {
	width: 100px;
	height: 40px;
	line-height: 40px;
	font-size: 16px;
	border-top-right-radius: 7px;
	border-bottom-right-radius: 7px;
	border-bottom: none;
	background-color: rgba(51, 133, 255, 1);
	box-shadow: 1px 0px 0 1px rgba(51, 133, 255, 1);
	transition: all 450ms;
}
.wrapper_new #head.fix-head .s_btn_wr .s_btn {
	line-height: 40px;
}
#form:hover {
	transition: all 450ms;
	border: 1px solid rgba(51, 133, 255, 0.6);
	-webkit-box-shadow: 0 0 0 4px rgba(51, 133, 255, 0.1);
	box-shadow: 0 0 0 4px rgba(51, 133, 255, 0.1);
}
#form:hover #su.bg {
	background-color: rgba(51, 133, 255, 0.9);
	box-shadow: unset;
	width: 101px;
}
#form {
	background-color: #fff;
	box-shadow: 0 0 8px #ccc;
	border-radius: 8px;
	width: 636px;
	border: 1px solid #fff;
	transition: all 450ms;
}
#head_wrapper .s-p-top {
	margin-bottom: 32px;
	padding-top: 64px;
}

/* search suggestions list */
.bdsug {
	width: 538px !important;
}
#fm .bdsug,
#form .bdsug,
#head_wrapper #form .bdsug-new,
.wrapper_new #form .bdsug-new {
	height: auto;
	top: 50px !important;
	border-radius: 8px;
	background-color: #fff;
	border: none !important;
	border-color: #fff transparent;
	box-shadow: 0 0 33px #ccc;
	cursor: pointer;
}

#head_wrapper #form .bdsug-new ul {
	background: none !important;
	margin: 0;
	padding: 0;
	border-top: none;
	cursor: pointer;
}
.bdsug li,
#head_wrapper #form .bdsug-new ul li {
	padding: 2px 10px !important;
	font: 15px arial;
	line-height: 28px;
	width: auto !important;
	cursor: pointer;
}
#head_wrapper #form .bdsug-new .bdsug-s,
#head_wrapper #form .bdsug-new .bdsug-s span,
#head_wrapper #form .bdsug-new .bdsug-s b {
	color: unset !important;
	background-color: #f0f0f0 !important;
}
.wrapper_new #form .bdsug-new .bdsug-s,
.wrapper_new #form .bdsug-new .bdsug-s span,
.wrapper_new #form .bdsug-new .bdsug-s b {
	color: unset !important;
	background-color: #f0f0f0 !important;
}
.bdsug[style="height: auto; display: block;"] {
	-webkit-transition: .3s;
	transition: .3s;
	opacity: 1;
	transform: translatey(0);
	height: auto!important
}
.bdsug-overflow {
	width: 500px !important;
	margin: 3px;
	border-radius: 6px;
}
#quickdelete {
	top: 5px!important;
	opacity: .5
}

/* search image form */
.soutu-layer,
#head_wrapper.s-down .soutu-env-newindex .soutu-layer,
.soutu-env-newindex .soutu-no-skin {
	border-radius: 8px;
	width: 636px;
}
.soutu-env-new .soutu-layer .soutu-drop,
.soutu-layer .soutu-url {
	border-radius: 8px;
}
.soutu-layer .soutu-url-wrap {
	border-radius: 8px 0 0 8px;
	height: 40px !important;
	width: 536px !important;
	border-bottom: none !important;
}
.soutu-layer .soutu-url-btn,
.soutu-env-new .soutu-layer .soutu-url-btn-new,
.soutu-env-newindex .soutu-no-skin .soutu-url-btn {
	height: 40px;
	line-height: 40px;
	border-radius: 0 8px 8px 0;
	width: 100px !important;
	font-size: 16px;
	border-bottom: none !important;
	margin: 0;
}
.soutu-layer .soutu-url-btn:hover {
	box-shadow: none;
	margin: 0;
}
.soutu-layer #soutu-url-kw {
	height: 40px;
	margin: auto 16px !important;
	font-size: 15px;
}
.soutu-env-newindex #soutu-url-kw {
	padding: 0 !important;
	border: none !important;
}
.soutu-layer .soutu-state-normal {
	box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .1);
	margin-top: 12px;
	border-radius: 8px;
	background-color: #fff;
}
.soutu-layer .soutu-close {
	display: none !important;
}
.soutu-layer .soutu-drop {
	border-radius: 8px;
}
.soutu-layer .upload-wrap {
	border-radius: 8px;
}
.soutu-layer .soutu-url-error {
	background: none;
	right: 104px;
	height: 28px;
	width: 120px;
	padding: 0 8px;
	line-height: 28px;
	border-radius: 4px;
	border: 1px solid #e43;
}

/* 搜索结果不存在的搜索框
 * https://www.baidu.com/error.html?param:{%22paperid%22:%22ff130f837fa8046e4beb5465397fc28e%22,%22site%22:%22xueshu_se%22,%22wise%22:false}
 */
.s_ipt_wr,
.s_btn_wr {
	border: none;
}
.s_form {
	height: 64px;
}
#s_tab {
	padding-top: 64px;
}
/* 搜索结果不存在的文字 */
.norsSuggest,
.norsTitle2 {
	font-size: 14px;
}
.norsSuggest a {
	text-decoration: none;
	color: #0055ff;
}
}

@-moz-document regexp("http[s]?:\\/\\/(www\\.)?baidu\\.com\\/s\\?.*") {
