	border-radius: 8px;
}
.i {
	height: 40px;
	padding: 4px 16px;
}
.btn_wr {
	border-bottom: none;
}
.btn {
	width: 100px;
	height: 40px;
	line-height: 40px;
	font-size: 16px;
	border-top-right-radius: 8px;
	border-bottom-right-radius: 8px;
	border-bottom: none;
}
.s_form a:hover {
	box-shadow: none !important;
}

/* 链接下划线 */
a {
	text-decoration: none;
	color: #222 !important;
}
a:hover {
	text-decoration: none !important;
	color: #315efb !important;
}

/* tab */
#s_tab {
	margin: 16px auto;
	width: 1000px;
	border-radius: 8px;
	background-color: #f1f1f1;
	padding-top: 0;
}
#s_tab_wrap {
	width: auto;
	margin: 0 auto;
	padding: 0 16px;
}

/* 标题 */
h3 {
	border-bottom: none;
	background-color: #f1f1f1;
	padding: 8px 16px;
	font-weight: 500;
	font-size: 16px;
	border-radius: 6px;
	width: auto;
}
h3 sup {
	display: none;
}

/* 产品项 */
.con {
	border: none;
	border-radius: 8px;
	margin: 16px 16px 0px 0;
	padding: 12px;
	box-shadow: 0 0 8px #ccc;
	transition: all .45s;
	height: 60px;
	width: 214px;
	position: relative;
}
.con:hover {
	box-shadow: 1px 2px 4px 0px #0000003b
}
.con_last {
	margin-right: 0px;
}
.con div {
	width: auto;
	position: absolute;
	margin-left: 32px !important;
	margin-right: 12px !important;
}
.abg,
.aipage,
.secr {
	margin-right: 12px !important;
	margin-left: -32px;
}
.abg:hover,
.aipage:hover,
.secr:hover {
	box-shadow: none;
}
.con a {
	font-size: 15px;
	font-weight: 400;
	margin-right: 0;
	line-height: 24px;
}
.con span {
	font-size: 12px;
}
}

