style.css:
@-moz-document domain(neuralnetworksanddeeplearning.com) {
p, body {
    background-color: #193549;
    color: #fff;
}
a, .equation_link {
    color: #ffc600;
}
a:hover, equation_link:hover {
    color: #ff9d00;
}
.nonumber_header, .header {
    background-color: #193549;
    border-bottom: 2px solid #ffc600;
}
.MJXp-display, .MathJax_Display, .math {
    color: #fff !important;
    border: 1px solid #ccc;
    padding: 8px;
    box-sizing: border-box;
}
canvas, img, svg {
    background: lightgrey;
}
h2 {
    font: bold 34px Georgia, serif;
}
.chapter_title {
    font: bold 34px Georgia, serif;
    text-align: center;
}
.chapter_number {
    font: italic 24px Georgia, serif;
}
.section {
    margin: 0 auto;
    width: 55vw;
    max-width: 800px;
    text-align: justify;
}
.h3 {
    text-align: left;
}
.sidebar_title {
    font: italic 16pt Georgia, serif;
    color: #999;
}
.highlight {
    background : #f8f8f8;
    color: black;
}
p.sidebar {
    color: #aaa;
}
/* Legacy code */
 .marginnote {
    right: 3vw;
    left: auto;
    max-width: 15vw;
    color: white;
}
#toc {
    left: 3vw;
    max-width: 15vw;
    text-align: left;
}
}
