style.css:
@-moz-document url-prefix("https://ark.intel.com") {
/* dark color 1 */
.rwd body,
.rwd .blade.solid.brand-primary,
.blade-support-featured-content.blade-featured-content .blade-support-feedback .feedback-title,
.support-wrapper,
.blade-support.breadcrumb,
.ark .blade,
.ark .compare-products-blade.compare-page-view,
.ark .blade .blade-inside,
.ark .compare-actions-row .highlight-differences.highlighted,
.footer-support-options{
    background-color: #202020;
    color: #afafaf;
}

/* dark color 2 */
#ark-searchbox,
#ark-searchbox::placeholder,
.ark .blade .blade-inside,
.ark .specs-section .blade .specs-list li .label,
.ark .compare-products-blade .compare-products-row .col-xs-12 .product-list .inner-list .product,
.ark .compare-products-blade .compare-products-row .col-sm-9 .product-list .inner-list .product,
.ark .compare-products-blade .compare-products-row .col-md-10 .product-list .inner-list .product,
.rwd .blade.solid.brand-lightest-gray,
.rwd .blade-group.solid.brand-lightest-gray,
.rwd .force-background.solid.brand-lightest-gray
{
    background-color: #404040;
    color: #cfcfcf;
}

/* dark color 3 */
.ark .compare-products-blade.compare-page-view .compare-products-row,
.ark .compare-products-blade.compare-page-view .compare-count-div,
.ark .blade .blade-inside.allow-full-bleed.compare-blade .compare-section-table-row.full-bleed .compare-table-wrapper .compare-section-table td.row-title,
.ark .blade .blade-inside.allow-full-bleed.compare-blade .compare-section-table-row.full-bleed .compare-table-wrapper .sticky-table td.row-title,
.ark .blade .blade-inside.allow-full-bleed.compare-blade .compare-section-table-row.full-bleed .compare-table-wrapper .compare-section-table td.row-title div.row-text,
.ark .blade .blade-inside.allow-full-bleed.compare-blade .compare-section-table-row.full-bleed .compare-table-wrapper .sticky-table td.row-title div.row-text
{
    background-color: #606060;
    border-color: #808080;
    color: #dfdfdf;
}

/* dark color 4 */
.rwd .table-responsive td,
.ark .blade .table-responsive .table td,
.ark .blade .table-responsive .table tr.highlighted td
{
    background-color: #404040;
    border-color: #808080;
    color: #e0e0e0;
}

/* dark color 4 (spec page) */
.ark .specs-section .blade .specs-list li .value
{
    color: #e0e0e0;
}

/* dark color 4 (stripped version) */
.ark .blade .table-responsive .table tr.highlighted td
{
    background-color: #606060;
    border-right: 3px solid #cf6060;
}

/* links */
.rwd a,
.rwd a:hover,
.ark .blade .info-modal,
.ark .specs-nav .spec-section-title,
.ark .specs-nav ul li a:hover,
.ark .specs-nav .specs-nav-section.always-active .spec-section-title:hover,
a.hrefcolor:link,
.table.support-table.collapsible a:link,
.support-modal a.searchAdvance:link,
.rwd.headerfooter-menu3 footer.global .content a,
.rwd.headerfooter-menu3 footer.global .content a:hover
{
    color: #00aeef;
}
.rwd a:hover
{
    text-decoration: underline;
}
.rwd .nav>li>a:hover,
.rwd .nav>li>a:focus
{
    background-color: #404040;
}

/* links in table cells */
.rwd .table-responsive td a,
.ark .blade .table-responsive .table td  a,
.ark .blade .table-responsive .table tr.highlighted td  a
{
    color: #00dfff;
}
}
