@-moz-document url-prefix("https://ucilnica.fri.uni-lj.si/"){
	body,
	a,
	.site-name,
	.nav-link,
	.dropdown-menu,
	.form-control,
	.custom-select,
	.menu-action-text,
	#id_q_5eb918b6ba1ec{
		color: white;
	}

	a:link{
		color: white;
	}


	.menu-action:hover{
		background-color: #444444!important;
	}

	.block-region,
	.yui3-js-enabled,
	#page-my-index,
	.d-print-block,
	.navbar-light,
	.col-12,
	.form-control,
	.table,
	.card-body,
	.course-listitem,
	.preferences-container,
	[data-region="drawer"],
	[data-region="view-overview"],
	[data-region="popover-region-container"],
	.body-container,
	.h-100,
	.card,
	.minicalendar,
	.course-summaryitem, [data-region="page-link"]{
